INSERT INTO `story` (`story_id`, `name`, `preview_text`, `detail`, `cat_id`, `picture`, `counter`, `create_at`, `status`) VALUES
(1, 'Tổng Tài Ở Trên Tôi Ở Dưới', '\"Có thai với tôi rồi mà còn muốn bỏ đi?\"Hắn cương quyết bắt cô phải giao ra đứa con cô đã sinh cho hắn vào ba năm trước. Không chịu một lần, hắn lại ép thêm một bận.\r\n\r\nTổng tài bất chấp thủ đoạn cố đoạt được tình yêu, cô tìm đủ mọi cách để kháng cự, bấu ', 'Editor: _KuniWang_\r\n\r\nCăn nhà đứng sừng sững ở giữa một vùng đất có rất nhiều cây. Một thành phố gần biên giới, huy hoàng đồ sộ vô cùng, lại còn làm cho người ta cảm giác thần bí.\r\n\r\nCăn nhà có đỉnh nhọn giống như sắp chọc thửng trời xanh, vào lúc ban đêm càng lộ ra  vể đẹp của mình, mọi người có thể nhìn thấy rõ nó.\r\n\r\nNóng.\r\n\r\nNóng quá, không thở nổi vì nóng.\r\n\r\nChiếc giường lớn theo phong cách Anh quốc, có một cô gái đang ngủ say, thân thể mền mại yểu điệu hấp dẫn thoát ẩn thoát hiện  trong bộ váy lụa tinh tế, càng lâu thì những giọt nô hôi lăn dài trên khuôn mặt thanh tú của cô cành nhiều hơn, thật sự rất nóng, từng giọt từng giọt mồ hôi cứ dần dần nhỏ xuống. Lăn dài xuống đôi môi nhỏ của cô\r\n\r\nTrên cánh tay của cô mồ hôi đã làm ướt một tầng lớp áo lụa trắng, để lộ ra xuân cảnh diễm lệ.\r\n\r\n\"Ân, nóng quá......\"\r\n\r\nThời Tiểu Niệm ưm một tiếng, từ trong giấc ngủ sâu mơ mơ màng màng mà tỉnh lại.\r\n\r\nLọt vào trong tầm mắt là một nơi xa hoa, môt căn phòng lạ lẫm, trên tường có treo bức tranh của Tây Phương thế kỉ 14 tại nàng không biết rõ lắm nhìn quanh căn phòng, lắc nhẹ đầu một cái.\r\n\r\nĐâu là chỗ nào?\r\n\r\nCô ý thức còn mơ màng đảo mắt nhìn xung quanh.\r\n\r\nChỉ thấy nơi góc khuất trên ghế sa lon, một người nam nhân chính ngồi, thân hình thon dài, trắng, nếu chỉ ra chỗ sai thì là hắn đang cầm li rượu vang mà uống.\r\n\r\n\"Anh là ai? Tại sao ở đây lại nóng như thế? Anh có thể tắt máy điều hòa được không?\"\r\n\r\nGiọng nói vừa cấ lên, Thời Tiểu Niệm mới phát hiện mình giọng nói của mình rất yếu, giống như mình bị bệnh rất nặng vậy.\r\n\r\nQuá nóng.\r\n\r\n\"Cô gái, nếu cô không tỉnh, tôi sẽ mở điều hòa lên 88 độ sau đó đem cô cùng chết chung!\"\r\n\r\nMột giọng nam trong phòng vang lên, nhiệt độ trong phòng rất cao, ngữ khí đấy hưng hăn khiến cho người nghe đột nhiên cảm thấy sợ hãi.\r\n\r\nChết chung?\r\n\r\nCái gì mà chết chung?\r\n\r\nThời Tiểu Niệm thần trí có chút mơ, mồ hôi rơi xuống, ánh mắt của nàng thật mê hoặc người khác.\r\n\r\nBên tai nghe thấy tiếng bước chân chầm chậm tới gần.\r\n\r\nCô dời tầm mắt, tay gỡ khỏi nút thắt, lộ ra xương vai rất gọi cảm, mồ hôi đổi ra như tắm, ánh mắt như chim ưng nhìn về phía trước như đang chông lại người đó.\r\n\r\nNam nhân đi lại dùng tại trước giường của cô đang nằm, hai chân thon dài đúng đấy, trên người mặc một chiếc áo sơ mi trắng làm nổi bật lên dáng người cao ráo của hắn, cổ áo mở hai cúc ở trên, hắn rất anh tuấn khiến cho người ta nhìn vào liền hít thở khó chịu mang lại cảm giác ngưỡng mộ cùng yêu thích, ngũ quan tinh xảo như một món quà của mẹ thiên nhiên, lông mày kiếm sắc lạnh và đen tuyền thể hiện sự uy quyền, sống mũi cao và thẳng tắp, làm môi mổng có chút hé mở, đẹp trai gợi cảm đến mức chết người!!\r\n\r\nRõ ràng trong phòng nhiệt độ trong phòng rất cao nhưng quái lạ ở chỗ nam nhân ấy không đổ một giọt mồ hôi nào, phong thái vẫn rất tao nhã bình tĩnh.\r\n\r\nNhìn xem nam nhân vừa đi tới trước mặt, rất trẻ tuổi, nhìn sơ thì chưa quá 29 tuổi.\r\n\r\nÁch, sao lại nhìn thấy người này có chút quen quen?\r\n\r\nĐã gặp ở đâu.....\r\n\r\nBởi vì thói quen nghề nghiệp, Thời Tiểu Niệm là người rất dễ cho tâm hồn bay bổng, treo ngược cành cây. Cô ngốc nhếch nhìn nam nhân đang đứng trước mặt bần thần một tí, nhưng rất nhanh, cô liền cho ý nghĩ đáp đất và tỉnh táo trở lại, bởi vì nam nhân lấy ra một cái súng ngắn bằng bạc.\r\n\r\nĐiều qua trọng chính là nòng súng đang chỉa ngay vào cô.\r\n\r\nHả? Chuyện gì đang xảy ra vậy?\r\n\r\n\"Anh định làm gì? Anh là ai? Anh rốt cuộc muốn làm cái gì nha?\"\r\n\r\nNgồi ở trên giường Thời Tiểu Niệm sợ hãi từ từ nhích ra phía sau, nam nhân một bước lại một bước tới gần nàng, nòng súng lạnh như báng cứ chỉ hướng cô, cô thì nóng đến ửng đỏ khuôn mặt.\r\n\r\nCô có một khuôn mặt có thể nói là rất thanh tú, ngũ quan tinh xảo, không có một chút ác cảm, tràn đầy sự ngây thơ.\r\n\r\nNòng súng của hắn chậm rãi đưa xuống dưới. Xuống đến môi của cô, rồi đến cằm, rồi sau cùng mới là xương quai xanh.\r\n\r\nLấy tay nhu mì vuốt ve khuôn mặt của cô.\r\n\r\nThời Tiểu Niệm không tự giác mà gồng mình lên, trên người chiếc váy lụa tựa hồ muốn rơi xuống, lúc trước mồ hôi chảy là vì nóng bây giờ chuyển thành mồ hôi lạnh.\r\n\r\n\"Cô gái, cô cho ta biết con của ta đang ở đâu?\"\r\n\r\nCung Âu đứng tại trước mặt cô, lên tiếng hỏi, ánh măt sâu thẳm nhìn qua thân thể ản hiện ra khỏi lớp áo lụa trắng, để lộ những đường cong gọi cảm .\r\n\r\n\"Cái gì nha?\"\r\n\r\nThời Tiểu Niệm nói.\r\n\r\n\"Ba năm trước đây, cô mang thai con của tôi, đứa con đó bây giờ đăng ở đâu?\"\r\n\r\nCung Âu nói một chữ thì một chữ cũng chỉ là câu hỏi, nhẹ đôngh cánh tay, nọng súng được vẻ vòng lên ngục cô, chỉ cách một lớp lụa trắng.\r\n\r\n\"Đứa con?\"\r\n\r\nThời Tiểu Niệm mơ màng, rất lâu sau cô mới hiểu ra vấn đề, \"Tôi nói này..... Anh có phải đã nghĩ sai điều gì? Tôi không quen biết anh.... Tôi cũng chưa từng có con!\"', 2, 'tong-tai-o-tren-toi-o-duoi.jpg', 12, '2020-09-17 00:42:50', '1'),
(2, 'Dịu Dàng Dành Riêng Em', 'Vội vã từ California Mĩ bay đến Hàn Quốc. Máy bay vừa hạ cánh, Hạ Dĩ Hàm đeo lên kính râm, bước nhanh ra khỏi sân bay. Trên tay trống không, cô không mang bất kì hành lý nào, chỉ vì quá sốt ruột sau khi nhận được điện thoại của chị mình.\r\n', 'Từ California Mĩ đến Hàn Quốc, máy bay hạ cánh.\r\n\r\nHạ Dĩ Hàm đeo kính râm lên, lao ra khỏi sân bay. Cô không mang bất cứ kiện hành lý nào, mà lần này đến Hàn Quốc chỉ vì một cuộc điện thoại của chị gái.\r\n\r\nHạ Dĩ Hàm lên xe taxi, dùng chút tiếng Hàn ngắc ngứ báo địa chỉ một bệnh viện, sau đó dựa lưng vào ghế, thoáng nhíu mày. Đêm qua đột nhiên cô nhận được điện thoại của Hạ Dĩ San, cô ấy nói cuộc phẫu thuật thẩm mĩ của cô ấy gặp chút vấn đề, bây giờ gương mặt thảm hại đến nỗi không dám nhìn nữa, cô ấy muốn trước khi cách ly với người đời được gặp em gái một lần. Tuy nhiên, có một điều kiện là, chuyện này phải tuyệt đối giữ bí mật, kể cả bố mẹ cũng không được biết.\r\n\r\nHạ Dĩ Hàm bất giác nhìn vào kính chiếu hậu, thấy gương mặt mình đang đeo chiếc kính râm to bự. Mắt kính gần như che khuất hết cả gương mặt cô, nhưng để lộ ra đường cong vừa vặn ở cằm, trắng nõn nà, hiếm có thế này cơ mà.\r\n\r\nCần thiết phải phẫu thuật thẩm mĩ sao? Hạ Dĩ Hàm sờ mặt mình, gương mặt này đâu có khó coi...\r\n\r\nThôi, Hạ Dĩ San thích làm, giờ lại thành ra hủy dung, xem chị ta còn kiêu hãnh kiểu gì.\r\n\r\nHạ Dĩ San là chị gái song sinh của cô, ngoài ánh mắt ra thì vẻ ngoài của hai người gần như là giống nhau như đúc. Mặt khác, Hạ Dĩ San còn có một thân phận khác, đó là một nghệ sĩ hạng ba mới gia nhập giới giải trí không lâu, Nghê Hạ.\r\n\r\nBốn mươi phút sau, Hạ Dĩ Hàm xuống xe tại cửa một bệnh viện. Cô lấy di động ra gọi vào số của Hạ Dĩ San, giọng điệu không mấy vui vẻ, \"Ở đâu?\"\r\n\r\nHạ Dĩ San thều thào ở đầu bên kia điện thoại, \"Chị bảo quản lý chờ em ở cửa rồi đấy, mặc áo khoác đen, với cả...\"\r\n\r\n\"Được rồi, em thấy rồi.\", không chờ Hạ Dĩ San nói xong, Hạ Dĩ Hàm đã thấy một người phụ nữ đứng cách đó không xa đang đi về phía mình.\r\n\r\nCảnh Tố dừng lại ở khoảng cách năm bước chân với Hạ Dĩ Hàm, chị ngơ ngác nhìn cô một lúc, cho đến khi Hạ Dĩ Hàm mất kiên nhẫn tháo kính ra, Cảnh Tố mới định hồn lại được. Chị vội vàng bảo Hạ Dĩ Hàm đeo kính lên, \"Đeo vào, đeo vào! Nếu mà bị người ta thấy Nghê Hạ xuất hiện ở bệnh viện thẩm mĩ thì thảm lắm! Trời ơi... Đúng là giống nhau như đúc.\"\r\n\r\nSau khi đeo kính vào, Hạ Dĩ Hàm thản nhiên nói: \"Dựa vào mức độ nổi tiếng của chị ấy bây giờ thì tỷ lệ bị nhận ra ở nước ngoài gần như bằng không.\"\r\n\r\nCảnh Tố nghẹn họng, \"Ờ... Nói cũng phải. Mình...lên tầng đi.\"\r\n\r\nHạ Dĩ Hàm hơi gật đầu, \"Đi thôi.\"\r\n\r\nHai người từ cửa bệnh viện đi về phía phòng bệnh VIP. Dọc đường đi, Cảnh Tố nói qua về nguyên nhân Hạ Dĩ San phẫu thuật. Thật ra cũng không phải là phẫu thuật gì lớn, chỉ là Hạ Dĩ San bị đối thủ một mất một còn trong giới giải trí kích thích, cướp đi vai diễn nữ hai trong một bộ phim truyền hình, thế nên Hạ Dĩ San quyết tâm sửa gương mặt quá thanh thuần của mình cho có chút vẻ yêu mị, nhân thể mở rộng con đường diễn xuất.\r\n\r\n\"Haiz, tệ thật, ai biết là Nghê Hạ bị dị ứng thuốc đâu, mới phẫu thuật được một nửa... Giờ mặt sưng phù như cái gì ấy. Bác sĩ bảo ít nhất phải một năm mới phục hồi được nguyên trạng.\", Cảnh Tố than ngắn thở dài.\r\n\r\nHạ Dĩ Hàm nhìn chị ấy, \"Chị là quản lý của chị ấy, sao chị không ngăn chị ấy lại?\"\r\n\r\nCảnh Tố bị giọng cảnh cáo đó của Hạ Dĩ Hàm hù dọa, thoáng ngượng ngùng trả lời: \"Chuyện này, nghệ sĩ sửa mặt một chút thật ra cũng bình thường mà.\"\r\n\r\nHạ Dĩ Hàm hừ lạnh một tiếng, \"Kĩ thuật diễn không theo kịp lại còn đổ tại mặt quá ngây thơ.\"\r\n\r\nCảnh Tố lại một lần nữa nghẹn họng. Cô em gái này của Nghê Hạ sao nói chuyện thẳng thắn, không chút kiêng nể gì thế...\r\n\r\nRốt cuộc cũng đến phòng bệnh. Hạ Dĩ Hàm vừa vào cửa đã nhìn thấy Hạ Dĩ San đang ngồi trên giường bệnh, đầu quấn đầy băng gạc. Nhìn thấy bộ dạng này của chị gái, cơn tức giận trong lòng cô cũng thoáng đổi thành sự đau lòng.\r\n\r\nHồi trước bố mẹ ly hôn, hai chị em cô, một người theo bố về Trung Quốc, một người cùng mẹ ở lại xứ người. Có điều, chuyện này không hề ảnh hưởng đến tình cảm cốt nhục giữa hai người. Tuy có rất nhiều lúc Hạ Dĩ Hàm cạn lời với kiểu hành động không dùng não của Hạ Dĩ San, nhưng từ nhỏ đến lớn, sự chăm sóc và giúp đỡ mà Hạ Dĩ San dành cho cô thì không phải bàn nhiều.\r\n\r\n\"Dĩ Hàm...\", Hạ Dĩ San mếu máo giơ tay ra như đòi một cái ôm. Hạ Dĩ Hàm cau mày bước lại gần, \"Nghiêm trọng thật à?\"\r\n\r\n\"Thật mà, Dĩ Hàm, chị hối hận chết mất, chị bị hủy dung rồi... Hu hu, Dĩ Hàm, em phải cứu chị...\"\r\n\r\nHạ Dĩ Hàm ngồi xuống cạnh cô ấy, giơ tay định sờ vào mặt nhưng lại sợ cô ấy đau, cô cứng miệng trách, \"Tự chị làm chuyện ngu ngốc thì tự chịu đi, em cứu chị á, em phải cứu chị thế nào? Giờ ngoan ngoãn ở lại bệnh viện điều trị đi. À phải rồi, chị bị dị ứng mà bệnh viện cũng không phát hiện ra, bệnh viện kiểu gì không biết, phải kiện!\"\r\n\r\n\"Đừng, đừng, đừng!\", Hạ Dĩ San ngăn cô lại, \"Đừng làm to chuyện lên, chuyện này mà bị làm ầm lên là toi, chị còn muốn quay về đóng phim mà. Nếu để mọi người biết chị phẫu thuật thẩm mĩ, sau này chị còn làm được gì nữa.\"\r\n\r\n\"Chị!\"\r\n\r\n\"Dĩ Hàm, hiện giờ chuyện đó không quan trọng, quan trọng là hôm nay chị gọi em đến đây không phải đơn giản là để em mắng chị.\"\r\n\r\n\"Chị muốn nói gì?\", Hạ Dĩ Hàm thoáng thấy bồn chồn.\r\n\r\nHạ Dĩ San cắn môi giữ tay Hạ Dĩ Hàm, \"Dĩ Hàm, em biết chị muốn được nổi tiếng, muốn trở thành diễn viên, muốn được mọi người tán thưởng thế nào mà. Chị cố gắng thi vào trường điện ảnh, cố gắng tìm đoàn phim, lăn lộn hai năm, vất vả lắm mới được ló mặt trên tivi, làm mọi người nhớ chị. Chị không muốn kiếm củi ba năm thiêu đốt một giờ đâu... Nếu chị ở ẩn dưỡng thương, thế thì sẽ bị hủy diệt luôn. Em cũng biết mà, bây giờ khán giả thay đổi thần tượng nhanh lắm, mọi người sẽ quên chị...\"\r\n\r\n\"Dừng, dừng, dừng!\", Hạ Dĩ Hàm chặn lời cô nàng, \"Chị nói vào điểm chính đi,\"\r\n\r\nHạ Dĩ San nức nở, \"Mấy năm nay chị chỉ nhận được mấy kịch bản nhỏ, nhưng hai hôm trước, đạo diễn Hầu Quang Trung đến tìm chị, mời chị làm vai nữ hai trong bộ phim điện ảnh của ông ấy. Hầu Quang Trung là ai? Hầu Quang Trung đấy, đạo diễn nổi tiếng, dành được vô số giải thưởng, phim của ông ấy, diễn viên nào cũng muốn tham gia. Thế mà ông ấy lại đến tìm chị.\"\r\n\r\n\"Thế cho nên, Hạ Dĩ San? Bộ dạng này của chị đương nhiên không thể tham gia được rồi, Hầu Quang Trung cái gì, chị quên đi cho em.\", Hạ Dĩ Hàm nghiêm túc nói.\r\n\r\n\"Không, không, không. Chị tuyệt đối không thể từ bỏ cơ hội lần này được, chị không biết khi nào chị mới có cơ hội một lần là nổi tiếng nữa.\", Hạ Dĩ San kích động nói.\r\n\r\n\"Nhưng bộ dạng này của chị...\"\r\n\r\n\"Dĩ Hàm! Em đi thay chị đi. Dù sao thì chị em mình cũng giống nhau như đúc, mọi người cũng không biết thông tin cá nhân của chị, căn bản là không hề biết chị có em gái sinh đôi...\"\r\n\r\n\"Chị nói linh tinh cái gì đấy!\", Hạ Dĩ Hàm không đợi cô nàng nói xong liền đứng bật dậy, \"Chị bảo em thay chị đóng phim?\"\r\n\r\nHạ Dĩ San mím môi, \"Dĩ Hàm, chị biết thật ra em cũng thích đóng phim, nếu ngày trước em không theo mẹ, nếu không phải mẹ không đồng ý cho em vào giới giải trí, thì chắc chắn em cũng đăng ký vào trường điện ảnh như chị thôi.\"\r\n\r\nHạ Dĩ Hàm mở trừng mắt. Chuyện từ cách đây bao lâu rồi, nếu giờ phút này Hạ Dĩ San không nhắc tới, có lẽ cô đã ngày qua ngày ở trong phòng thí nghiệm mà quên đi giấc mơ hão huyền đó.\r\n\r\nPhải, cô đã từng, từng muốn làm một diễn viên.\r\n\r\n\"Dĩ Hàm, em giúp bà chị đáng thương này của em đi. Em cũng dung túng bản thân thử trải nghiệm cuộc sống của một diễn viên xem. Em yên tâm, chị tuyệt đối sẽ không nói cho mẹ biết. Làm ơn, làm ơn mà... Nếu em không giúp chị, chị thật sự, thật sự, muốn chết đấy!\", nói xong, Hạ Dĩ San liền làm ra vẻ muốn đâm đầu vào tường.\r\n\r\nCảnh Tố còn phối hợp chạy đến giữ Hạ Dĩ San lại, \"Cô Hạ, cô... cô đồng ý đi. Tôi nghe Nghê Hạ nói lúc nhỏ cô rất thích diễn kịch, coi như một lần trải nghiệm đi. Chờ Nghê Hạ hồi phục rồi, hai người có thể lặng lẽ tráo đổi lại, sẽ chẳng ai biết đâu.\"\r\n\r\nHạ Dĩ Hàm nhìn hai người phụ nữ diễn kịch một cách thiếu nội hàm mà thở dài, \"Ở trường bên kia em còn có việc chưa làm xong.\"\r\n\r\n\"Dĩ Hàm, chị mà lại không bằng mấy cái nghiên cứu hóa học đấy của em à?...\", Hạ Dĩ San nhìn cô bằng vẻ \"đau thương gần chết.\"\r\n\r\n\"...\"\r\n\r\nCó lẽ là thật sự hiểu sự chấp nhất của Hạ Dĩ San với giới giải trí, cũng có lẽ là bởi ước mơ muốn làm diễn viên trong lòng lại một lần nữa bị móc ra, cuối cùng, sau khi Hạ Dĩ Hàm bình tĩnh lại được thì cô đã theo Cảnh Tố đến Hoành Điếm - Trung Quốc rồi.\r\n\r\n\"Ba ngày vừa rồi chị đã phụ đạo khẩn cấp cho em về các nhân vật và sự kiện trong giới giải trí, em đều nhớ cả chứ?\", Cảnh Tố lo lắng nhìn Hạ Dĩ Hàm. Không, hiện tại bắt đầu phải gọi cô là Nghê Hạ rồi.\r\n\r\nNghê Hạ khẽ thở dài một hơi, có chút phiền não vì chẳng hiểu sao mình lại nhận lời Hạ Dĩ San mà đến cái chốn Hoành Điếm này, đúng là không còn đường lui.\r\n\r\n\"Khoảng tám, chín phần.\"\r\n\r\n\"Vậy được, chúng ta đến quán cà phê đằng trước xem lại kịch bản Mê Thành, ngày mai em phải gia nhập đoàn phim rồi, đừng căng thẳng.\"\r\n\r\nNghê Hạ \"ừm\" một tiếng, \"Em không căng thẳng, chị yên tâm.\"\r\n\r\nBiểu hiện của Nghê Hạ quá sức lãnh đạm, điều này làm cho Cảnh Tố có chút không quen khi mà đã quá quen với kiểu hấp tấp vội vàng của Hạ Dĩ San. Chị không khỏi buồn bực, rõ ràng là chị em sinh đôi, thế mà tính tình lại khác nhau quá nhiều.\r\n\r\nNghê Hạ ngồi ở một góc quen trong quán cà phê học lời thoại cho nhân vật của mình. Đây là lần đầu tiên cô học kịch bản, nhưng lại cực kỳ quen thuộc với thứ này. Từ nhỏ đến lớn, từ bố, cô biết được rất nhiều.\r\n\r\nCảnh Tố đặt túi xách xuống rồi đứng dậy đi gọi đồ. Nghê Hạ đang nghiêm túc học lời thoại thì chợt nghe có tiếng reo khẽ của một cô bé nữ sinh. Cô theo bản năng ngước mắt lên thì thấy một đôi nam nữ bước vào quán cà phê.\r\n\r\nNgười nam khá cường tráng, người nữ cao gầy, cả hai đều đeo kính râm, có điều, cặp kính râm cũng không che nổi hào quang của siêu sao trên họ.\r\n\r\nHoành Điếm là nơi hằng năm đều có đoàn làm phim ở lại, thế nên đến Hoành Điếm mà gặp được ngôi sao cũng không phải chuyện hiếm. Nghê Hạ chỉ liếc mắt một cái rồi lại cúi đầu đọc kịch bản của mình.\r\n\r\nThế nhưng, cô không ngờ, lúc đôi nam nữ đó đi ngang qua mình thì chợt dừng lại, ngay sau đó, cô nghe thấy một tiếng reo đầy kinh ngạc.\r\n\r\n\"Đây chẳng phải là Nghê Hạ sao? Sao cô lại ở đây?\"\r\n\r\nNghê Hạ khẽ nhướng mày, việc đầu tiên là nhét tập kịch bản vào túi xách. Nghe nói bộ phim điện ảnh này có tính bảo mật cao, thế nên cô không thể để người khác nhìn thấy cô đang đọc kịch bản Mê Thành được.\r\n\r\nCô ngẩng đầu, đập vào mắt là người đàn ông đang mím môi vì mất kiên nhẫn, còn người phụ nữ thì tháo kính râm ra, gương mặt hết sức xinh đẹp.\r\n\r\nNghê Hạ hơi nhếch môi, \"À, là tôi, chào cô.\"', 2, 'diu-dang-danh-rieng-em.jpg', 9, '2020-09-17 00:45:41', '1'),
(3, 'Đợi Tôi 28 Tuổi, Rồi Chú Lấy Tôi Nhé', 'Một câu chuyện có thật về cuộc đời của tác giả, một mối tình, một khoảng thời gian khó khăn, trăn trở... Và có những thứ đến rất tự nhiên, bản thân mình không thể nào khống chế được. Tình cảm, vừa chỉ thoáng chốc, vừa là cả đời...', '—Mày đứng lại cho tao.....đồ mất dậy..!\r\n\r\n—Hôm nay trực nhật lớp nên con phải đi sớm.Mẹ đừng chửi nữa.\r\n\r\n—Miền...đứng lại cho tao....có nghe thấy gì không hả?\r\n\r\n—Con muộn học rồi...chào mẹ hai...con đi đây..\r\n\r\n—Con quỷ...ai là mẹ mày hả...?\r\n\r\nTừ tờ mờ sáng,tiếng chửi của \"mẹ hai\" tôi đã vang vẳng tận đầu con ngõ nhỏ.Chuyện này xảy ra như cơm bữa nên đối với tôi hết sức bình thường.\r\n\r\nTôi vội vàng đeo cặp rồi rời nhanh ra khỏi nhà trước khi bị mẹ hai đuổi kịp,hàng ngày cứ sáng ra là tôi lại đụng độ mẹ hai như vậy đấy.Ba tôi thì đi làm ăn xa,ở nhà chỉ có mỗi tôi,mẹ hai và một đứa em trai nhỏ hơn tôi 2 tuổi.\r\n\r\nMẹ tôi mất từ lâu rồi, lúc tôi còn chưa tròn tháng thì mẹ đã phải nhập viện,không lâu sau thì qua đời,người nhà không kể cho mình hay mẹ bị bệnh gì nên tôi cũng không biết.Ba thì cưới vợ mới là mẹ hai tôi bây giờ.Gọi là mẹ nhưng chẳng biết có yêu thương tôi không nữa.Hồi còn bé xíu xíu thấy mẹ hai cũng đối xử tốt lắm, nhưng càng lớn lên thay vì những sự quan tâm,cử chỉ nhẹ nhàng thì lại là những đòn roi đau tê buốt.\r\n\r\nTừ một cô bé tính tình nhút nhát,tôi đã dần trở thành một cô gái mạnh mẽ,ngang bướng.Những trận đòn roi đau đớn ấy rồi cũng có ngày mủi lòng trước sự cứng rắn của tôi\r\n\r\nCàng lớn,tôi càng trở nên bất cần với tất cả mọi thứ.Ngày trước thay vì đứng yên chịu trận thì giờ đây tôi đã biết phản kháng lại những điều tiêu cực ấy.Có người hỏi tại sao mình lại không nói với ba về những việc mà mẹ hai đã và đang gây ra cho tôi.Lúc ấy tôi chỉ mỉm cười mà nói,dù sao thì bà ấy cũng là người nuôi nấng mình,cũng đã cho tôi gọi một tiếng \"mẹ\" dù đó chỉ là hình thức.Tôi vui vì những điều ấy,và phải chấp nhận hi sinh,cuộc đời là thế,có cho thì phải có nhận,có nợ thì phải có trả.\r\n\r\nNgười mẹ đã qua đời của tôu,thời gian trôi nhanh đến nỗi mình còn chẳng thể nhớ nổi gương mặt hay dáng hình của bà.Điều duy nhất tôi có thể nhớ chính là mỗi nhìn vào bức ảnh thờ lạnh lẽo,những kí ức đau buồn mờ ảo lại làm tim tôi nhói đau\r\n\r\nQuay lại thực tại.\r\n\r\nTôi mỉm cười khi đã thoát khỏi trận địa \"chửi\" của mẹ hai.Nói thật,thà bị đánh hơn là bị chửi như thế này.Lâu dần cơ thể cũng hình thình một loại kháng sinh bất khả kháng với những lời chửi ấy.Điều đặc biệt là thái độ thản nhiên của tôi cũng một phần chọc tức mẹ hai không ít.Nói thật,nhìn mẹ hai tức giận,mặt đỏ tía tai như quả cà chua làm tôi vô cùng hạnh phúc,nghĩ đến thôi đã muốn sung sướng cả một ngày.\r\n\r\nNăm nay đã là năm cuối cấp ba của tôi...Học lực thì thấp,có lẽ thi đại học là một việc quá sức nên tôi nghĩ đến việc đi làm sau khi ra trường.Tôi thuộc loại con gái đã học dốt lại còn rất thích đánh nhau,lười việc nhà mà chăm việc đời.Thật ra cũng không dám xưng anh xưng chị gì đâu,chẳng qua cũng được cái tiếng be bé nên dễ bề hoạt động,chứ cũng không dám làm càn.\r\n\r\nHọc dốt đánh nhau thì giỏi nên được thầy cô yêu quý gấp đôi.Cũng vì mấy lần ấy mà mẹ hai được gọi lên trường họp khiến bà ấy tức sôi máu.Mỗi lần ấy là y rằng tôi lại vừa bị nghe chửi rồi bị đánh nữa.Với bạn bè,tôi là đứa có chí khí,bọn nó nghĩ rằng ngoài vỏ bọc mạnh mẽ thì tôi không còn gì cả...Nhưng không,tôi cũng là con người mà,cũng có cảm xúc,có vui,có buồn,có khóc chứ....\r\n\r\nĐi lang thang ra công viên một lúc,hít thở bầu không khí của màu hè để lấy động lực cho một ngày mới.Mặt trời vẫn lên,nắng vẫn chiếu,con người lại quay cuồng với công việc...thời gian trôi qua xem chừng lãng phí?\r\n\r\nTôi không ăn sáng mà đi thẳng tới trường.Chuyện sẽ suôn sẻ chẳng có gì nếu như tôi không gặp thằng Hùng sói.Nghe nói nó là đàn anh máu mặt ở vùng này này nhưng lại học khác trường với tôi,hôm nay coi như cơ hội để gặp mặt.Tất nhiên tôi sẽ chẳng quan tâm nếu như hắn ta không dẫn đồng bọn đứng trước cổng trường tôi để chặn đánh.\r\n\r\nBọn này mới sáng sớm đã thích gây gổ,tôi đây còn chưa được cái gì lót dạ,đã bực mình lại gặp phải một lũ điêm.Hơn nữa hôm nay lại đi một mình lên không biết có chiến lại với chúng nó không nữa?Xem chừng đứa nào cũng hùng hổ khí thế lắm,quả này mình đối phó hơi mệt.\r\n\r\nThằng Hùng sói vừa thấy mình vội dập điếu thuốc lá đang hút trên miệng,hùng hổ chạy lại túm lấy cổ áo mình như sợ tôi chạy mất.Mẹ thằng chó,mới sáng ra,ít ra mình đây cũng là phận đàn bà nữ nhi,gì thì gì cũng phải nhẹ tay thôi chứ.\r\n\r\n—Mày định trốn đi đâu..?\r\n\r\nTôi cũng không phải dạng vừa,cố gắng trợn to hai con mắt,quay phắt lại nhìn nó.Giọng cũng hùng hổ mà trả lời lại,chẳng biết xưng hô thế nào cho đúng?Thằng đó gọi mày xưng tao thì tôi cũng làm y như thế.\r\n\r\n—Tao không trốn...Nhưng việc mày đang túm cổ áo tao thấy tao cảm thấy hơi khó chịu rồi đấy.\r\n\r\nNói xong nó cũng thả xuống,bên nó công nhận đông,kiểu này mà xông vào đánh mình thì chỉ có nước nhập viện chứ chẳng chơi.Thôi,hổ báo thế nào thì tính mạng cũng là trên hết,chứ mà bọn nó đánh hội đồng nhỡ may đánh mạnh quá lại nhập viện chấn thương sợ não sống thực vật cả đời thì chết.Thôi thì phải nghĩ kế mà đối phó gấp\r\n\r\n—Ê con nhỏ kia,mày biết tao là ai không?\r\n\r\n—Hùng sói...biết chút chút..\r\n\r\n—Hôm nọ mày bắt nạt em tao à?\r\n\r\n—Em mày là đứa nào...?\r\n\r\n—Mày không nhớ..?\r\n\r\n—Cả trường này có biết bao nhiêu người,bố tao cũng không nhớ nổi?\r\n\r\n—Hà Múp....\r\n\r\n—À tưởng ai...thế mày là anh trai,anh trai kết nghĩa,anh trai nuôi hay anh trai bao?\r\n\r\n—Vấn đề là giờ mày định giải quyết thế nào?\r\n\r\n— mới sáng sớm chúng mày đã kéo đàn đến tận đây đòi xử tao,giờ còn định hỏi tao xem giải quyết thế nào?mày có hỏi ngu quá không vậy?\r\n\r\n—Mẹ con chó...\r\n\r\nChưa kịp để tôi phản ứng gì tiếp theo,thằng Hùng Sói dơ nắm đấm bộp một phát vào má.Thôi rồi, gọi là cú tát cho nhẹ nhàng.Tưởng thế nào,cũng mạnh mẽ phết đấy.Tôi ngã dúi dụi xuống đất,đầu óc choáng váng.Rất may là có kinh nghiệm nên tôi nhanh chóng lấy lại tinh thần mà đứng dậy.Con Hà Múp lúc này cũng có mặt.Thằng Hùng Sói đứng đó,hùng hổ thông báo.\r\n\r\n— Hôm nay mày mà không xin lỗi cái Hà thì nơi đây sẽ nơi chôn cất của mày\r\n\r\nĐúng là lũ đàn ông,có gái vào là lại sĩ diện như thường.Tất nhiên tôi đâu để bọn nó đứng đó hiên ngang mà lộng hành xỉ nhục tôi như vậy.Tôi nhìn thẳng vào mắt thằng Hùng Sói mà nói.\r\n\r\n—Đấm hơi bị đau đấy?\r\n\r\n—Nếu không nhìn tao tưởng mày là con trai đấy...Ánh à...Mạnh mẽ lắm\r\n\r\n—Vậy thì mày vào luôn đê...\r\n\r\n—Được hùng hổ lắm con..\r\n\r\nCả đường đông kín người,trường tôi học đã có vài ba học sinh đến nơi.Tất cả đều hiếu kì mà dừng lại theo dõi.Nhìn vậy thôi nhưng tôi cũng nhát lắm,nhưng khổ nỗi giờ mà xuống nước thì lại chết vì nhục.Nhìn cái con Hà Múp đứng đối diện đang cười khinh khỉnh trông thấy ngứa cả mắt.Tôi đang nghĩ bây giờ mà mình kích đểu,bọn nó mà kích động đập mình một phát chết tươi thì xong\r\n\r\nTôi bèn nghĩ kế khác\r\n\r\n—Trường này tao học,có gì trưa này về gặp nhau ở bãi đất hoang.\r\n\r\n—Tao không nghe.\r\n\r\nCố gắng câu giờ để tìm cứu trợ mà bọn nó có nghe đâu,thế mới khổ.Đang túng quẫn không biết làm thế nào thì tự nhiên trông đầu nảy ra một ý tưởng hết sức điên rồ.Tôi cá cả cái mạng của mình,chắc chắn thằng Hùng sói và con Hà Múp đang yêu nhau.Thẩm nào...Bảo vệ nhau ghê quá,bây giờ tooi muốn chạy cũng không còn đường để chốn nữa.Bọn nó chỉ cậy đông, chứ ở đây mà có mỗi tôi và con hà múp thì tôi sẽ đập nó chết liền mà không cần tốn nhiều sức.\r\n\r\n—Mày có biết ông Mạnh Vổ không?\r\n\r\n—Cái gì...Mạnh Vổ...?\r\n\r\nCái tên vừa vang lên khiến thằng Hùng sói mặt thâm tím lại sợ hãi.Tay cũng tự thế mà đẩy hẳn con Hà Múp bắn sang một bên.Tôi nhìn thấy chỉ biết ôm bụng mà phì cười không thể chịu nổi.\r\n\r\n—Sao...thế có biết Mạnh Vổ không?\r\n\r\n—Hôm nay là ngày lành,chúng tao tha cho mày...\r\n\r\n—Về may mắn nha các em..\r\n\r\nChúng nó bắt đầu nổ xe rồi rời đi.Thằng Hùng sói cũng đi luôn bỏ lại một mình con Hà Múp ở lại.Nghĩ cũng buồn cười,chuyện chẳng là hôm nọ thấy mẹ hai nói chuyện với bà hàng xóm kêu ông Mạnh Vổ xóm dưới vừa đi tù về.Thế nào vừa nãy cuống quá nói luôn,chứ có biết ông ta là ai đâu.Không ngờ có tác dụng khủng khiếp.Chắc ông này cũng có quá khứ ghê gớm nên nhìn vẻ mặt của thằng Hùng mới hốt hoảng như vậy.Mà thôi cũng không quan tâm....\r\n\r\nTôi đi vào trường,lại tiếp tục những tiết học trên lớp.Cứ bình yên như vậy thôi,cầu mong đừng thêm bất cứ sóng gió ập lên đôi vai bé nhỏ này nữa...\r\n\r\nTrưa tan học,tôi ngó nghiêng xung quanh cổng cũng không thấy bóng dáng của Tụi Hùng sói.Chắc là cái tên Mạnh Vổ lúc sáng tôi nói làm cho bọn này sợ chạy mất dép rồi.Con Hà Múp cả 5 tiết cũng trốn tôi như trốn tà,cứ thui thủi như sợ tôi trả thù lại.Chắc nó cũng tức điên lên mất,nhưng mà kệ,không quan tâm.\r\n\r\nTrời nắng đang định đi bộ về thì được Thắng,cậu bạn cùng lớp ngỏ lời.\r\n\r\n—Sao,thế có quá giang không?\r\n\r\nCái tên này,nếu đã có ý cho người ta đi nhờ rồi thì ít ra cũng nên thành ý một chút chứ.Đằng này mặt cau có khó chịu thấy ghét.Nếu không phải trời nắng thì chị đây cũng chẳng thèm đi nhờ.\r\n\r\nThắng chở tôi trên đường mà im re,một câu cũng không nói.Thỉnh thoảng tôi vẫn phải véo nhẹ vào hông xem còn phản ứng không?Hình như đèo mình ở phía sau nên cậu ta cố tình đi chầm chậm thì phải\r\n\r\n—Véo cái gì ấy?Để im để tôi tập trung lái xe...\r\n\r\n—Ai làm gì... im im thấy ghê...\r\n\r\n—Hôm nay Miền lại đánh nhau à?\r\n\r\n—Ai bảo vậy?Linh tinh..\r\n\r\nTôi khăng khăng phủ nhận.\r\n\r\n—Trên miệng vẫn còn vết thương kìa?\r\n\r\n—À...do ngã đó.Lúc sáng đi học vội quá ấy mà.\r\n\r\n—Nói dối nhiều quá thành thói quen rồi đấy,Miền...!\r\n\r\nNgồi ở đằng sau,tôi thẹn thùng ngượng.Có lẽ sự việc sáng nay đã được truyền đến tai cậu ta.Tôi hừ giọng.\r\n\r\n—Thôi thôi...không nói nữa...\r\n\r\nThấy Thắng im lặng không trả lời,sợ cậu ta giận,tôi lại phải nói thêm câu nữa.\r\n\r\n—Hôm nay trời nắng quá nhỉ.\r\n\r\n—Ngồi im đi..\r\n\r\nGiọng có vẻ bực bội,cái tên này bị khùng hay sao ấy,tự nhiên nổi giận với tôi.Tôi thì vẫn cố giấu không cho cậu ta biết vụ việc lúc sáng xảy ra,giọng giả lai không để đâu hết sự điêu thuyền.\r\n\r\n—ừm tôi đang ráng ngồi im nè.\r\n\r\nTôi không bắt chuyện nữa,cũng không có ý hỏi thêm gì cả,để mặc hai đứa miên man vào miền suy nghĩ...', 2, 'doi-toi-28-tuoi-roi-chu-lay-toi-nhe.jpg', 13, '2020-09-16 20:54:15', '1'),
(4, 'Từ Từ Dụ Dỗ', 'Nha sĩ lạnh lùng phúc hắc X thực tập sinh ngây ngô, hoạt bát.\r\n\r\nBan đầu cô là bệnh nhân của anh.\r\n\r\nKhi tới phòng khám, anh hỏi cô:\r\n\r\n- Sao giờ mới đi niềng răng?\r\n\r\n- Em sợ đau.\r\n\r\nTừ Nhuận Thanh cong khoé môi, thầm nghĩ: nói cứ như hiện tại không sợ ấ', 'Sắc trời có chút âm trầm, tầng mây bị ép tới cực thấp, vẫn còn quang cảnh của sáng sớm, khi ánh nắng còn chưa rõ ràng.\r\n\r\nNiệm Tưởng ngồi trên ghế dài của bệnh viện, khẩn trương đến mức tim trong lồng ngực cũng đập nhanh từng trận vang dội. Một tay cô áp lên má phải, nhịn không được chuyên chú nhìn về phía cửa nha khoa đối diện.\r\n\r\nTuy rằng khoảng cách có chút xa, nhìn thấy gì cũng không rõ ràng, nhưng cái này cũng không gây trở ngại cô đối với bệnh nhân đang tiếp thu trị liệu, sâu sắc đồng cảm.\r\n\r\nCô đã ở chỗ này chờ gần nửa giờ.\r\n\r\nNgay lúc cô cho rằng hôm nay không biết sẽ chờ đến bao giờ, rốt cuộc lúc đang ôm răng đau than thở, cô thoáng chốc nghe thấy có người kêu tên của mình.\r\n\r\nSửng sốt một chút, ngay lúc tên của mình được đọc lại lần thứ hai, lần sau rõ ràng hơn lần trước, cô nhanh chóng nhổm dậy đi vào.\r\n\r\nQuy cách của gian phòng cũng không nhỏ, được tấm kính dầy mờ chỉnh tề ngăn cách hình thành từng gian phòng nhỏ độc lập. Ngoài Niệm Tưởng vẫn đang hết sức chuyên chú nhìn chằm chằm bác sĩ đang trị liệu cho bệnh nhân, bên trong một phòng nhỏ khác còn có một vị bác sĩ cùng hộ sĩ.\r\n\r\nHẳn là vừa kết thúc trị liệu cho bệnh nhân, anh còn mang khẩu trang, chỉ lộ ra đôi mắt. Trong tay đang cầm giấy khám mà cô đăng ký, chuyên chú nhìn cái gì.\r\n\r\nNiệm Tưởng đi qua, răng đau đến có chút không thể há mồm, sau khi hít một hơi mới nhỏ giọng nói: “Bác sĩ, răng tôi đau quá, anh xem giúp tôi một chút đi…”\r\n\r\nTừ Nhuận Thanh ngẩng đầu lên, đôi mắt đó trong trẻo, thấu triệt đến mức giống như một dòng suối ở vùng núi cao, an tĩnh nhìn về phía cô.\r\n\r\nNiệm Tưởng bị anh nhìn đến ngây người, bàn tay còn ôm mặt, cứ như vậy ngơ ngơ ngẩn ngẩn đối diện cùng anh.\r\n\r\n“Niệm Tưởng?” Thật lâu sau, anh mới buông giấy đăng ký trên tay xuống, đứng dậy.\r\n\r\n“… Là tôi.” Bởi vì họ này đích thực quá ít, hơn nữa tên cũng có chút độc đáo, tình huống bị người khác hỏi lại để xác nhận, hoặc là muốn nhìn người mang tên họ này trông như thế nào …Niệm Tưởng sớm đã thành thói quen.\r\n\r\nTừ Nhuận Thanh hình như là cười một chút, ánh mắt hơi cong cong, nâng ngón tay lên, đẩy cằm cô cao hơn.\r\n\r\nVừa tắm qua, ngón tay có chút lạnh.\r\n\r\nMột tay anh cố định cằm của cô, một tay khác bóp nhẹ cằm dưới kéo xuống một chút, tách miệng cô ra, ngón tay tiếp xúc trên da của cô, nhỏ giọng hỏi: “Đau ở đâu?”\r\n\r\n“Bên phải… Tận cùng bên trong … là răng khôn…” Bởi vì bị ép mở miệng, thanh âm của cô mơ hồ không rõ.\r\n\r\nVóc dáng anh rất cao, nghe vậy lại nâng nâng cằm của cô, hơi hơi cúi người, cẩn thận quan sát răng khôn của cô. Mi tâm đẹp chậm rãi nhăn lại, ngữ điệu bình thản: “Răng của cô đã có vấn đề từ rất lâu.”\r\n\r\nNiệm Tưởng đáng thương gật gật đầu, đích xác đã lâu rồi, nếu như không phải bị đau đến chịu không nổi, cô phỏng chừng vẫn sẽ kiên trì không đến nơi này.\r\n\r\n…\r\n\r\n…\r\n\r\nĐây là mùa thu của rất nhiều năm về trước.\r\n\r\nLúc bắt đầu, cô là bệnh nhân của anh.\r\n\r\nMà rất nhiều năm về sau, cũng vào một mùa thu hoa lá rụng đầy trời, anh ngồi trên ghế, nhìn hai chữ “Niệm Tưởng” trên phiếu hẹn trước, thì nhịn không được hơi hơi cong môi.\r\n\r\n———————————————————–\r\n\r\nKhông xong…\r\n\r\nNiệm Tưởng nhìn kính chiếu hậu âm thầm nhíu mày, sao cô mới đi lên mua mấy thùng sữa thôi, bên cạnh xe cô đã đổ đầy xe thế này!\r\n\r\nCô cầm vô lăng điều chỉnh cả nửa ngày, cuối cùng nhịn không được hạ cửa kính xe xuống, nhìn về sau xe. Vừa rồi cô lui xe, góc độ không được tốt lắm, tay lái đánh nửa vòng, thế cho nên hiện tại bên phải không thể động đậy.\r\n\r\nỪm, đối diện đuôi xe là một chiếc Maserati, phía bên phải cô là một chiếc Audi, bên trái là Land Rover. Xong, dù đụng phải xe nào cũng đều muốn táng gia bại sản.\r\n\r\nCô lui người về trong xe, âm thầm bơm hơi cho mình, bình tĩnh một chút… Tìm đúng góc độ, thử lại một lần nữa.\r\n\r\nNiệm Tưởng nắm nhẹ vô lăng, hít một hơi thật sâu, từ kính chiếu hậu nhìn ra góc độ nhỏ mà điều chỉnh, thẳng tiến.\r\n\r\nMột thanh âm va chạm nhẹ nhàng vang lên, cùng với thân xe bị chấn động một cái.\r\n\r\nDa đầu Niệm Tưởng nhất thời run lên một trận, trong lòng bàn tay đều đổ mồ hôi lạnh. Cô nhìn sang kính chiếu hậu bên phải, thật muốn khóc.\r\n\r\nĐụng rồi… Đụng rồi!\r\n\r\nNiệm Tưởng cắn môi một cái, lập tức xuống xe đi kiểm tra tình hình.\r\n\r\nTình hình… Không tính là quá xấu, nhưng tuyệt đối không được tốt lắm. Xe của cô trực tiếp đụng vào thân xe Audi, cọ trầy một lớp sơn. Tầm mắt của cô chạm đến đèn xe trên thân, gần chỗ bị cọ vào, tròng mắt chuyển động hai vòng, khóc không ra nước mắt.\r\n\r\nVì thế, kế tiếp, Niệm Tưởng liền nghĩ ở trong xe đợi chủ xe xuống bàn bạc cách giải quyết, hay là nên ở ngoài xe đợi, đợi khi chủ xe vừa đến, lập tức giải thích để bày tỏ thành ý, tranh thủ được khoan hồng.\r\n\r\nKết quả cuối cùng là… Vẫn nên ở trong xe chờ, vạn nhất gặp chủ xe hung hãn, không nói lý, cô lại không có kinh nghiệm xử lý sự cố, nhất định sẽ chịu thiệt.\r\n\r\nHơn nữa, điểm quan trọng nhất là, xe bị kẹt không ra được, nếu cô tiếp tục muốn nhích ra, không biết tiếp theo bị đụng vào có phải là Maserati hay không, rồi lao sang Land Rover kế bên…\r\n\r\nCô bóp trán, quay người vừa muốn lên xe lấy di động, liền nghe thấy tiếng bước chân từ xa đi lại gần.\r\n\r\nNiệm Tưởng quay người nhìn lại, ở khúc quanh của xe có một người đàn ông đang đứng, trong tay cầm một túi đồ, đang nhìn về phía này, biểu cảm —— tựa hồ có chút phức tạp.\r\n\r\nNgười đàn ông ghé mắt nhìn cô một cái, chân mày chậm rãi nhăn lại, đi nhanh tới. Đợi đến khi nhìn thấy xe cô đang thân mật với vách xe Audi, thì Niệm Tưởng nhận thấy rõ rệt rằng độ ấm quanh thân đang giảm một cách đột ngột.\r\n\r\nNiệm Tưởng có chút khẩn trương liếm môi một cái, châm chước từ ngữ mở miệng: “Cái kia… xin chào tiên sinh… tôi không cẩn thận lui xe, đụng trúng xe anh…”\r\n\r\n“Tôi thấy được.” Anh chặn lời cô, đứng thẳng người, cúi xuống nhìn về phía cô.\r\n\r\nTrong bãi đỗ xe của siêu thị, ánh sáng cũng không phải là tốt, bởi vì hoàn cảnh chật chội, có vẻ hơi mờ tối.\r\n\r\nĐối diện, cô bé kia rõ ràng rất khẩn trương, nhìn anh không nháy mắt.\r\n\r\nÁnh mắt Từ Nhuận Thanh từ trên mặt của cô trượt đến hai tay đang nắm chặt, đặt xuôi ở bên người, giơ tay bóp nhẹ mi tâm một chút, lại mở miệng thì thanh âm cũng trầm xuống vài phần: “Cho tôi số điện thoại liên lạc, sau khi sửa chữa, chỗ tiền đó cô chịu trách nhiệm.”\r\n\r\nNiệm Tưởng hiển nhiên không nghĩ tới anh dễ nói chuyện như vậy, ngước mắt, cẩn thận nhìn vào mắt anh.\r\n\r\nAnh cầm chìa khóa xe mở khóa, đèn hai bên thân xe lóe lên một chút, phát ra tiếng “Tích tích”. Anh đi đến cạnh cửa xe bên phải, mở cửa,  đem đồ bỏ vào, thế này mới lại đi đến trước mặt cô.\r\n\r\nThấy cô không có phản ứng, hơi nhướn mi một chút, khẽ giọng hỏi: “Không muốn chịu trách nhiệm?”\r\n\r\nĐôi mắt Niệm Tưởng hơi mở, nhanh chóng lắc đầu phủ nhận: “Không, không, không phải, tôi sẽ phụ trách, nhất định phụ trách. Tôi cho anh số điện thoại, sau khi xe anh sửa xong, hãy gọi cho tôi.”\r\n\r\nNói xong, quay người liền lấy giấy bút trên xe.\r\n\r\nTừ Nhuận Thanh nhìn cô tay chân luống cuống, ngón tay xoay quanh chìa khóa một vòng lại một vòng, như có đăm chiêu.\r\n\r\nNiệm Tưởng viết xong dãy số di động, nghĩ nghĩ, lại muốn viết tên lên. Ngòi bút chạm vào mặt giấy thì lại có một do dự trong chớp mắt—— trực tiếp cho tên thật không có vấn đề gì chứ?\r\n\r\nLoại chuyện này, không nói tên thật càng có vấn đề a. Cô nhận mệnh viết xuống tên của mình, viết xong còn để ở trong tay quan sát vài lần… Rất tốt, dù sao tên của cô trông cũng giống tên giả.\r\n\r\nCô đem giấy ghi chép đưa cho anh, thấy chân mày anh vừa giãn ra lại nhăn tít lại, biểu tình trên mặt nghiêm túc hơn vài phần, cho rằng anh không tin, liền giải thích: “Anh yên tâm, tôi sẽ không chơi xấu, tôi có thể ghi thêm địa chỉ trường học… Bằng không, tôi cho anh xem thẻ căn cước?”\r\n\r\nTừ Nhuận Thanh: “…”\r\n\r\nAnh trầm mặc, Niệm Tưởng liền thật sự muốn lấy ra chứng minh thư cho anh xác nhận.\r\n\r\nAnh ho nhẹ một tiếng, thanh âm có chút khàn khàn: “Không cần…”\r\n\r\nNiệm Tưởng ngẩng đầu nhìn anh.\r\n\r\nÁnh mắt cô bé trong veo sáng ngời, cứ như vậy chuyên chú nhìn anh, còn có thể từ trong mắt cô thấy rõ bóng hình của anh.\r\n\r\nNgón tay Từ Nhuận Thanh duỗi ra một chút, kẹp tờ giấy ghi chép: “Vậy được rồi… Ở đây có camera, nếu như không tìm được cô, tôi có thể báo bảo vệ của siêu thị.”\r\n\r\nNiệm Tưởng quyết định thu hồi lời khen vừa rồi, anh mà dễ nói chuyện cái gì, cái này… giống như là cảnh cáo, cô lặng lẽ lau lệ.\r\n\r\nTừ Nhuận Thanh cất xong tờ giấy, thấy bộ dáng cô ủ rũ, chăm chú nhìn về góc nghiêng của xe, lại loay hoay nhìn bốn phía, nhất thời sáng tỏ: “Xe không ra được?”\r\n\r\nCô gật gật đầu, vẻ mặt càng thêm uể oải.\r\n\r\nTừ Nhuận Thanh suy nghĩ một lát: “Lên xe.”\r\n\r\nNiệm Tưởng sửng sốt, thấy anh cúi mắt nhìn mình, lại hất hất cằm ý bảo mình lên xe, thế này mới phản ứng kịp.\r\n\r\nTừ Nhuận Thanh nhìn vị trí của xe, chân mày nhíu chặt hơn  —— cô làm sao đổ xe được thành như vậy? Nếu như nhất định phải hình dung tình huống hiện trường, phỏng chừng chỉ có thể dùng bốn chữ “Vô cùng thê thảm” đến hình dung.\r\n\r\nAnh đi đến bên cạnh xa, ngón tay khoát lên trên cửa kính xe, nhẹ nhàng mà gõ một cái: “Giữ chắc thắng, rồi nhích từng chút một.”\r\n\r\nNiệm Tưởng lập tức nghe theo, nghe được anh nói “Giữ thắng” thì lúc nào chân phải cũng chuẩn bị lập tức đạp xuống.\r\n\r\n“Hướng bên phải vòng 90 độ.”\r\n\r\n“… Được.”\r\n\r\n“Nhích lên một chút, đến khi tôi nói dừng thì dừng.”\r\n\r\n“Được.”\r\n\r\nTừ Nhuận Thanh cúi người, hướng mắt vào trong xe nhìn: “Phương hướng không đánh tốt.” Nói xong, anh dứt khoát đưa tay nắm vô lăng trong tay cô.\r\n\r\nNiệm Tưởng lúc này mới chú ý đến ngón tay anh, thon dài trắng nõn, khớp xương rõ ràng. Hơi hơi cong tay thì đường cong rất ưu nhã, ngay cả móng tay cũng được cắt rất chỉnh tề, sạch sẽ, nhìn qua hẳn là được chú ý chăm sóc.\r\n\r\nCô nhịn không được thất thần, nâng mắt nhìn anh một cái, lại không ngờ, vừa lúc chống lại ánh mắt anh, bị ánh sáng đong đưa nơi đáy mắt trong vắt của anh làm sửng sốt một chút, cô nhanh chóng cúi đầu che dấu, nhìn xuống vô lăng.\r\n\r\nTừ Nhuận Thanh híp mắt lại một chút, thế này mới dời tầm mắt.\r\n\r\nNăm phút sau ——\r\n\r\nKhốn cảnh đã được giải quyết.\r\n\r\nTại lần thứ ba Niệm Tưởng nói cám ơn, anh hướng ánh nhìn về cửa kính phía sau xe, không có nhìn thấy hai chữ “Tập lái” thì hỏi: “Lấy giấy phép lái xe được bao lâu?”\r\n\r\nNiệm Tưởng nhếch môi không trả lời… Nếu nói bây giờ cô không có bằng lái, có thể bị bắt lại hay không…', 2, 'tu-tu-du-do.jpg', 10, '2020-09-16 20:54:25', '1');
INSERT INTO `story` (`story_id`, `name`, `preview_text`, `detail`, `cat_id`, `picture`, `counter`, `create_at`, `status`) VALUES
(5, 'Công Chúa, Ngoan Một Chút', 'Một gia đình cùng nhau xuyên tới tương lai. Không phải là gia đình bình thường đâu nha, là gia đình hoàng thất chính hiệu. \r\n\r\nAii... Thời đại và tư tưởng khác biệt, cả gia đình cũng biến hoá rõ rệt. Phụ hoàng ăn bám, mẫu hậu mạnh mẽ, thái tử ca ca nổi lo', 'Chúc Yểu bị dì Phương bảo mẫu đánh thức.\r\n\r\nRèm cửa sổ màu kem bị kéo ra, những tia nắng ban mai xuyên qua lớp kính thủy tinh, chiếu vào phòng thật ấm áp. Chúc Yểu ngồi dậy, mái tóc dài mềm mại hơi rối. Cô đưa tay dụi nhẹ đôi mắt vẫn đang lim dim rồi mới hất chăn mền ra, bước xuống giường, xỏ chân vào đôi dép lê bằng vải mềm, đi vào nhà vệ sinh rửa mặt.\r\n\r\nBàn chải đánh răng màu hồng nhạt, lông bàn chải mềm và mảnh, mùi bạc hà của kem đánh răng lan tỏa trong miệng, mát mẻ sảng khoái.\r\n\r\nĐánh răng xong, tiếp theo là rửa mặt. Sau một tuần học tập, Chúc Yểu đã bắt đầu thích nghi với cuộc sống hiện tại. Treo chiếc khăn mặt đã được vắt khô lên giá, sau đó chọn một sợi dây thun trong chiếc hộp kim loại để trên bồn rửa mặt, buộc cao mái tóc dài ngang vai lên.\r\n\r\nỞ ngoài, dì Phương đã lấy sẵn cho cô quần áo cần mặc hôm nay: áo ngắn tay màu trắng, váy xếp li màu xanh đậm.\r\n\r\nChúc Yểu bước tới, nhặt chiếc váy xếp li trên giường lên, đặt trên hông mình để ướm thử. Chiếc váy dày dặn, chất lượng rất tốt, theo cô đánh giá thì đúng là rất đẹp, có điều… ngắn quá.\r\n\r\nCuối cùng, Chúc Yểu không chịu mặc váy. Cô vào phòng để quần áo, từ trong một hàng dài váy áo rực rỡ muôn màu chọn ra một chiếc quần đồng phục rộng rãi, mặc vào.\r\n\r\nThay quần áo xong thì xuống lầu.\r\n\r\nBên bàn ăn, Chúc Tấn Ung và Tiêu Minh Châu đang dùng bữa sáng. Mùi cháo thơm ngào ngạt ăn kèm với dưa muối, cộng thêm mấy cái bánh điểm tâm nhỏ nhỏ xinh xinh.\r\n\r\nChúc Tấn Ung mặc bộ đồ ngủ, trông rất lười nhác, tay trái đang xé một cái bánh bao kim sa, thấy vợ mình – người đang ngồi ở ghế chủ tọa – vừa ăn dở dang vừa cúi đầu đọc báo, không nhịn được bèn nói: “Ăn uống cho đàng hoàng được không!”\r\n\r\nKhác với bộ dáng nhàn nhã của Chúc Tấn Ung, Tiêu Minh Châu hiển nhiên là 24/24 luôn duy trì phong thái sắc sảo của một nữ cường nhân: dung mạo, trang phục chỉn chu, mái tóc ngắn gọn gàng. Chúc Tấn Ung vừa nói xong, một ánh mắt sắc như dao lập tức phóng tới, giọng nói nghiêm nghị: “Lo mà ăn phần ông đi!”\r\n\r\nChúc Tấn Ung há hốc mồm, nhét nửa cái bánh bao còn lại trên tay vào miệng, tức tối lắm nhưng không dám nói gì.\r\n\r\nNhìn dáng vẻ nhẫn nhục chịu đựng của Chúc Tấn Ung, không ai ngờ được rằng chỉ một tháng trước đây thôi, người đàn ông trung niên mặc bộ đồ ngủ bằng lụa kia đã từng ngồi trên ngai vàng trong hoàng cung Đại Ngụy, được quần thần cúi đầu bái lạy.\r\n\r\nLúc đó Chúc Tấn Ung là hoàng đế đời thứ sáu của Đại Ngụy, còn Tiêu Minh Châu – người mặc đồ tây, mang giày da bên cạnh – lại là vị hoàng hậu thanh cao nho nhã, hiền thục đức hạnh của ông. Mười bốn tuổi vào cung làm hoàng hậu, lần lượt hạ sinh một trai một gái: Thái tử Chúc Hằng và tiểu công chúa Chúc Yểu.\r\n\r\nKhi đó, Chúc Tấn Ung là hoàng đế, còn Tiêu Minh Châu chỉ là một người phụ nữ quản lý hậu cung. Tuy đã từng xinh đẹp nức tiếng kinh thành, nhưng đối với một hoàng đế có mới nới cũ mà nói, bà đã là hoa héo cuối mùa. Vị hoàng đế phong lưu bất tài này sủng ái nhất chính là Từ quý phi xinh tươi diễm lệ, còn bà hoàng hậu già nua kia thì không thèm liếc nhìn một cái.\r\n\r\nNào ngờ trong bữa tiệc tại Vũ Anh điện hôm đó, yến tiệc linh đình, xa hoa khí phái, tiếng đàn sáo du dương bên tai, cảnh tượng phồn hoa, ca múa tưng bừng nhưng chỉ một khắc sau, trên trời giáng xuống một viên thiên thạch khổng lồ, vừa vặn rơi trúng hàng ghế chủ tọa trên Vũ Anh điện…\r\n\r\nThế rồi trước mắt tối sầm, đôi vợ chồng hoàng thất này mang theo cả gia đình đầu thai chuyển kiếp đến nơi này: Tấn Thành của thế kỷ 21.\r\n\r\nBà hoàng hậu mà ông ta từng chán chê như đôi giày rách kia, thoắt cái trở thành chủ tịch tập đoàn Bích Mậu, là nữ cường nhân tiếng tăm lừng lẫy trong lĩnh vực bất động sản.\r\n\r\nCòn vị hoàng đế như ông lại trở thành chàng rể ăn bám, phụ thuộc vào nhà họ Tiêu.\r\n\r\nKết hôn được ba năm, sau khi sinh được hai đứa con, Tiêu Minh Châu bèn tiếp quản cơ nghiệp của dòng họ, vứt ông chồng của mình ở nhà, thành kẻ ăn bám.\r\n\r\nTuy nói là đầu thai chuyển kiếp, nhưng ai dè tất cả mọi người đều lưu lại ký ức của vương triều Đại Ngụy…\r\n\r\nĐiều này làm cho vị hoàng đế sinh ra trong nhung lụa hoàng gia này không cách nào coi như không có chuyện gì, nuốt trôi miếng cơm mà Tiêu Minh Châu ban cho mình.\r\n\r\nCòn Tiêu Minh Châu thì lại rất hài lòng với thân phận nữ chủ tịch độc tài của mình bây giờ.\r\n\r\nChúc Tấn Ung hiện nay, đã không còn là hoàng đế cao cao tại thượng của Đại Ngụy nữa, cho nên hai đứa con theo họ của ông ta đã là phần phước lớn nhất rồi. Tiêu Minh Châu nhớ đến cuộc sống trong hoàng cung khi xưa là không thể có thái độ ôn hòa với Chúc Tấn Ung.\r\n\r\nBây giờ, nếu ông ta ngoan ngoãn ăn bám thì không sao, nhưng nếu có ý kiến ý cò gì, chỉ cần một tờ giấy thỏa thuận ly hôn quăng ra là có thể khiến ông ta về quê làm ruộng.\r\n\r\nChúc Yểu mang đôi dép lê, từ trên cầu thang đi xuống. Hiển nhiên là đã quá quen với cảnh cha mẹ om sòm với nhau nên ngoan ngoãn ngồi xuống ăn sáng.\r\n\r\nKhác với mấy người Chúc Tấn Ung, có ký ức của cả hai kiếp, ký ức của Chúc Yểu lại không được đầy đủ. Những ký ức về hoàng cung Đại Ngụy thì rất rõ ràng, như mới hôm qua vậy. Còn ký ức ở Tấn Thành hiện đại thì lại hơi mơ hồ, nhiều khi gặp phải những người quen hoặc việc quen thì mới từ từ nhớ lại.\r\n\r\nĐiều này có thể là do cô đầu thai chuyển kiếp chậm hơn người khác.\r\n\r\nHôm đó thiên thạch giáng xuống, rõ ràng là cô ngồi ngay bên cạnh cha mẹ, theo lý mà nói thì phải đi ngày lúc đó mới phải. Nhưng không biết vì nguyên nhân gì mà cô sống thêm ở Đại Ngụy đến bảy ngày.\r\n\r\nTuy Tiêu Minh Châu rất lạnh nhạt với Chúc Tấn Ung nhưng lại cực kỳ yêu thương cô con gái Chúc Yểu này. Bất luận là lúc còn ở Đại Ngụy hay đến Tấn Thành hiện tại, Chúc Yểu luôn là bảo bối trong lòng Tiêu Minh Châu. Thấy Chúc Yểu, Tiêu Minh Châu lập tức mỉm cười rồi  tự tay gắp một cái bánh bao kim sa đặt vào cái đĩa ăn trước mặt Chúc Yểu.\r\n\r\nHôm nay Chúc Yểu phải đi học, Tiêu Minh Châu rất lo lắng: “Đến trường, nếu bị bạn học bắt nạt thì phải nói với anh con đó.”\r\n\r\n“Dạ.” Chúc Yểu ngoan ngoãn gật đầu. “Con biết mà mẹ.”\r\n\r\nNơi này khác với Đại Ngụy. Dù cô là con gái thì cũng có thể xuất đầu lộ diện, đến trường học tập giống như con trai.\r\n\r\nChúc Yểu và anh trai cô Chúc Hằng đều học chung một trường cấp ba. Chúc Hằng vốn học trên cô một khóa, nhưng đến học kỳ 2 của lớp 11 bỗng dưng bị bệnh nặng, nghỉ một học kỳ nên mới ở lại lớp.\r\n\r\nNăm nay hai anh em vừa vặn cùng nhau lên lớp 12.\r\n\r\nTrên lầu vang lên tiếng động, là tiếng bước chân đi trên mặt sàn.\r\n\r\nTiếp đó là giọng nói sang sảng của một chàng thiếu niên: “Ai dám bắt nạt em gái ông, ông đây đánh chết nó!”\r\n\r\nBất luận là chàng thái tử ăn chơi ngày xưa, hay là cậu thiếu niên nổi loạn bây giờ, Chúc Hằng luôn rất bênh vực em gái. Cậu tròng cái áo thun qua khỏi đầu mình, bước tới xoa đầu em gái, còn tay kia thì bốc một cái bánh bao nhỏ, nhúng vào nước chấm rồi nhét vào miệng.\r\n\r\nGiọng cậu ngồm ngoàm không rõ: “Mẹ cứ yên tâm đi, ở trường có con che chở cho Yểu Yểu, đảm bảo nó có thể đi ngang trong trường.”\r\n\r\nTiêu Minh Châu rất vui vẻ, mở khóa màn hình điện thoại, ấn một dãy số, thế là một khoản tiền tiêu vặt được chuyển sang.\r\n\r\nĐiện thoại phát ra tiếng những đồng tiền kêu leng keng.\r\n\r\nChúc Hằng tươi cười nịnh nọt: “Tạ ơn mẫu hậu.”\r\n\r\nHừ! Chúc Tấn Ung đã quá quen với vẻ mặt này, chẳng khác gì với Cao Đức hầu hạ ông ta khi xưa. Có điều bây giờ nhà họ Chúc đã không còn ngôi vị hoàng đế để kế thừa, ông chỉ là một người nhàn hạ, ngồi trong nhà, phải dựa vào số tiền vợ phát cho mỗi tháng để sống. Vì thế ông tạm thời im như thóc, chẳng có tư cách gì mà đi trách mắng con trai.\r\n\r\n……\r\n\r\nTrên chiếc xe riêng của họ, hai anh em một cao một thấp cùng ngồi trên băng ghế sau. Tối qua Chúc Hằng chơi game rất khuya nên bây giờ, hai tay khoanh trước ngực, ngửa mặt, dựa vào lưng ghế ngủ gật.\r\n\r\nChúc Yểu thì lại khác, chịu ảnh hưởng của nghi lễ hoàng gia từ nhỏ nên tư thế ngồi rất ngay ngắn, cần cổ mảnh mai thon dài lộ ra khỏi cổ áo đồng phục màu xanh lam.\r\n\r\nNgoài cửa sổ, những tòa nhà cao tầng san sát nhau, ngựa xe như nước, người người qua lại, giống hệt như những gì mà cô nhìn thấy trên TV mấy ngày nay.\r\n\r\nKhác với vẻ rực rỡ huy hoàng của hoàng thành Đại Ngụy, nhưng lại có điểm đặc sắc riêng.\r\n\r\nChúc Hằng từ từ mở mắt ra, nhìn em gái mình, ánh mắt đầy vẻ cưng chiều, giọng nói cũng khác với thường ngày, vô cùng ấm áp: “Nơi nay rất tốt phải không. Ở hoàng cung, tuy chúng ta là hoàng tử công chúa nhưng làm gì được tự do như bây giờ!”\r\n\r\nKhông còn bị phụ hoàng răn dạy, mẫu hậu lại bận, không có thời gian trông nom bọn họ.\r\n\r\nVừa nói xong thì chợt nhớ ra điều gì, Chúc Hằng đặt hai tay ra sau đầu, gối lên đó, giọng nghe có vẻ biếng nhác: “Cũng không còn bị ép lấy người mà mình không thích.”\r\n\r\nChúc Yểu không nhịn được, bèn nói: “Tiểu thư của Phùng gia rất tốt mà.”\r\n\r\nLúc còn là thái tử, Chúc Hằng rất ham chơi, tính cách buông tuồng, hoàn toàn không thích kiểu con gái hiền như cục đất. Trong ký ức của cậu, cô gái kia mỗi lần nhìn thấy cậu là rất sợ hãi, cứ ngây ra, hoàn toàn không có vẻ gì là tri thức lễ nghĩa như phụ hoàng mẫu hậu hay nói. Có điều bây giờ cũng không quan trọng nữa rồi. Thái tử Chúc Hằng của Đại Ngụy đã chết, cô ấy không cần phải gả cho cậu.\r\n\r\n“Có điều…”\r\n\r\nChúc Hằng nhướng mày, bỗng nhiên có chút tiếc nuối: “Có điều không được gặp thái phó nữa… cũng rất nhớ ngài ấy.” Giọng nói ngày càng nhỏ đi.\r\n\r\nBàn tay đặt trên đùi của Chúc Yểu đột nhiên siết chặt lại, nhưng không nói gì.\r\n\r\nChúc hằng không để ý, tiếp tục hồi tưởng lại những ngày tháng mà thái phó còn dạy bảo mình. Người đó tuy rằng tuổi đời còn rất trẻ, nhưng lại là người duy nhất mà cậu kính nể. Theo lý mà nói, không cần phải chịu sự dạy dỗ nghiêm khắc của thái phó, lẽ ra cậu nên vui mừng. Nhưng đột nhiên thái phó không trông chừng cậu nữa, cậu bỗng thấy có chút mất mát.\r\n\r\nNhớ đến một việc, cậu nói với em gái mình: “Ngẫm lại khi đó, nếu không nhờ thái phó ra mặt, có lẽ phụ hoàng đã đưa muội đến xứ man di đó để hòa thân rồi… Vì  chuyện này, Chúc Hằng ta cảm kích ngài ấy cả đời.”\r\n\r\nHàng mi Chúc Yểu hơi run run, đương nhiên là cô vẫn nhớ…\r\n\r\nĐang chìm trong hồi ức thì xe đã dừng lại. Trường học cách nhà Chúc Yểu không xa, chỉ chốc lát đã đến trước cổng trường. Chúc Yểu quay đầu qua nhìn ra ngoài cửa sổ: Toàn là một đám học sinh mặc đồng phục, tốp năm tốp ba, cùng nhau vào trường.\r\n\r\nChúc Yểu chú ý đến quần áo của những nữ sinh kia.\r\n\r\nÁo trắng cùng với váy xếp li ngang gối màu xanh đậm. Sáng nay dì Phương cũng chuẩn bị cho cô một bộ như vậy, có điều cô cảm thấy cái váy đó quá ngắn, mặc ra ngoài có vẻ không được lịch sự cho lắm, cho nên mới đổi thành quần dài. Bây giờ xem ra, cách ăn mặc ấy dường như không có gì là không ổn, trái lại cô thì quá kín đáo, trông có vẻ hơi gây sự chú ý.\r\n\r\nChúc Hằng xuống xe, rồi vòng qua bên này mở cửa cho em gái.\r\n\r\nĐầu tiên là lấy cặp sách của Chúc Yểu ra, đợi cô xuống xe xong thì đeo cặp lên lưng cho cô. Cậu lớn hơn Chúc yểu một tuổi, hai năm nay vóc dáng cao lớn rất nhanh. Cậu đóng cửa xe lại, nói với em gái: “Được rồi, em vào trước đi.”\r\n\r\nChúc Yểu sửa sang lại quai cặp xong, nghe Chúc Hằng nói thế thì nghi hoặc hỏi: “Anh không vào chung với em à?”\r\n\r\nLàm sao mà cậu lại ngoan ngoãn đi học được? Còn chưa chơi đã mà. Chúc Hằng xoa đầu em gái, nói: “Ngoan, em vào trước đi.”\r\n\r\nSau đó bèn khoác hờ chiếc ba lô trên một vai, đi về phía quán net ở gần đó.\r\n\r\nChúc Yểu đeo cặp sách, nhìn theo bóng dáng xa dần của anh trai, sau đó mới quay người qua, nhìn về phía cái bảng có viết mấy chữ: Trường trung học Hành Dương, Tấn Thành.\r\n\r\nBuổi sáng, học sinh rất đông, ai nấy nhìn cũng tinh thần phơi phới. Chúc Yểu cũng đi vào, ùa theo dòng người bước vào cổng trường.\r\n\r\nĐội Sao Đỏ của hội học sinh đang kiểm tra phù hiệu. Một nam sinh mang phù hiệu của hội học sinh trước ngực, tay cầm cuốn sổ ghi chép, mắt nhìn chằm chặp vào các học sinh đang đi vào.\r\n\r\nTrời nóng, ai cũng mặc áo ngắn tay, các nữ sinh thì đồng loạt mặc váy xếp li, đột nhiên nhìn thấy một bóng người mặc áo dài tay, quần dài nốt, mắt cậu ta bất ngờ khựng lại, sau đó nhanh chóng gọi Chúc Yểu. “Bạn gì ơi, phù hiệu của bạn đâu?”\r\n\r\nNam sinh nói với giọng rất nghiêm túc, đứng trước mặt cô, thấy trước ngực cô trống không, quả nhiên là không mang phù hiệu.\r\n\r\nThế là cậu ta lấy bút ra, định bắt đầu ghi tên. “Tên là gì? Ở lớp nào?”\r\n\r\nCái phù hiệu nho nhỏ kia, trên đó còn có hình thẻ của cô… Chuyện mang phù hiệu, tối hôm qua Chúc Yểu vẫn nhớ. Cô nghĩ ngợi một chút rồi báo tên và lớp của mình: “Tôi học lớp 12.9, tên là Chúc Yểu.”\r\n\r\nGiọng nói ngọt ngào êm tai, ngón tay đang cầm bút của cậu học sinh bỗng sựng lại một chút.\r\n\r\nLớp 12… Cô bạn này nhỏ nhắn xinh xắn, gương mặt bé bằng bàn tay, đôi mắt nai vừa to tròn vừa trong trẻo, làn da trắng đến sáng lên.\r\n\r\nNgoại hình vô cùng bắt mắt.\r\n\r\nNhìn có vẻ rất nhỏ, bảo lớp 10 cậu cũng tin. Bây giờ bị ghi tên, trừ điểm thi đua của lớp mà vẻ mặt của cô vẫn rất bình tĩnh, thoạt nhìn chẳng lo lắng chút nào.\r\n\r\nĐang quan sát, Chúc Yểu ngước hàng mi lên, đôi mắt trong veo như hồ nước nhìn về phía cậu.\r\n\r\nNam sinh giả vờ trấn tĩnh tằng hắng một cái, vành tai đỏ bừng lên. cậu cầm lấy bút, cúi đầu lẩm bẩm: “À, lớp 12.9 sao, tên là Chúc… Chúc gì ấy nhỉ?”\r\n\r\n“Yểu.”\r\n\r\nChúc yểu mỉm cười, hai mắt cong cong như hai vầng trăng, ôn hòa giải thích. “Yểu trong yểu điệu thục nữ.”\r\n\r\nMẹ ơi! Sao mà ngoan hiền thế nhỉ. Cậu học sinh nghĩ thầm trong bụng.\r\n\r\n“Được rồi.” Nam sinh hơi mỉm cười, gãi vào sau gáy với vẻ mất tự nhiên, giọng bỗng trở nên rất dịu dàng. “Vậy… cậu mau vào trong đi.”\r\n\r\nChúc Yểu gật đầu, không nhanh không chậm cất bước.\r\n\r\n“Thật là đẹp quá đi mất… Chẳng lẽ là học sinh mới chuyển đến?” Cậu ta nhìn chằm chằm vào cái tên trên cuốn sổ, lớp 12.9 khi nào thì có một học sinh xinh đẹp nhường này, sao cậu lại không hay biết nhỉ?\r\n\r\nĐang nghĩ ngợi, cuốn sổ trên tay bỗng bị ai đó giật mất.\r\n\r\nCậu ta ngẩng đầu lên, nhìn dáng người cao lớn trước mặt, mắt lập tức sáng rỡ. “Đúng rồi! Đội trưởng, chẳng phải cậu cũng học lớp đó sao?”\r\n\r\nNgười kia không trả lời.\r\n\r\nCó điều đôi mắt lại nhìn chằm chặp vào cái tên trên cuốn sổ, sau đó dùng ngón tay cái đẩy nắp bút ra. Nắp bút rơi vào giữa hai trang giấy. Ngón tay thon dài cầm lấy cây bút lông màu đen.\r\n\r\nXoẹt một tiếng, gạch cái tên trên đó đi.', 2, 'cong-chua-ngoan-mot-chut.jpg', 6, '2020-09-16 20:54:21', '1'),
(6, 'Boss À! Tha Cho Em', 'Cố Thiên Vũ hiểu được sức mạnh của tiền tài và quyền lực. Trong giới hắc đạo lăn lộn, anh không tin tưởng tình yêu sẽ tồn tại, đối với anh chỉ có lợi dụng hoặc là trao đổi lợi ích mà thôi. Nhưng khi gặp cô, suy nghĩ của anh dần thay đổi.', 'Bệnh viện Hope. 8h sáng\r\n\r\n- \" chào buổi sáng, bác sĩ Lâm \". Tiếp tân bệnh viện nói.\r\n\r\n- \" chào mọi người \". Lâm Nguyệt Y nở nụ cười rạng rỡ chào lại mọi người.\r\n\r\n- \" ây cha cha, Nguyệt Y, hôm qua vừa thất tình, ở nhà lôi tớ ra tâm sự, khóc lóc thảm thiết lắm mà, sao sáng nay tươi tỉnh dữ vậy \". Lưu Vy từ xa đi lại nói.\r\n\r\n- \" nè, đừng nói chuyện đó ở đây chứ, mất hình tượng tớ hết \". Lâm Nguyệt Y vội nắm tay áo Lưu Y đi ra chỗ khác.\r\n\r\nLâm Nguyệt Y và Lưu Vy làm việc ở bệnh viên Hope được 3 năm. Lâm Nguyệt Y làm bên khoa ngoại, còn Lưu Ly làm y tá. Từ nhỏ họ đã chơi thân với nhau, Lâm Nguyệt Y có mặt ở đâu là Lưu Vy có mặt ở đấy.\r\n\r\nỞ sảnh trước bệnh viện, giờ giải lao.\r\n\r\n- \" nè, y tá Lưu Vy, cô có nghe tin gì chưa? \". Tổ trưởng tổ y tế hỏi.\r\n\r\n- \" chuyện gì là chuyện gì?\". Lưu Vy thắc mắc hỏi.\r\n\r\n- \" trời, tin động trời vậy mà không biết. Để kể cho nghe. Hai hôm trước, viện trưởng bán bệnh viện này rồi \".\r\n\r\nLưu Vy đang uống nước liền phun hết ra ngoài.\r\n\r\n- \" cái cái gì, bán bệnh viện này, mà.... mà bán cho ai, chẳng phải viện trưởng đang rất bình thường sao, sao lại bán? \". Lưu Vy hấp tấp hỏi.\r\n\r\n- \" ai biết đâu, tôi nghe được là, viện trưởng thiếu nợ một con số rất lớn, nên bán bệnh viện rồi, bán cho chủ nợ. Mà chủ nợ không phải là bác sĩ, không biết chút gì về ngành y hết. Nghe nói người thu mua là chủ tịch tập đoàn Cố Thị \".\r\n\r\n- \" chủ tịch tập đoàn Cố Thị? \". Lưu Vy không biết người này, nên hỏi lại.\r\n\r\n- \" đừng nói là y tá Lưu Vy không biết nha. Chủ tịch tập đoàn Cố Thị là một người đàn ông khoảng hai mấy gần ba chục tuổi gì đó, nghe nói lạnh lùng kiêu ngạo lắm, nhưng bù lại, hắn rất tài năng, còn đẹp trai nữa. Ở độ tuổi đó mà đã nắm trong tay số tiền không hề nhỏ, còn những tập đoàn khác nữa \".\r\n\r\nLưu Vy nghe đến đẹp trai là hai mắt sáng rực lên. Cô để ly nước xuống rồi chạy vào phòng làm việc của Lâm Nguyệt Y.\r\n\r\nPhòng làm việc của Lâm Nguyệt Y.\r\n\r\nCạch, cửa phòng mở ra. Lưu Vy hối hãi chạy vào.\r\n\r\n- \" Nguyệt Y, có thông tin hot, muốn nghe không \".\r\n\r\n- \" thì nói đi, tớ nghe \". Lâm Nguyệt Y vẻ mặt khó hiểu nhìn Lưu Vy.\r\n\r\nLưu Vy đem mọi chuyện kể lại hết, nhưng vẻ mặt của Lâm Nguyệt Y vẫn bình thường, nàng không quan tâm, nàng quay sang hỏi một câu bằng giọng nói lạnh lùng.\r\n\r\n- \" thì sao?\".\r\n\r\nLưu Vy nghe xong hai chữ \" thì sao \", thì vẻ mặt cũng chùng xuống. Cô quên là Lâm Nguyệt Y không có hứng thú với trai đẹp, đặc biệt là người kiêu ngạo, cộng thêm Lâm Nghuyệt Y với chia tay bạn trai. Nêm dù thông tin có hot đến đâu, nàng cũng không quan tâm.\r\n\r\n- \" ờ, cũng không có gì, chỉ muốn kể cho nghe thôi, tại nghe nói tổng giám đốc Cố Thị đẹp trai, và hơn nữa, bây giờ bệnh viện này, hắn đã thu mua lại rồi, tất là mình là nhân viên dưới cấp của hắn \".\r\n\r\nLâm Nguyệt Y nhìn sang Lưu Vy rồi quăng ra câu nói hờ hững.\r\n\r\n- \" bệnh viện ai thu mua tớ không quan tâm, tớ chỉ biết nhiệm vụ của tớ là vào đây làm việc và chữa trị cho bệnh nhân cần đến tớ, tới tháng tớ lãnh lương, vậy thôi. Còn chuyện còn lại, tớ không quan tâm \".\r\n\r\nNăm xưa Lâm Nguyệt Y vốn không muốn làm bác sĩ, chỉ vì ba mẹ nàng quá khắc khe.\r\n\r\nTừ lúc vào làm trong bệnh viện Hope, nàng chỉ hoàn thành tốt công việc của mình, chỉ đơn giản đây không phải là ước mơ của nàng, bây giờ đi làm, cũng chỉ vì muốn kiếm tiền sống qua ngày. Năm xưa trước đi ba mẹ nàng qua Pari, nàng đã tự học cách kiếm tiền và không dùng đến tiền của ba mẹ. Nàng và Lưu Vy góp tiền lại mua căn nhà nhỏ ở gần bệnh viện. Dần dần thời gian trôi qua, nàng cảm thấy như vậy cũng tốt, ban ngày đi là cùng cô bạn thân của mình, tan làm thì cùng nó đi la cà đâu đó, tối về thì ngủ. Mọi chuyện cứ như thế lập đi lập lại 3 năm và nàng đã trưởng thành.\r\n\r\n- -------\r\n\r\nBiệt thự Cố gia.\r\n\r\n- \" Boss, hai ngày nữa, tất là vào ngày 25/8 chúng ta sẽ có một cuộc họp ở Pari. Là cuộc họp về đối tác đầu tư trung tâm thương mại \". Phỉ Ngạn rõ cửa phòng rồi bước vào nói.\r\n\r\nMột giọng nói lạnh băng thốt lên.\r\n\r\n- \" được, tôi biết rồi \".\r\n\r\n- --------\r\n\r\nHai ngày sau, tại Pari.\r\n\r\n- \" bắt nó lại, lần này phải đem đầu nó về cho đại ca \". Một tên mặt vest đen lớn tiếng thét lên, một đám người tầm cỡ 8,9 người đuổi bắt Cố Thiên Vũ.\r\n\r\nCố Thiên Vũ lần này qua Pari họp, bàn về vấn đề mở trung tâm thương mại, nhưng khi trên đường về khách sạn, một đám người đuổi giết anh, do lần này vệ sĩ theo ít, với lại Phỉ Hạo không đi cùng anh, nên 36 kế, chạy là thượng sách.\r\n\r\nHôm nay, Lâm Nguyệt Y cũng đến Pari thăm ba mẹ, nhưng nàng không ngủ lại ở nhà ba mẹ mà về khách sạn.\r\n\r\n8h tối, ở Pari khung cảnh đẹp hữu tình dưới những ánh đèn led, nhìn trông xa xa là tháp Eiffel cao sừng sững hiêng ngang. Không khí ở Pari vào mùa thu thật mát mẻ, với những cây phong đậm màu sắc. Bỗng trời đổ cơn mưa rào, từ từ trở thành cơn mưa lớn.\r\n\r\nLâm Nguyệt Y đang đi trong con hẻm số 17 của Pari, thì vội chạy vì cơn mưa. Nhưng, nàng vấp ngã vì có một vật cảng ngang đường. Nhìn kỹ lại, là một cái chân, là máu.\r\n\r\nMột người đàn ông đang bị thương ở vai, ngồi dựa vào bước tường, do trời tối nên Lâm Nguyệt Y không nhìn rõ là người pháp hay là người nước khác. Là một bác sĩ, nàng không nghĩ nhiều nhe vậy, việc trước mắt phải làm là cứu người đàn ông này.\r\n\r\nLâm Nguyệt Y dìu người đàn ông này vào trú hiên trước nhà gần đó.\r\n\r\nCốc cốc.\r\n\r\n- \" Excuse me, is there anyone at home, someone was injured \". ( xin hỏi, có ai ở nhà không, có người bị thương).\r\n\r\nTrong nhà bước đi một đôi vợ chồng trung niên, họ thấy có người bị thương nêm để Lâm Nguyệt Y và người đàn ông đó vào nhà.\r\n\r\n- \" Please ask, is there a hospital recently? \". ( xin hỏi, gần đây có bệnh viện không?). Lâm Nguyệt Y hỏi.\r\n\r\n- \" recently no \". ( gần đây không có) vợ chồng ấy trả lời.\r\n\r\n- \" I\"ll be back, please watch over him\". ( tôi sẽ trở lại, xin hãy trông chừng anh ấy).\r\n\r\nNói xong Lâm Nguyệt Y mượn cây dù của vợ chồng trẻ rồi chạy một mạch ra trạm y tế, cũng may trên đường trở về khách sạn, nàng tình cờ thấy, nếu không chắc không biết cứu người đàn ông kia như thế nào.\r\n\r\nKhi trở lại, Lâm Nguyệt Y đã mua, nhíp, dao, cồn, băng gạt, bông gòn.\r\n\r\nNgười đàn ông ấy trúng đạn ở vai, cũng không sâu lắm, nhưng do dầm mưa lâu nên ngất, cũng nhờ đó mà thuận lợi để Lâm Nguyệt Y phẩu thuật lấy viên đạn ra.\r\n\r\nSáng hôm sau, trời đã tạnh. Lâm Nguyệt Y dìu người đàn ông đó lên taxi rồi chở đến bệnh viện.\r\n\r\nKhi người đàn ông đó tỉnh lại. Lâm Nguyệt Y mới thấy đỡ lo hơn. Nàng cũng đã biết người đàn ông này là người trung quốc, khi lấy giấy căn cước của anh ta làm thủ tục nhập viện.\r\n\r\nDưới ánh đèn của bệnh viện, khuôn mặt người đàn ông hiện lên một cách hoàn mỹ, đôi mắt sâu thẳm lạnh lùng, sóng mũi cao vút, đôi chân mày rậm, tất cả như một kiệt tác thần linh ban xuống, vẻ đẹp lạnh lùng.\r\n\r\n- \" chào anh, anh tỉnh rồi. Anh còn nhớ anh là ai, vì sao bị thương hay không? \".\r\n\r\nCảm giác đau từ bả bạ xuống cánh tay, lộ ra trên mặt người đàn ông này.\r\n\r\n- \" tôi tên Cố Thiên Vũ\".\r\n\r\nChỉ giới thiệu tên, không nói gì thêm, mãi đến một hồi, anh mới hỏi.\r\n\r\n- \" cô đã cứu tôi? \".\r\n\r\n- \" ừ, tối qua anh ngất ở con hẻm số 17 \".\r\n\r\n- \" cô đưa tôi vào bệnh viện? \". Một sự ngạt nhiên hiện lên gương mặt Cố Vũ Thiên.\r\n\r\n- \" tôi đâu nào mạnh như vậy. Hôm qua tôi dìu anh vào nhà gần đó, họ cho tôi và anh ở nhờ. Tôi đi mua dụng cụ y tế, rồi lấy đạn ra cho anh, sáng hôm nay mới đưa anh vào bệnh viện \".\r\n\r\n- \" cô là bác sĩ? \".\r\n\r\n- \" ừ, tôi là bác sĩ, xem như anh may mắn gặp tôi đấy \". Lâm Nguyệt Y mỉm cười.\r\n\r\nNụ cười nhẹ nhàng mà làm cho tâm tình của Cố Thiên Vũ có xíu xao động. Cô gái này đã cứu anh một mạng. Còn tốt bụng đưa anh vào bệnh viện.\r\n\r\nTrước giờ Cố Thiên Vũ cho rằng phụ nữ là sự phiền phức, cho nên anh chẳng bao giờ phải lòng một người phụ nữ nào cả, nhưng,,,, nhưng hôm nay, người con gái này đã cứu anh cho dù chưa biết gì về anh cả. Một người con gái vừa đẹp vừa tốt bụng. Có lẽ đây là duyên phận của Lâm Nguyệt Y và Cố Thiên Vũ', 2, 'boss-a-tha-cho-em.jpg', 13, '2020-08-03 11:00:30', '1'),
(7, 'Hồ Nữ', 'Nữ chính hơn người ở điểm nào? Như vậy thì nam thần sao có thể để ngươi nhúng chàm? \r\n\r\nBiện pháp bảo vệ duy nhất chính là chiếm lấy nam thần làm của riêng.\r\n\r\nTiên quân sờ lỗ tai của tiểu hồ ly hỏi: \"Tuyết Hoa, ngươi thích hồ ly đực như thế nào?\"\r\n\r\nTiểu', '\"Ngao ~~ ngao ô ~~\" Tiểu hồ ly ngửa đầu kêu rên, hướng về phía Hắc Lang phun ra một khối cầu lửa rồi nhanh chóng quay người chạy trốn vào rừng cây rậm rạp.\r\n\r\nThân thể Hắc Lang nhanh chóng tung ra một tầng bảo hộ màu xanh, đoàn cầu lửa vừa va chạm đã bốc hơi hết.\r\n\r\nHắc Lang hướng về phía tiểu hồ ly vừa chạy trốn nhe răng gầm gừ rồi bắt đầu chạy đón đầu. Mũi sói nhạy như thế, không đời nào để con mồi chạy trốn.\r\n\r\n“ Hai tiểu yêu tinh ở trong rừng cây một con đuổi, một con trốn, tiểu linh hồ liên tiếp thoát khỏi Hắc Lang trong gang tấc mà Hắc Lang cũng không vội như cố ý nhường. Rõ ràng là Hắc Lang đang chơi mèo đuổi chuột. Sói tinh săn mồi mạnh mẽ hung mãnh, Tiểu Bạch Hồ khó thoát khỏi cái chết. \" Một đạo trưởng có vẻ thưởng thức con sói tinh.\r\n\r\n\"Vạn vật trong trời đất kẻ yếu tất nhiên sẽ trở thành mồi cho kẻ mạnh. \" Một thanh âm khác, ngữ khí vân đạm phong khinh.\r\n\r\nCó người, có người nha! Cứu mạng nha, cứu cứu ta!\r\n\r\nKhông muốn trở thành đồ ăn của Hắc Lang tiểu hồ ly không cần suy nghĩ chạy như điên tới vị trí có thanh âm. Đi theo sau lưng nó Hắc Lang cũng nghe được tiếng nói chuyện, tốc độ dần chậm lại.\r\n\r\nCứu người, cứu người nha! À không là cứu hồ nha!\r\n\r\n<!--Ambient video inpage desktop-->\r\n\r\nĐột nhiên, nó bị một luồng sức mạnh nhẹ nhàng cản trở.\r\n\r\n\"Do hoảng hốt chạy bừa,.... Hay là linh hồ thông minh biết chạy đi cầu cứu?\" Một giọng nói thanh lãnh truyền vào tai nó.\r\n\r\n\"Oa ngô, oa ô ~~\" Ta rất đáng yêu, rất nghe lời, có thể làm sủng vật cho nên cứu ta với.\r\n\r\nSau khi bị Hắc Lang truy đuổi tới đường cùng, tiểu hồ ly không còn chút cốt khí, quay ra học chó vẫy đuôi nịnh bợ.\r\n\r\n\"Sư phụ, tiểu hồ ly này nhìn đặc biệt có linh tính. \" Một nam tử khác kinh ngạc nói, \"Nó nhỏ như vậy, xem ra vẫn là ấu hồ. \"\r\n\r\nMột bàn tay nhẹ nhàng túm lông gáy của nó, sau đó nó thấy một khuôn mặt có vẻ lớn nhưng thực tuấn mỹ.\r\n\r\n\"Đây là một hồ ly tạp chủng lông trắng, chỉ là một linh hồ mới tu luyện một thời gian. Thanh nhi, nếu như ngươi để ý nó có thể đem thu làm linh sủng. \"\r\n\r\n\"Oa ô, oa ngô ~~\" Tiểu hồ ly hướng về phía mỹ nam cố gắng nháy mắt, dùng một chi chỉ vào hắn. Tuy người này khiến nó cảm giác thanh lãnh như ánh trăng trên trời, lại không muốn nó nhưng trong thế giới tu tiên này, phần lớn sư phụ đều sẽ nhất định tài giỏi hơn đồ đệ.\r\n\r\n\"Ồ? Nó hiểu được chúng ta nói chuyện, còn biết chọn chủ?\" Nam tử tuấn mĩ khí chất thanh lãnh thoáng nhướn mày.\r\n\r\n\"Sư phụ, con linh hồ này nhìn thế nào cũng không lợi hại, đồ nhi không cần. Đương nhiên, nó càng không xứng với ngài. \" Đồ đệ nói, \"Nhỏ như vậy, đan điền không có Tinh Nguyên, trên người cũng không có mấy lượng thịt, sư phụ vẫn là ném xuống đi. \"\r\n\r\nHồ ly tuy rất có linh tính nhưng hắn chỉ muốn Hắc lang hung mãnh. Hắc lang kia chắc đã trốn, xem ra cảm giác trước nguy cơ cũng rất mạnh, hắn sẽ chậm rãi tìm khắp cánh rừng này, đem nó tìm ra, phục tùng nó, đem nó thu làm linh sủng!\r\n\r\nNam tử thanh lãnh khẽ gật đầu, khom lưng đem tiểu hồ ly thả xuống.\r\n\r\nThật hiếm khi mới tìm được người không có ý xấu, lại có giá trị cao. Nhất định phải bám chặt\r\n\r\nTiểu hồ ly lập tức nhảy lên giày theo ống quần leo lên trên, hai chân trước nắm chặt ống quần, miệng không ngừng oa ô gọi.\r\n\r\n\"Sư phụ, đồ nhi chưa thấy linh hồ hoang dại nào quấn người đến thế. Thú vị thật. \" Đồ đệ đột nhiên bật cười.\r\n\r\nSư phụ cũng hứng thú, nhấc tiểu hồ ly lên, dò hỏi, \"Ngươi muốn đi theo ta?\"\r\n\r\n\"Ngao ô. \" Vâng. Tiểu hồ ly vội vàng gật đầu. Ta biết làm nũng, có thể giải buồn, biết ấm giường, cầu bao nuôi...\r\n\r\n\"Nếu ta dùng máu thịt của ngươi để chế tác đan dược?\" Hắn đe dọa.\r\n\r\nTiểu hồ ly nhất thời run rẩy một chút, sau đó cố gắng vẫy đuôi, lè đầu lưỡi liếm láp ngón tay hắn, sau đó thử nói: \"Ngao uông, ô a uông, uông uông. Miêu, miêu ô ~~\"Tiểu hồ ly bắt chước vẹt gọi tiếng động vật khác.\r\n\r\n\"Ha ha ha, sư phụ, tiểu linh hồ đang làm nũng với người, cầu người thu nó đó. \" Đồ đệ nhìn được cười ha hả, cũng ngồi xuống nhìn tiểu linh hồ.\r\n\r\nSư phụ đưa tay vuốt ve đầu tiểu linh hồ. Tiểu linh hồ cảm nhận được một dòng khí ấm áp chảy qua thân thể mình.\r\n\r\n\"Tu vi của nó đã vượt qua nhị phẩm thủy hỏa linh căn. \" Sư phụ nói, \"Thủy khắc hỏa, nó tu đến mức này mà hai luồng linh lực còn chưa có xung khắc, quả là hiếm có. Tương lai, nó có thể trở thành linh thú biến dị cao cấp. \"\r\n\r\nThì ra chính chủ chết do linh lực thủy hỏa tương khắc, mà ta vô tình nhập linh hồn vào trong thân thể tiểu hồ ly. May mắn là ta biết dùng phương pháp luyện Thái Cực nội công, đem hai linh lực tương khắc này tạm thời bình ổn.\r\n\r\nTiểu hồ ly ngửa đầu, thật biết điều cọ vào tay hắn. Bàn tay hắn vừa lớn vừa ấm áp, cọ rất thoải mái.\r\n\r\n\"Thật là một Linh thú biết điều lại khôn khéo. Thôi, ta thu ngươi. \" Sư phụ thản nhiên nói, cắn nát ngón tay đặt lên đầu tiểu hồ ly, miệng lẩm bẩm.\r\n\r\nBởi vì môi hắn chỉ mấp máy nên tiểu hồ ly căn bản không nghe thấy gì.\r\n\r\n\"Ồ?\" Sư phụ hơi hơi nghi ngờ.\r\n\r\n\"Sư phụ, làm sao vậy?\" Đồ đệ của hắn lập tức hỏi.\r\n\r\n\" Ký kết khế ước linh sủng thất bại. \" Hắn nói. Chỉ những linh thú thích tu sĩ mới có khả năng cùng tu sĩ ký kết khế ước linh sủng, cùng nhau tu luyện. Nhưng tiểu linh hồ này... Khó có khi hắn có hứng thú thu linh sủng nhưng lại thất bại.\r\n\r\n\" Sao có thể?\" Đồ đệ của hắn nhất thời kinh ngạc.\r\n\r\nSư phụ của hắn là thất phẩm tiên quân, làm sao có chuyện không có cách đánh dấu linh sủng? Sư phụ chưa có linh sủng, mà tiểu linh hồ lại chủ động quấn lên hắn, tất nhiên là thích sư phụ.\r\n\r\nLinh thú biến thành linh sủng có thể cùng chủ nhân tâm linh tương thông, được chủ nhân trợ giúp tu luyện. Những linh thú có chút linh trí cao, tập tính tương đối gần người thường chủ động tìm kiếm chủ nhân, trở thành linh sủng đạt được các loại linh đan để tu luyện, nói tiếng người, cuối cùng rút đi da lông biến thành hình người.\r\n\r\nSư phụ trầm tư một hồi, thử dò xét nói: \"Tiểu linh hồ, ngươi nguyện ý đi theo ta sao?\".\r\n\r\nTiểu hồ ly lập tức gật đầu như giã tỏi. Nó, không, nàng nhập hồn vào hồ ly mới có hai tháng, mỗi ngày trải qua đều run như cầy sấy, sợ mình thành đồ ăn của động vât khác. Nó bây giờ khả ái mê người, vô cùng thích hợp làm sủng vật, nếu được ban linh đan diệu dược, có thể sớm biến hình người. Chờ nàng trở nên lợi hại, biết đâu có thể tìm được biện pháp trở về thân thể cũ.\r\n\r\nHồ ly là loài động vật ăn thịt, nàng ăn nhiều trái cây nên bụng không thoải mái, chỉ có thể bắt gà hoang thỏ hoang, dùng pháp thuật hệ hỏa nướng gà hoang thỏ hoang cùng luộc trứng gà. Có điều, nàng chịu đủ truy đuổi cùng đánh nhau, vừa sống vừa phòng bị, chỉ có thể tìm một chủ nhân cao cường làm chỗ dựa. Sư phụ không biết tiểu linh hồ đang suy nghĩ cái gì. Hắn đem nó dựng dậy tách mở hai cái chân sau kiểm tra một chút, nói: \"Là hồ ly cái. \"\r\n\r\n\"Ngao ô --\" Ngay khi phát hiện tiểu hồ ly đột nhiên thét chói tai, mạnh xoay người kẹp chặt hai chân, dùng lớp lông mềm như nhung xoã tung cái đuôi che khuất thân thể của mình. Bị nam nhân nhìn thấy... Xấu hổ, xấu hổ...\r\n\r\nSư phụ cùng đồ đệ nhất thời cảm giác khó có thể tin tưởng, động vật cũng biết nam nữ khác biệt?\r\n\r\n\"Sư phụ, đồ nhi cảm thấy linh hồ này rất cổ quái, có phải là phân thân của nữ tiên quân nào đó không?\"\r\n\r\nNgũ Nguyên đại lục Tu Tiên giới đem tu sĩ chia làm tam giai cửu phẩm. Tam giai: đạo, thật, tiên. Một tới tam phẩm là đạo, tứ tới lục phẩm là thật, thất tới cửu phẩm là tiên. Tiên giai tu sĩ có thể đem linh lực trong cơ thể chuyển hóa thành thực thể, đi tới nơi mình không thể tới.\r\n\r\n\"Không thể, tiên giai tu sĩ sử dụng phân thân thuật, nhiều điểm ở phân thân không bằng bản thể. Phân thân cùng bản thể có quan hệ đặc thù: tâm linh tương thông, phân thân chịu sự chỉ huy hoàn toàn của bản thể; tùy thời có thể đem phân thân thu hồi, cũng có thể đem tự thân linh lực cùng ý thức chuyển dời đến trên người phân thân, giúp phân thân xử lý nguy cơ; bản thể bị hao tổn, phân thân nhất định biến mất, phân thân bị thương, thực lực của bản thể tổn thương nghiêm trọng có thể mất mạng ngay lập tức Sư phụ nhân cơ hội chỉ bảo đồ đệ, \"Ngươi vừa rồi cũng cảm giác được, tiểu linh hồ bị Đại Hắc lang truy đuổi, nghe được chúng ta nói chuyện liền liều lĩnh chạy tới cầu viện. \" Nhân loại tu sĩ đi săn linh thú, dùng tinh nguyên cùng huyết nhục của linh thú luyện chế đan dược, hỗ trợ tu luyện của chính mình nhưng tiểu linh hồ lại nhưng chủ động hướng người cầu cứu, rõ ràng là đặt cược mệnh!\r\n\r\nSư phụ chỉ bảo xong đồ đệ, quay lại nhìn tiểu linh hồ còn xấu hổ trầm ngâm thật lâu sau nói: \"Ngươi có một bộ lông trắng như tuyết, vậy gọi là Tuyết Hoa đi. \"\r\n\r\nTuyết Hoa, Tuyết Hoa, tên của ta cũng vốn là Lâm Tuyết Hoa.\r\n\r\nTiểu hồ ly vui mừng gọi.\r\n\r\n\"Ngươi thích?\" Sư phụ mỉm cười nhìn tiểu sủng vật.\r\n\r\n\"Oa ô ~~\" Vâng, ta thích\r\n\r\n\"Thanh nhi, chúng ta tiếp tục tìm xem có linh thú lợi hại không\" Sư phụ nói. Thanh nhi rất muốn thu một linh sủng, vừa lúc hắn tịch mịch lâu, liền cùng đồ đệ tìm kiếm.\r\n\r\n\"Sư phụ, đồ nhi chỉ muốn Hắc lang kia. Con định gọi nó là Tiểu Hắc. \" Đồ đệ hưng phấn nói.\r\n\r\n\" Hắc lang kia dã tính quá cao, ngươi giết nó thì dễ nhưng nó muốn phục tùng cùng hảo cảm thì không đơn giản. \" Sư phụ nhắc nhở.\r\n\r\nTu sĩ khi thuần phục linh thú thì cần kiên nhẫn mà sau khi ký kết khế ước, mỗi lần tu luyện đạt được linh lực đều có một chút thông qua khế ước chuyển đến cơ thể linh thú. Đây là lý do 800 năm qua hắn chưa nghĩ tới việc thu linh sủng.\r\n\r\n\"Sư phụ, đồ nhi có lòng tin sẽ thu được nó. \" Đồ đệ tự tin nói\r\n\r\nSư phụ ôm tiểu linh hồ Tuyết Hoa, hơi hơi ngửa đầu cảm ứng một chút, nói: \"Ngươi đi đi. Hắc lang kia đang ở ba dặm hướng Đông Bắc. Ta mang Tuyết Hoa đến phía nam nghỉ tạm. \"\r\n\r\n\"Vâng, sư phụ. \" Đồ đệ lập tức chắp quyền, sau đó lui ra phía sau vài bước quay người, ngự kiếm phi hành về hướng Đông Bắc.', 1, 'ho-nu.jpg', 14, '2020-08-06 11:00:30', '1'),
(8, 'Chiêu Diêu', 'Ngày còn sống Lộ Chiêu Diêu nàng làm cái gì cũng cực kỳ phô trương, chỉ riêng cái chết là đặc biệt bình thường.', 'Ngày ta chết lại đúng vào ngày xuất hiện của ma khí thượng cổ - Kiếm Vạn Quân.\r\n\r\nTừ lâu trên giang hồ đã có truyền thuyết, nói rằng người nào có được Kiếm Vạn Quân thì sẽ giành được địa vị tối cao của Ma giới, là Ma vương ngồi trên đỉnh cao nhất.\r\n\r\nTa đã làm “ma” cả đời, những thành tựu cần có đều đã đạt được, chỉ còn thiếu mỗi bước này nữa mà thôi. Ta mong đợi có thể đoạt được ngôi vị Ma tôn, từ nay về sau thống nhất Ma giới đã phân chia ngàn năm, quyền khuynh thiên hạ, ra lệnh muôn dân, không ai dám không tuân theo!\r\n\r\nDo đó, tại lúc Kiếm Vạn Quân sắp xuất thế, ta dẫn theo môn đồ trong Vạn Lục môn của ta tìm đến Kiếm mộ ngàn năm. Ở chỗ đó đã sớm có người trong Ma đạo tàn sát lẫn nhau, tất nhiên ta chẳng thèm nhìn lấy một cái, hạ lệnh cho môn đồ giúp ta cản phía sau, còn ta đi một mình vào Kiếm mộ.\r\n\r\nBây giờ ngồi nhớ lại, ngay tại thời điểm đó ta đã phạm phải hai sai lầm liên tiếp. Một là không lưu ý đến khí tức hỗn loạn bị che dấu dưới tiên khí bên trong Kiếm mộ, hai là không để tâm đến tên quái dị đi theo sau lưng ta vào trong đó.\r\n\r\nKỳ thật tên quái dị đó cũng có tên, là ta đặt cho hắn – Mặc Thanh.\r\n\r\nBởi vì tại thời điểm lần đầu tiên ta gặp hắn, toàn thân hắn đều bị thương, chỗ xanh chỗ tím. Trên mặt còn có những vết sẹo đen kịt, cái này nối cái kia, trông giống như một bức phú chú thần bí, khiến cho khuôn mặt hắn đặc biệt xấu xí, đáng sợ…\r\n\r\nNhưng ta tu ma (*), chưa bao giờ từng sợ những thứ này.\r\n\r\n(*) tương tự như “tu tiên.”\r\n\r\nLúc ấy, hắn ôm chặt lấy thi thể lạnh buốt của mẫu thân trong lồng ngực, trước mặt hắn là nhóm Gia chủ của Thập Đại Thế Gia tu tiên, bọn họ nói hắn là con trai của Ma vương.\r\n\r\nTa lại không cho là đúng.\r\n\r\nMa vương mà Ma giới chúng ta công nhận đã chết từ tám trăm năm trước. Sau khi lão Ma vương chết, cả Ma đạo chia năm xẻ bảy, mỗi người chiếm giữ một phần, không có cái nào là chính thống. Nhưng những kẻ chính đạo này, cứ bắt được một thủ hạ của Ma giới thì đều hô rằng đó là thân nhân của Ma vương; nói như bọn họ thì họ hàng của Ma vương đã có hàng ngàn hàng vạn người trong thiên hạ rồi.\r\n\r\nHơn nữa quá đáng nhất chính là…\r\n\r\nNếu như theo cách tính của bọn họ, cho dù có hàng vạn tên ma đầu đứng xếp hàng thì cũng chẳng tới lượt ta, bởi vì lúc ấy trong tay ta chẳng có người nào có thể đem ra sử dụng cả.\r\n\r\nTa không phục cho lắm, vì vậy dự định giáo huấn bọn họ một chút. Để cho bọn họ biết rõ, tuy ta không có một thủ hạ nào cũng có thể chiến đấu rất lợi hại.\r\n\r\nCho nên năm đó, ta đây đứng chắn trước mặt Mặc Thanh, trào phúng mấy kẻ chính đạo một phen, vài trăm người xúm hết vào bắt nạt người ta chỉ là cô nhi quả mẫu, sau đó còn nhân tiện cùng Thập Đại Thế Gia đấu đá một hồi.\r\n\r\nHậu nhân truyền lại, trận đấu pháp kia làm trời đấ mù mịt, sông khô nước cạn. Tuy thực tế không có khoa trương như bọn họ nói, nhưng đây đúng là trận chiến giúp ta lập danh trong Ma đạo.Ta một thân máu me cứu Mặc Thanh ra, sau đó thanh danh truyền ra bên ngoài, tất cả mọi người đều biết núi Trần Tắc có một nữ ma đầu có thể một mình đấu lại Thập Đại Thế Gia. Số người xin gia nhập vào môn phái của ta nườm nượp không dứt.\r\n\r\nTa xây xong Vạn Lục môn, thu hơn một ngàn môn đồ. Mà Mặc Thanh được ta cứu ra từ trong trận chiến ấy, bởi vì công việc bận rộn, ta cũng chẳng rảnh để quan tâm đến hắn, chỉ cấp cho hắn một người sư phụ. Sư phụ hắn nói hắn không có thiên phú tu ma, vì vậy liền đuổi hắn đi trông cửa sơn môn.\r\n\r\nSau đó, ta rất ít khi nghe được tin tức của hắn, mãi cho đến lúc chết ta mới đặt hắn vào mắt một lần nữa. Khi đó hắn đã trưởng thành, hoa văn quái dị trên mặt vẫn đáng sợ như cũ….\r\n\r\nNgày ấy trong Kiếm mộ, người của tiên môn đã sớm ở chỗ này bày ra sát trận, mưu đồ diệt trừ các môn phái ma đầu nổi danh. Chỉ có điều, bọn họ không ngờ Vạn Lục môn của ta lại có thực lực lợi hại như vậy, chỉ bằng môn đồ của Vạn Lục môn đã ngăn tất cả người của Ma đạo ở bên ngoài, có duy nhất một mình ta là vào được Kiếm mộ.\r\n\r\nTrước khi Kiếm Vạn Quân xuất thế, ta chuyên tâm áp chế kiếm khí cuồn cuộn trong Kiếm mộ, lệ khí và oán khí lắng đọng của hàng trăm năm trước lúc từng dùng người sống để hiến tế. Những người của tiên môn ẩn núp đã lâu, bây giờ đột nhiên động thủ.\r\n\r\nTa vốn không để những người này vào mắt, nhưng lại không lường trước được khí tức trong Kiếm mộ lại lợi hại như vậy.\r\n\r\nTa khiến cho sát trận của tiên môn vỡ nát, mà không đề phòng được sát khi trong Kiếm mộ. Ta bị đâm khá nặng, liều mạng dùng hết chỗ khí lực cuối cùng mạnh mẽ áp chế khi tức Kiếm mộ, trốn nhanh vào trong góc. Chỉ cần đợi Kiếm Vạn Quân xuất thế, ta chớp lấy cơ hội đoạt nó về là có thể xưng vương thiên hạ rồi.\r\n\r\nNhưng mà những đệ tử tiên môn bị đánh nát sát trận kia kiên quyết không chịu rời đi, bọn họ ở lại Kiếm mộ tìm kiếm tung tích của ta. Giờ khắc này, ta không còn khí tức để tiếp tục đánh nhau với bọn họ nữa. Vết thương do sát khí Kiếm mộ tạo ra trên cổ ta sâu đủ thấy xương, gần như chém đứt cả cổ, khiến cho ta muốn nói cũng cực kỳ khó khăn.\r\n\r\nTa dè dặt thận trọng núp phía sau tảng đá, đột nhiên lúc ấy, ta cảm thấy trên cổ nóng lên, toàn thân xiết chặt, đang muốn phản kháng lại bị người khác bịt miệng. Ta ngẩng đầu lên nhìn, một khuôn mặt sẹo đen trải dài, đúng là Mặc Thanh.\r\n\r\nHắn thấy ta đã nhận ra hắn, lúc này mới thả ta ra. Hắn giữ chặt lấy cổ ta, giúp ta cầm máu. Ta nhìn hắn, ánh mắt hắn không che dấu được vẻ lo lắng, ta liền đảo mắt:\r\n\r\n“Mặc Thanh.” Ta hô tên của hắn, giọng nói khàn khàn, “Có phải ngươi thích ta hay không?”\r\n\r\nVâng, quả thật ta hỏi vấn đề này có hơi đột ngột, nhưng sự ân cần trong mắt hắn đâu phải là thứ mà một môn nhân bình thường có thể lộ ra. Càng nghĩ ta lại càng hướng đến khả năng này.\r\n\r\nQuả nhiên, sau khi ta hỏi hắn, hắn liến nhìn ta, trầm mặc không nói gì, chỉ là bàn tay thả lỏng bên người lặng lẽ nắm chặt lại. Ta đột nhiên cười, mặt hắn đen kịt. Một kẻ không có thiên phú tu ma, lặng lẽ đi theo ta tiến vào bên trong Kiếm mộ, nhất định là nhằm vào thời khắc mấu chốt sẽ giúp đỡ ta, khiến cho ta nhớ kỹ hắn. Bởi vì ngoài biện pháp này ra, hắn không thể nào xuất hiện ở trước mắt ta được.Ta nhìn hắn, hòa ái thân thiết mỉm cười: “Mặc Thanh, ngươi đã thích ta thì chắc hẳn sẽ không để ta chết ở chỗ này, đúng hay không?”\r\n\r\nHắn trầm mặc nhìn lại ta, sau đó rũ mắt xuống, nhìn thẳng vào chiếc gương bạc nhỏ treo trên cổ ta, trong gương phản chiếu khuôn mặt trải đầy vết sẹo của hắn. Ta không biết hắn đang suy nghĩ cái gì, tạm thời cho rằng hắn muốn ta tặng cho hắn, vì vậy ta làm bộ muốn lấy gương bạc xuống: “Cái gương bạc này ta cho ngươi làm tín vật, nếu hôm nay ngươi có thể bảo vệ ta bình yên rời khỏi chô này thì tương lai ta sẽ bảo vệ ngươi trước cả Ma giới.”\r\n\r\nCái gương bạc nhỏ này từ đâu mà có ta đã quên mất rồi, cũng không biết nó có tác dụng gì, bình thường cảm thấy mang theo khá đẹp mắt cho nên mới đeo vào, vì vậy bây giờ cho đi tuyệt nhiên không do dự.\r\n\r\nNhưng Mặc Thanh lại trầm mặc đè lại tay của ta: “Ngươi không cần cho ta cái gì.” Khác với khuôn mặt quái dị đáng sợ, giọng nói của hắn lại cực kỳ dễ nghe, “Ngươi cứ giữ lấy đi.” Hắn nói, “Giữ nó thật tốt là được.”\r\n\r\nCó thể không phải dùng bất kỳ vật gì để đổi lấy việc người khác bán mạng cho ta, tất nhiên là ta đồng ý. Vì vậy ta buông gương bạc nhỏ ra, nhìn hắn, cố gắng ôn nhu mỉm cười: “Ngươi giúp ta thu hút sự chú ý để kéo những đệ tử tiên môn kia rời đi, có được không?”\r\n\r\nHắn đột nhiên giơ tay lên, nhẹ nhàng sờ gò má ta, đầu ngón tay đặt lên má lúm đồng tiền. Hiện tại ta còn cần hắn cứu mạng, đương nhiên không còn cách nào khác là tùy ý để hắn sờ thế nào thì sờ.\r\n\r\n“Môn chủ.” Hắn gọi ta như vậy, không khác gì những đệ tử bình thường khác gọi ta, nhưng bởi vì giờ phút này đầu ngón tay của hắn đang chạm vào mặt ta, cho nên có khác biệt rất lớn, “Ta có thể vì ngươi mà vứt bỏ mọi thứ, chỉ cần ngươi bình an.”\r\n\r\nỪ, lúc này mã vẫn có thể biểu đạt được sự trung thành, đúng là một đệ tử giỏi ăn nói.\r\n\r\nChỉ có điều, nghe những lời hắn nói, ta chẳng hề xúc động. Loại chuyện này ta đã thấy khá nhiều, rất nhiều người đã từng nói: vì ngươi, ta có thể vứt bỏ tất cả, không phải bởi hắn là một người vĩ đại, mà là vì vốn dĩ hắn chỉ có hai bàn tay trắng.\r\n\r\nTrong lòng ta nghĩ như vậy, lại cảm nhận được ngón tay đặt trên mặt ta của Mặc Thanh cứng lại, khoảnh khắc đó, ta cho rằng hắn đã nhìn thấu được ý nghĩ của ta. Trong nội tâm ta thấy hơi sợ, nhưng sau một lát, Mặc Thanh rút kiếm đi ra ngoài.\r\n\r\nKhông nhìn vào mặt hắn, ta chỉ cảm thấy bóng lưng cao ngất của người thanh niên này khiến cho người ta phải đui mù.\r\n\r\nKhi đó, ta đã nghĩ nhất định ta được cứu rồi, chờ Mặc Thanh dẫn dụ bọn người kia đi, ta lấy Kiếm Vạn Quân rồi lặng lẽ mà trốn, đến một nơi không có ai, chữa trị vết thương cho tốt. Sau đó quay lại Vạn Lục môn, thống nhất thiên hạ. Tới lúc đó, nếu Mặc Thanh còn sống, ta sẽ bảo bọc hắn, còn nếu đã chết… ta đây sẽ lập cho hắn một cái bia mộ thật đẹp.\r\n\r\nTa suy nghĩ rất chu toàn, nhưng thời điểm ghé mắt nhìn qua kẽ hở xem tình hình ở bên ngoài ra sao, lại trông thấy tên tiểu tử Mặc Thanh kia rõ ràng một bên cùng người của tiên môn chiến đấu, một bên lùi dần về hướng của Kiếm mộ.\r\n\r\nSát khí bên trong Kiếm mộ đã bị ta đè xuống, ở giữa có một ánh hào quang đang muốn chui từ dưới đất lên.\r\n\r\nLà Kiếm Vạn Quân.\r\n\r\nĐầu óc ta quýnh lên, chỉ thấy Mặc Thanh có tu vi vốn không cao đã bị người của tiên môn chém đến máu tươi đầm đìa. Hắn đứng ở phía trên Kiếm mộ, máu tươi chảy vào đó, nhập vào tia sáng kia.\r\n\r\nĐúng lúc đó, trong tiên môn có người dùng kiếm chặt đứt gân chân của hắn, Mặc Thah ngã sấp xuống Kiếm mộ, hắn vươn tay ra, hoàn toàn cầm lấy chuôi của Kiếm Vạn Quân vừa từ lòng đất chui lên.\r\n\r\nKiếm Vạn Quân nhận chủ!\r\n\r\nMặc dù tâm trạng của ta gấp gáp, nhưng vẫn tin chắc rằng kẻ có một chút xíu tu vi như Mặc Thanh tuyệt đối không nhổ được thanh kiếm lên. Ai mà ngờ, máu của hắn lại theo chuôi kiếm chậm rãi thấm xuống dưới, đột nhiên Kiếm mộ phát ra khí tức mãnh liệt, các loại khí tức thi nhau phun ra, phá tan sự cầm cố ta vừa lập, trong nhất thời bắn chết vô số đệ tử tiên môn có trong Kiếm mộ!.. Đợi đến khi Mặc Thanh hô to một tiếng, đem kiếm rút ra hoàn toàn, khí tức của Kiếm mộ cũng nổ tung, gột rửa ngàn dặm, quét ngang tam giới, mà kẻ không phải là chủ của Kiếm Vạn Quân còn đang bị trọng thương là ta đây, ở chính giữa chấn động của luồng khí tức khổng lồ…\r\n\r\nCứ như vậy bị đánh tới chết…\r\n\r\nTrước khi chết, ta thấy những vết sẹo đen chằng chịt trên mặt Mặc Thanh theo ánh sáng lưu động của thanh kiếm chậm rãi biến mất.\r\n\r\nLúc này ta mới ý thức được, hóa ra những vết sẹo đen trên mặt hắn không phải là phù chú, mà là phong ấn, phong ấn của con trai Ma vương.\r\n\r\nTa cũng ý thức được, hóa ra năm đó Thập Đại Thế Gia nói hắn là con trai của Ma vương lại là thật chứ không lừa ta…\r\n\r\nTa càng đột nhiên ý thức được, tên Mặc Thanh này, đi theo ta tiến vào Kiếm mộ, có lẽ ngay từ đầu hắn đã lên kế hoạch rất kỹ càng. Không phải vì hắn muốn bảo vệ ta, không phải muốn khiến ta chú ý, lại càng không phải vì hắn thích ta, mà hắn chỉ muốn đoạt lại thứ thuộc về phụ thân hắn. Bởi vì Kiếm Vạn Quân bị phong ấn, sức lực hắn không đủ, cho nên mới chờ sau khi ta xử lý hoàn tất mọi chuyện mới cầm kiếm lên chém vài tên tiên nhân, cuối cùng lấy được Kiếm Vạn Quân, dùng máu của mình làm cho kiếm nhận chủ…\r\n\r\nTiểu tử này! Mưu kế cũng thật hay!\r\n\r\nChỉ tiếc cho cuộc đời của ta! Ra sức liều mạng, lăn qua lăn lại, rổ cuộc lại làm mai mối cho người khác! Ta hận muốn hộc máu, thật sự là không cam lòng mà!\r\n\r\nNhưng dù có cam lòng hay không thì ta cũng đã chết.\r\n\r\nChết không một chút hoa lệ, cũng không một chút oanh liệt, thậm chí còn chả giải thích nổi…\r\n\r\nKhi ta cảm nhận được thế giới này còn tồn tại, là vào một đêm mưa lớn, ta ngồi ở trên ngôi mộ mới, tùy ý để hạt mưa xuyên qua hồn thể, rơi lộp độp xuống tấm bia và mộ phần.\r\n\r\nTa đi vòng qua nhìn bia mộ của ta, trên đó không ghi một chữ nào cả, ta tức giận đến mức muốn đạp vỡ cái bia này. Hận không thể tự mình cầm cái dùi trực tiếp tạc vào đó mấy chữ thật to “Ma Vương Chí Thượng Chí Tôn Uy Vũ Vô Địch Thiên Thượng Thiên Hạ - Lộ Chiêu Diêu”.\r\n\r\nBia còn không viết xong, có để cho người ta an tâm chết hay không!\r\n\r\n', 1, 'chieu-dieu.jpg', 15, '2020-08-27 11:04:03', '1');
INSERT INTO `story` (`story_id`, `name`, `preview_text`, `detail`, `cat_id`, `picture`, `counter`, `create_at`, `status`) VALUES
(9, 'Vu Thần Kỷ', 'Khi những thứ ở quá khứ trở thành truyền thuyết\r\n\r\nKhi truyền thuyết trở thành những câu chuyện thần thoại\r\n\r\nKhi những câu chuyện thần thoại dần bị lãng quên\r\n\r\nKhi tất cả bị cát bụi thời gian lướt qua xóa nhòa...', 'Bầu trời màu xám, ráng hồng quay cuồng.\r\n\r\nBên cạnh tầng tầng lớp lớp vân phiến, có lôi quang lóe ra. Gợn sét gợn sóng nhanh chóng cuồn cuộn qua bầu trời, nhưng không có nửa điểm tiếng động nào cả.\r\n\r\nTrên đỉnh trụ trời, đại thần Cường Lương cầm cái rìu đá, chân đạp đầu độc mãng vừa mới chém giết, ngạc nhiên nhìn bầu trời. Từ cực đông đến cực tây bầu trời, hầu như che phủ toàn bộ không trung là một cái đuôi rắn màu tím, đang ở phía chân trời cực cao uốn lượn trườn đi.\r\n\r\nThân rắn này khổng lồ như thế, không thấy đầu đuôi của nó, nhưng đã che toàn bộ bầu trời tầm mắt có thể đạt được.\r\n\r\nThân rắn che cả bầu trời thỉnh thoảng nhập vào trong mây, lẫn làm một thể với ráng hồng. Ngẫu nhiên một trận lôi quang va chạm ở trên thân rắn, liền có một đạo khí tức đáng sợ từ bầu trời hạ xuống, áp bách Mạnh Minh cao trăm trượng nhe răng trợn mắt, thân thể không ngừng lay động.\r\n\r\n“Mụ đàn bà đáng sợ này phát hỏa rồi, ai lại trêu chọc ả ta?!”\r\n\r\nMột cái đỉnh tròn ba chân màu cổ loang lổ chậm rãi từ trong tầng mây hạ xuống, trong đỉnh vang to một tiếng, vô số điện quang hướng bốn phía tuôn trào. Chờ lúc điện quang biến mất, tất cả dị trạng đều đã tiêu tán, thân rắn khổng lồ trên bầu trời cũng biến mất không còn thấy bóng dáng tăm hơi.\r\n\r\n...\r\n\r\nBầu trời mây đen dày đặc, mưa to điên cuồng quất đại địa, vô số bọt nước tung tóe lên.\r\n\r\nKim tự tháp thủy tinh pha lê chế thành đèn đuốc sáng trưng, chiếu sáng bầu trời đêm tối đen. Mấy chục tráng hán trang bị đến tận răng nắm chặt súng ống, thân khoác áo mưa vờn quanh kim tự tháp tới lui tuần tra, cảnh giác nhìn chăm chú bốn phía.\r\n\r\nBọt nước mưa tung tóe đột nhiên nhảy lên có tiết tấu, giống như nhịp tim, từng chút giọt nước mưa trong suốt không ngừng nhảy lên cao hơn một thước. Mưa bụi trên không trung rơi xuống ‘Tốc tốc’ dây dưa cùng một chỗ, bện thành mấy chục sợi dây thừng nước vô thanh vô tức quấn ở cổ tráng hán võ trang.\r\n\r\nDây thừng nước dùng sức vung, các tráng hán không thể phát ra nửa điểm thanh âm đã bị vặn gãy xương cổ.\r\n\r\nXoáy nước mờ mịt, một bóng người ảm đạm đột ngột hiện lên, đi từng bước một hướng cửa chính kim tự tháp. Đi mỗi một bước, thân hình mông lung liền rõ ràng hơn một phần, lúc đến cửa kim tự tháp, bóng người sương mù ngưng tụ thành đã biến thành người nước trong suốt.\r\n\r\nThân thể hơi nhoáng lên một cái, người nước chợt biến sắc, Thanh Long tuấn lãng dáng người thon dài, gầy gò, mặc trang phục màu đen, bộ dạng võ vàng hiện ra thân hình.\r\n\r\nVô số mưa ngưng tụ thành dây thừng nước vặn vẹo, giống như rắn nhảy múa chui vào kim tự tháp, theo gió chui vào khe cửa sổ, giống như đao sắc chặt đứt dây dẫn của toàn bộ trang bị bảo an. Mảng lớn ánh lửa điện phun ra, trong kim tự tháp chỉ có hệ thống chiếu sáng vẫn hoàn hảo không tổn hao gì.\r\n\r\nDùng sức đẩy ra cửa chính thủy tinh pha lê dày ba thước, Thanh Long chậm rãi đi vào trong sảnh triển lãm của kim tự tháp.\r\n\r\nHơn một trăm cái đài triển lãm thủy tinh chế thành lẳng lặng đứng sừng sững ở dưới đèn đuốc huy hoàng, trên đài triển lãm ngoại vi đặt mấy chục bộ hài cốt ngồi khoanh chân, trên một cái đài triển lãm chính giữa, dưới vòng bảo hộ thủy tinh chống đạn thật dày chế thành, sừng sững một cái đỉnh tròn to bằng đầu người long phượng quấn quanh, ngũ sắc gần như trong suốt.\r\n\r\nThanh Long đến trước một cái đài triển lãm ngoại vi, lẳng lặng quan sát một bộ hài cốt khoanh chân.\r\n\r\nNhững hài cốt này rất giống cốt cách nhân loại, nhưng hài cốt trước mắt toàn thân màu vàng sẫm bán trong suốt, giống như lưu ly ở dưới đèn đuốc chiếu rọi tỏa sáng rạng rỡ. Nếu bọn họ đứng thẳng, chiều cao hẳn là đều ở trên hai thước. Càng làm cho người ta thấy khác biệt là, trên đầu lâu bọn họ trừ một trái một phải hai cái lỗ thủng tối như mực, chính giữa mi tâm còn có một cái lỗ mắt lớn hơn nữa.\r\n\r\n“Người ba mắt?” Thanh Long từ trong giày rút ra một con dao găm, dùng sức gõ gõ hài cốt trước mặt.\r\n\r\nHài cốt khi gõ lên thì vang ‘Leng keng’, dao găm hợp kim đặc chủng rèn thành, trình độ cứng rắn có thể so với kim cương thế mà không thể lưu lại bất cứ dấu vết nào ở trên hài cốt này. Sắc mặt Thanh Long trở nên đặc biệt nghiêm túc, hài cốt loài người tính chất so với kim cương còn cứng hơn, giá trị chất chứa trong đó quá lớn.\r\n\r\n“Một chuyến này ta tự mình động thủ, quả thật cũng đáng giá.” Thanh Long xoay người, đi tới trước đài triển lãm chính giữa cười nói: “Ầy, các ngươi còn không ra mặt, ta sẽ mang theo những bảo bối này trở về.”\r\n\r\nCửa hông sảnh triển lãm mở ra, một đội nam nữ hơn hai mươi người mặc áo gió thật dài màu đen, thân thể bị lửa, sấm sét, gió lốc… các loại dị tượng bao vây đi ra, đi dẫn đầu là một cô gái lạnh lùng tóc vàng mắt biếc, dáng người cao gầy yểu điệu, cầm kiếm ngọc hình thù kỳ lạ.\r\n\r\n“Thanh Long tiên sinh, nghe đại danh đã lâu.” Cô gái đi tới đối diện đài triển lãm, hướng Thanh Long hơi hạ thấp người hành một lễ: “Mấy năm nay, đồng bạn chúng ta chết ở trên tay ngài đã vượt qua một trăm người, trong đó tiền nhiệm đặc cần cục trưởng của ta đã có nhiều tới ba người. Nhưng làm người ta không thể tưởng tượng là, chúng ta thế mà vẫn là lần đầu tiên nhìn thấy bộ dáng thật sự của ngài.”\r\n\r\n“A nha nha, vấn đề này thật ra không quan trọng như vậy. Bộ dạng ta không đẹp trai cho lắm!” Thanh Long vừa trêu chọc cô gái, tay phải đặt ở trên lồng thủy tinh chống đạn của đài triển lãm, cơ thịt lòng bàn tay khẽ giật, thủy tinh chống đạn dày tới hai thước liền vỡ thành vô số hạt cát.\r\n\r\n“Thần sao!” Mọi người theo phía sau cô gái đi ra ùn ùn kinh hô thành tiếng, kinh hãi lui một bước. Bọn họ đã sớm nghe nói đông phương Hoa Hạ Thanh Long là nhân loại mạnh nhất thế gian, nhưng bọn họ nằm mơ cũng không ngờ được Thanh Long có thể mạnh như vậy!\r\n\r\nThủy tinh chống đạn đặc chủng dày hai thước, món đồ chơi ghê gớm pháo chính của xe tăng cũng không phá nổi, vậy mà một chưởng nhẹ nhàng đã có thể chấn vỡ? Loại lực lượng này, hoàn toàn vượt qua cực hạn tưởng tượng của những người này—— nhân loại thật sự có thể trở nên mạnh như vậy?\r\n\r\nHai tay nâng lên đỉnh tròn ba chân, một cảm giác cực kỳ kỳ diệu dần sinh ra. Thanh Long đang cái đỉnh này, liền cảm giác giống như đang nắm toàn bộ vũ trụ.\r\n\r\nCẩn thận đem cái đỉnh tròn bỏ vào trong túi da co rút lại bên hông, Thanh Long cười nói: “Những bảo bối này, đến từ Lương Chử cổ thành của quốc gia ta, ta phải đem chúng nó thu hồi. Dựa theo luật lệ của chúng ta, nắm đấm to là đại gia, các ngươi không có ý kiến đối với chuyện ta thu về văn vật của bổn quốc chứ?”\r\n\r\nCô gái cười khẽ nhìn Thanh Long, nhẹ nhàng nói: “Thanh Long tiên sinh, ngài có lẽ có hứng thú biết một số thành quả nghiên cứu của chúng ta đối với những bảo vật thần bí này? Ngài có lẽ đoán không ngờ được, tầng đất bao phủ chúng nó, trải qua chúng ta đo lường tính toán, niên đại vượt qua mười vạn năm.”\r\n\r\nThanh Long theo bản năng sờ sờ túi da bên hông, đồ cổ mười vạn năm trước? Mười vạn năm trước nhân loại còn đang ăn lông ở lỗ nhỉ? Bọn họ có thể chế tạo được bảo vật tinh xảo đẹp đẽ như vậy, chất chứa năng lượng kỳ diệu như vậy?\r\n\r\nKhẽ lật bàn tay, cô gái lấy ra kiếm ngọc trong tay.\r\n\r\nKiếm ngọc dài ba thước rộng cỡ bàn tay, cả thanh kiếm ngọc chính là dùng cả một khối ngọc thạch điêu khắc thành. Chỗ gần chuôi kiếm có một hoa văn kỳ dị—— trên một tòa tháp cao, lơ lửng một con mắt dựng thẳng, con mắt màu đỏ tươi nhìn chằm chằm Thanh Long, tản mát ra một hơi thở tà dị âm lãnh.\r\n\r\n“Chúng ta đã từng thí nghiệm, thanh kiếm ngọc này dùng Hòa Điền dương chi ngọc (ngọc mỡ dê) điêu khắc thành, chất liệu quả thật là dương chi ngọc. Mọi người đều biết, dương chi ngọc là một loại ngọc mềm, tính chất phi thường nhẵn nhụi.” Cô gái mỉm cười nói: “Nhưng vô luận là kim cương thiên nhiên hay là hợp kim đặc chủng cứng rắn nhất phòng thí nghiệm của chúng ta hợp thành, nó đều có thể thoải mái, giống như cắt đậu phụ vậy đem bọn nó cắt ra.”\r\n\r\nTùy tay rung một cái kiếm hoa, cô gái mỉm cười nói: “Ta dùng nó, tự tay một kiếm bổ ra một cái máy bay ném bom chiến lược —— cách hai km, lấy thực lực ta, ta có thể bổ ra một cái máy bay ném bom chiến lược. Thanh Long tiên sinh, nếu thanh kiếm này nắm ở trong tay của ngài, ta thật sự không thể tưởng tượng lực sát thương của nó có thể lớn bao nhiêu.”\r\n\r\nSắc mặt Thanh Long lại biến hóa, cô gái trước mắt và một đám cấp dưới phía sau cô hoàn toàn không đặt ở trong lòng Thanh Long, gà đất chó ngói, một đòn nhẹ nhàng có thể hoàn toàn tan rã. Nhưng nếu thanh kiếm này thật sự có uy lực như vậy, như vậy Thanh Long hôm nay nhắm chừng sẽ rất khó toàn thân mà lui.\r\n\r\n“Nói như vậy, hôm nay nơi này là một cái bẫy?” Thanh Long vặn vẹo xương cổ một cái, bắt đầu hoạt động các đốt xương toàn thân.\r\n\r\n“Chúng ta muốn có cơ hội mặt đối mặt đàm phán với Thanh Long tiên sinh.” Cô gái cười rất quyến rũ: “Ngài ở vài năm trước tạo thành tổn thương quá lớn cho chúng ta, chúng ta hy vọng, Thanh Long tiên sinh có thể gia nhập chúng ta, chức vị của ta, thậm chí chức vị thủ trưởng trực tiếp quản lý ta, chỉ cần ngài mở miệng, chính là của ngài.”\r\n\r\n“Đương nhiên, làm trao đổi, chúng ta hy vọng có thể đạt được toàn bộ sự trung thành của ngài, cùng với《 Cửu Tự Chân Ngôn Đan Kinh 》ngài sáng tạo!” Lúc cô gái nói mấy chữ cuối cùng, hơi thở của nàng và người phía sau nàng chợt bắt đầu rối loạn.\r\n\r\nCửu Tự Chân Ngôn, là vô thượng bí thuật, khai phá tiềm lực cơ thể người, câu thông lực lượng thần bí của vũ trụ, có uy lực quỷ thần khó lường.\r\n\r\nMà Thanh Long sáng tạo Cửu Tự Chân Ngôn Đan Kinh độc đáo, thì là lấy Cửu Tự Chân Ngôn làm cơ sở sáng tạo một môn thần công độc đáo, Thanh Long chính là dựa vào môn thần công này khống chế địa thủy hỏa phong, câu thông thiên địa u minh, đến vô ảnh đi vô tung, được xưng nhân loại mạnh nhất đương thời.\r\n\r\n“Cái này đương nhiên là không có khả năng!” Thanh Long vỗ vỗ túi da bên hông, cười lên cổ quái: “Các ngươi hẳn là biết tính tình của ta, ta có một mỹ danh ‘Trừng mắt tất báo’, đồng thời ta còn là có tiếng —— không bán tổ tông!”\r\n\r\nCười một tiếng quái dị, thân thể Thanh Long nhoáng lên một cái, chợt biến thành mấy chục cái tàn ảnh, kéo theo vô số lưỡi đao gió sắc bén bằng bàn tay hướng cô gái và mọi người phía sau cô cuồn cuộn đánh tới. Tàn ảnh cùng phong đao hóa thành lốc xoáy mông lung, đem toàn bộ sảnh triển lãm của kim tự tháp đều bao phủ vào.\r\n\r\nCô gái bất đắc dĩ lắc đầu thở dài một tiếng: “Ta đã biết là như thế mà, nhưng đám thủ trưởng ngu xuẩn chết tiệt kia, bọn họ cứ muốn phí khí lực!”\r\n\r\nTrong tiếng cười lạnh, cô gái lấy ra một thẻ tre màu vàng, dùng sức hướng đỉnh đầu ném. Trời cao vang lên một tiếng sấm sét kinh thiên động địa, trong lỗ mắt tối như mực trên đầu gần trăm hài cốt khô lâu ngồi xếp bằng ở trên đài triển lãm bốn phía đồng thời phun ra ánh sáng màu máu.\r\n\r\nÁnh sáng màu máu hội tụ thành biển, trong tiếng ‘Ong ong’ chợt hóa thành một màn hào quang màu máu thật lớn, đem Thanh Long giam cầm chặt chẽ ở bên trong.\r\n\r\nVô số tàn ảnh và phong đao đánh vào trên màn hào quang màu máu, màn hào quang không chút sứt mẻ, tàn ảnh và phong đao thì vỡ nát tựa như trứng chọi đá.\r\n\r\nTrời cao điện chớp sấm rền, một mảng mây đen vốn tối như mực đột nhiên biến thành vô số mảng mây chồng chất như vẩy cá. Điện quang như sóng nước lướt qua giữa khe hở mảng mây, đầy bầu trời điện quang, bầu trời giống như phát cuồng một cái tiếp một cái tia sét kinh thiên động địa không ngừng truyền đến.\r\n\r\nTrên đường cái chung quanh truyền đến vô số tiếng kinh hô, nhiều người đi đêm ngẩng đầu lên, nhìn tầng mây dị biến trên trời —— ở phía sau tầng mây thật dày, rất nhiều người như thấy được một cái thân rắn cực lớn đến mức không cách nào hình dung đang quay cuồng vặn vẹo.\r\n\r\nTrong kim tự tháp, Thanh Long chật vật quỳ rạp xuống đất, cả người đầm đìa máu tươi. Trong màn hào quang màu máu, chín mươi chín cây giáo huyết quang ngưng tụ thành lơ lửng trên không, lẳng lặng chỉ hướng Thanh Long. Mấy chục cây giáo xuyên thấu thân thể Thanh Long, tứ chi cùng lục phủ ngũ tạng đồng thời bị cây giáo màu máu xuyên thủng.\r\n\r\n“Chết tiệt, những đồ chơi cổ quái này thực sự có uy lực mạnh như vậy?” Cô gái hưng phấn nắm chặt thẻ tre màu vàng, khàn giọng thét chói tai: “Thanh Long, ngươi thấy chưa? Nhân loại cường đại nhất cái gì, ở trước mặt chúng ta vẫn không chịu nổi một đòn!”\r\n\r\n“Những hài cốt này cùng thẻ tre này, cùng cái đỉnh ba chân kia đến từ cùng một chỗ! Đều là đồ cổ mười vạn năm trước! Trời ạ, quá không thể tưởng tượng, những hài cốt này dựa theo phương pháp ghi lại trên những thẻ tre này, dùng loại phương vị cổ quái này bố trí, thật sự có thể phát huy ra lực lượng tàn sát thần linh!” Cô gái hầu như điên cuồng lớn tiếng cười chói tai.\r\n\r\n“Cái này con - mẹ - nó - là cái gì vậy?” Thanh Long gian nan ngẩng đầu lên rít gào một tiếng.\r\n\r\n“Trên thẻ tre này có ghi lại, đây là một loại trận pháp cổ xưa, tên cụ thể, xin lỗi, nhà ngôn ngữ học của chúng ta còn chưa phiên dịch ra!” Cô gái nhún vai, bất đắc dĩ nói: “Nhưng những bộ xương này sao, tên của bọn họ là...”\r\n\r\nMột đạo cuồng lôi màu tím từ trên cao hạ xuống, rơi thẳng tắp ở trên kim tự tháp thủy tinh pha lê chế thành.\r\n\r\nToàn bộ thủy tinh pha lê nháy mắt hóa thành tro bụi, cô gái cùng cấp dưới phía sau nàng ở trong nháy mắt bị hóa thành hư ảo, đỉnh tròn ba chân trong túi da bên hông Thanh Long phóng ra ánh sáng năm màu nhàn nhạt, đem cả người hắn bọc vào. Thanh Long điên cuồng gầm rú, thân thể kịch liệt run rẩy, tinh hoa máu, lục phủ ngũ tạng cả người, còn có linh hồn của mình, đều đang bị cái đỉnh tròn ba chân này điên cuồng rút ra.\r\n\r\nChung quanh, hài cốt ba mắt đồng thời giơ hai tay, tạo thế hướng bầu trời vồ ngược. Huyết quang trong mắt bọn họ phun ra ngưng tụ thành một con rồng khổng lồ màu máu, giương nanh múa vuốt hướng về cuồng lôi màu tím cào tới một nhát.\r\n\r\nSét tím hạ xuống, cuồng long màu máu tan thành mây khói, chín mươi chín bộ hài cốt ba mắt kịch liệt chấn động một cái, đồng thời nổ tung thành khói bụi màu vàng.\r\n\r\nSét tím bao trùm Thanh Long, lực lượng sấm sét vô cùng vô tận từ trên cao hạ xuống, đem phạm vi cả dặm triệt để hòa tan hết, lôi quang áp súc thành một quả cầu sét to bằng đầu người, một mảng điện quang cuối cùng hiện lên, tất cả đều bình ổn.\r\n\r\nQuảng trường chỗ kim tự tháp ban đầu không còn sót lại chút gì, không có bất cứ vật thể nào lưu lại.', 1, 'vu-than-ky.jpg', 19, '2020-09-17 03:37:58', '1'),
(10, 'Đại Niết Bàn', 'Đại Niết Bàn - Sống lại tuổi thanh xuân! Câu chuyện bắt đầu từ sự trùng sinh, nhưng đó chỉ là cái cớ để tác giả đưa ra quan điểm và triết lý sống về tuổi trẻ chúng ta – những con người đang ở độ tuổi sung sức nhất để cống hiến cho đời. \r\n', 'Tích tắc thời gian khựng lại đó, cụ thể mà nói là không hề có cảm giác gì.\r\n\r\nTô Xán sửng sốt nhìn mình vẫn giữ nguyên tư thế, hai tay đút túi, chân trái đã bước tới, chân sau đã nhón lên.\r\n\r\nY đông cứng như pho tượng ở trên đường, quái dị hết mức.\r\n\r\nCái mồm vì một tích tắc trước còn vì kinh ngạc mà hơi hé ra, ngay phía trước ba centimet là con ruồi chổng mông lên rất tiêu sái. Nhưng nó và Tô Xán cùng số mệnh, đều bị ấn pause, dừng hình giữa không trung, mắt kép xanh rờn rợn làm Tô Xán trông phát tởm, cơn buồn nôn cuộn từng cơn trong bụng.\r\n\r\nBên cạnh là bà chủ cửa hàng tạp hóa đang hất nước bẩn ra đường, giọt nước tròn xoe to như hạt đỗ tung lên giữa không gian, nhưng con mẹ nó không chịu rơi xuống.\r\n\r\nChùm sáng từ trên cột đèn chiếu xuống một góc phố, muỗi chi chít bu dưới ánh đèn, giống như một bị trộn bê tông đông cừng vào đó.\r\n\r\nChỉ mười giây phút thôi, cái thế giới này của Tô Xán vẫn vận hành một cách bình thường.\r\n\r\nBa tháng trước, phòng hạng mục của công ty Tô Xán gia nhập đối thủ cạnh tranh mới, một sinh viên xuất sắc tới từ đại học đỉnh cấp trong nước, so với cái tên từ nhỏ thành tích trồi sụt, cuối cùng tốt nghiệp đại học hạng ba như y đúng là khác nhau như trời với đất.\r\n\r\nThời gian lại đẩy tới một tháng trước, đối thủ cạnh tranh mới đó dùng tốc độ cực nhanh trở nên thân thiết với công ty từ trên xuống dưới, nghiệp vụ cũng nổi bật, Tô Xán thua kém rõ ràng.\r\n\r\nRồi tới một tuần trước, ban giám đốc công ty dùng bản kế hoạch của đối phương, còn địa vị của Tô Xán bị hạ thấp tới mức không đáng một xu. Cuối cùng đối phương ôm con gái ông chủ, hẹn gặp y trong phòng riêng hắn mới giành được.\r\n\r\nThế là Tô Xán thua trắng bởi đối thủ mạnh mẽ, mất chức trưởng phòng hạng mục mới ngồi ấm chỗ được ba năm, đối lấy bảy ngày nghỉ, chuẩn bị chuyển công tác xuống chi nhánh, kiểu giống như bị lưu đầy, một loại biến tướng xỉ nhục của kẻ thắng và người thua.\r\n\r\nCuộc đời Tô Xán chẳng xán lạn như cái tên của y, đi trên thành phố nhỏ quê hương mà y đã sống mười chín năm, nhìn con đường mà ít nhất mình đã đi mười năm. Sao trên trời lấp lánh, ánh đèn mờ ảo, Tô Xán cười méo miệng, y thấy cuộc đời mình cũng như con đường phía trước kia, chỉ có u ám và cực khổ.\r\n\r\nTrách ai đây? Tiên trách kỷ hậu trách nhân, thành tích tệ hại miễn cưỡng mới vào được sơ trung, cao trung thê thảm mang tới thành tích tốt nghiệp cũng thê thảm, cuối cùng chỉ vào được một cái trường đại học mà chẳng ai biết tên. Cuộc đời vô số bước ngoặt, nhưng Tô Xán chưa bao giờ nắm được cái nào, mỗi cuộc chiến trong cuộc sống y đều thất bại, cấu thành cuộc sống bi kịch thuộc về y.\r\n\r\nCũng chẳng phải y oán trách, trông lên thì mình chẳng bằng ai, nhìn xuống chẳng ai bằng mình, bi kịch cuộc đời vô số, người thê thảm hơn y bội phần có hàng triệu, thậm chí cả tỷ, còn người giống y chắc cũng cả tỷ nữa.\r\n\r\nTô Xán chẳng phải hạng vô dụng vứt đi, y đã nỗ lực đã liều mạng nỗ lực, nếu không làm sao một sinh viên tốt nghiệp trường đại học hạng bét có thể leo lên được chức trưởng phòng hạng mục đó, nhưng lần này đối thủ quá mạnh đã tước bỏ mọi nỗ lực của y...\r\n\r\nMang chút đìu hiu trở về thành phố nhỏ quê hương để trị thương, đi trên con đường quen thuộc ấy, trong ký ức của Tô Xán có một bộ phận sắp mốc meo được khơi lên. Rất nhiều rất nhiều năm trước y cũng đi trên con đường tới trường này.\r\n\r\nQuá khứ mười năm trước ùa về trong lòng, Tô Xán chừng như nhìn thấy chính bản thân mười năm trước, chú nhóc loắt choắt ăn mặc hơi luộm thuộm đeo cái cặp căng phồng đang lầm lũi bước đi, y nở nụ cười vừa hoài niệm, lại mang chút xót xa tiếc nuối, bước tới đúng vị trí đó, đúng thời gian đó, và dùng đúng cái động tác y đã dùng vào trong tối mùa đông trăng sao thưa thớt kia.\r\n\r\nĐúng lúc hai hình ảnh quá khứ và hiện tại trùng hợp làm một đó, thời gian ngừng lại.\r\n\r\nCả thế giới im phăng phắc, Tô Xán không biết mình gặp phải cái gì, thần tiên hay yêu quái? Hay là một lời nguyền khủng khiếp, y chỉ biết bản thân trừ có thể suy nghĩ, cảm quan chưa bị phong tỏa thì không làm gì được, kể cả chớp mắt, mà đôi mắt đã mở to hai mươi giây, cái chân trái nhấc lên hai mươi giây mà không hề cảm thấy mệt mỏi.\r\n\r\nTrơ mắt nhìn thế giới dừng lại trước mắt, lời tiên đoán về ngày tận thế của người Maya tới sớm? Hay mình bị ảo giác? Nhưng Tô Xán thấy mình chưa ngu tới mức coi cảnh tượng chân thật như thế thành ảo ảnh.\r\n\r\nTô Xán thề thời khắc này không một ai có thể giải thích được, bất kể là Albert Einstein, Isaac Newton, Archimedes... Kể cả cái đám chuyên gia ngày nào cũng chửi bới trên truyền thông kia gặp phải loại chuyện này cũng chỉ trừng mắt lên như mình thì cũng chỉ phun rắm được thôi.. À chắc cũng chẳng được, y thử rồi, không đánh rắm được.\r\n\r\nThế giới đột nhiên chuyển động, thế nhưng thời gian quay ngược trở lại.\r\n\r\nXô nước bẩn hất tùy tiện ra đường chui ngược vào trong xô không thiếu chút nào, chủ hiệu khá là thành thục ngon mắt kia lui vào trong hiệu biến mất, trông có mùi của một bộ phim kinh dị.\r\n\r\nCon ruồi chổng đít vào mặt y cách đó ba centimet bị vứt lên chín tầng mây với tốc độ âm thanh.\r\n\r\nTrăng sao biến mất, rồi sáng lên, rồi biến mất, rồi sáng lên...\r\n\r\nMới đầu Tô Xán còn cảm nhận được thời gian đang trôi ngược lại, càng về sau tốc độ càng nhanh, y chỉ thấy xung quanh cứ chớp tắt không ngừng.\r\n\r\nĐường phố quanh y thay đổi, nếu mắt Tô Xán có thể mở to hơn được nữa thì chắc nó toét mất rồi vì cái tòa nhà bảy tầng mới xây trước mắt đang \"phân giải\", từ hoàn thành biến thành chỉ có trơ cái khung thép xấu xí, rồi biến mất, lún vào trong móng, lấp kín, thành một mảnh đất trống, \"mọc\" lên vô số căn nhà ngói, tường quét vôi loang lổ.\r\n\r\nHiệu tạp hóa kia vẫn còn, chẳng những thế biến từ cũ kỹ trở nên mới mẻ, càng ngày càng mới.\r\n\r\nTrên đầu vốn trống không dần dần bị những đường dây chằng chịt thay thế, bên đường đột nhiên biến ra mấy cái cột điện bê tông, đường phố co hẹp, cây cối biến mất.\r\n\r\nCảnh tưởng thay đổi vùn vụt như ngoài cửa sổ tàu siêu tốc, dạ dày Tô Xán nhộn nhạo, mắt hoa lên, choáng váng, đến khi y thích ứng được thì toàn bộ thế giới đã thay đổi, nhưng y thà để đầu óc mơ hồ như vừa rồi còn hơn tình táo như bây giờ, một hơi lạnh chạy dọc sống lưng, vì y không thấy bất kỳ một ai.\r\n\r\nKhông có ai hết! Thế giới vẫn biến đổi như một màn kịch câm, khủng hoảng và khiếp hãi lấp kín đầu óc Tô Xán.\r\n\r\nRồi \"con tàu\" chạy chậm lại, cảnh quan biến đổi chậm dần, chậm dần, giống như lúc mới đầu từ chầm chậm thành nhanh.\r\n\r\nLúc này đây đường phố xung quanh biến hóa nghiêng trời lệch đất, nhưng cảnh tượng này không hề xa lạ với Tô Xán, y dã đi mòn không biết bao nhiêu đôi giày trên con đường này, không biết bao nhiêu ký ức buồn vui giận hờn cay đắng lưu giữ trên con đường này, y lập tức nhận ra... Đây là cảnh tượng mười năm trước.\r\n\r\nThời gian lại lần nữa ngưng đọng mười giây.\r\n\r\nKhôi phục bình thường!\r\n\r\n- Khụ, khụ, khụ...\r\n\r\nDưới ảnh hưởng tâm lý, Tô Xán nìn thở một thời gian cực dài bắt đầu hít thở như cá mắc cạn, thở gấp tới mức ho muốn chảy nước mắt.\r\n\r\nCó âm thanh, cuối cùng cũng có âm thanh rồi, ôi con mẹ nó, cuối cùng mình cũng quay về thế giới bình thường!\r\n\r\nKhông những có mà còn quá nhiều, đủ thứ tiếng động huyên náo đập vào tai, Tô Xán nhất thời chẳng phân biệt được thứ âm thanh rõ ràng nào, nhưng y cảm thấy hạnh phúc vô ngần, giang rộng cánh tay cảm thụ sinh mạng quay lại cơ thể, có điều tay vừa giơ lên đã chạm phải người bên cạnh, Tô Xán chớp mắt lia lịa, y đang ở trong đám đông, cụ thể mà nói là trong làn sóng học sinh.', 1, 'dai-niet-ban.jpg', 18, '2020-08-05 11:07:20', '1'),
(11, 'Trở Về Năm 1981', 'Nội dung chính của truyện Trở về năm 1981 mang màu sắc huyền ảo và kì bí với một cốt truyện khá lạ và nhân vật cá tính.Nhân vật chính là Kim Minh Viễn là một tên tội phạm giết người hàng loạt nhưng có quá khứ vô cùng đáng thương. Không những vậy, thân phậ', 'Trở về năm 1981\r\n\r\nĐây là nhiệm vụ mới của tôi.\r\n\r\nSự việc bắt đầu từ một tuần trước.\r\n\r\nHôm đó tôi và mấy thực tập sinh cùng phòng lủi qua phòng kế bên tán gẫu với nhau, vừa khéo gặp lúc viện Kiểm sát đưa hồ sơ tới. Mà lần này người đưa hồ sơ không phải ai khác, chính là ông anh họ Lưu Hạo Duy của tôi.\r\n\r\nGần đây cả thành phố đang xôn xao vì vụ án 23/5, do đó tôi cũng không kìm được lòng tò mò mà hỏi mượn anh tập hồ sơ xem qua một chút. Vừa mới lật trang đầu tiên, thực tập sinh Tiểu Hoàng bên cạnh tôi đã thốt lên bằng giọng hết sức kinh ngạc: \"Không ngờ lại là một anh chàng điển trai!\"\r\n\r\nKhi tôi cúi xuống nhìn, trong lòng cũng cảm thấy có chút kinh ngạc. Thường ngày nhìn thấy đám phần tử bất hảo có bộ dạng xấu xa hung ác đã nhiều, nay đột nhiên thấy một khuôn mặt trắng trẻo điển trai như thế, trong thời gian ngắn thật đúng là không thích ứng nổi.\r\n\r\nNgười đàn ông trên bức ảnh trông còn trẻ hơn tuổi thực, khuôn mặt trắng trẻo sạch sẽ, hết sức dễ nhìn, hơn nữa còn có vẻ phong độ của thành phần trí thức. Càng hiếm có hơn là đôi mắt anh ta rất có thần, tràn đầy chính khí, dù có nhìn thế nào cũng không thể liên tưởng tới gã hung thủ trong vụ án giết người ngày Hai mươi ba tháng Năm.\r\n\r\nTiểu Hoàng hiển nhiên cũng có suy nghĩ giống tôi, không kìm được thấp giọng làu bàu: \"Anh Lưu này, chắc không phải cảnh sát không bắt được ai, cho nên mới tìm bừa một người cho có đấy chứ? Người này vừa nhìn là biết thuộc thành phần tinh hoa của xã hội, sao có thể là tội phạm giết người được.\"\r\n\r\nNhững đồng sự khác trong phòng cũng nhao nhao ghé tới góp vui, phần lớn phụ nữ đều đồng ý với suy nghĩ của chúng tôi. Nhưng cũng có người lớn tiếng phản đối: \"Phụ nữ các cô đều thích xem mặt mà bắt hình dong như thế cả, chưa nghe thấy câu mặt người dạ thú bao giờ sao, đây chính là ví dụ xác đáng nhất đấy...\"\r\n\r\nLời còn chưa kịp nói xong, anh chàng đó đã bị các chị em phụ nữ trong phòng đuổi biến ra ngoài.\r\n\r\nNgày hôm sau, viện Kiểm sát đã sắp xếp công việc xong. Bởi vì đây là một vụ án hết sức nghiêm trọng, lại gây ảnh hưởng cực lớn, do đó viện trưởng đã đích thân đứng ra làm chủ tọa phiên tòa, mấy nhân viên nhiều kinh nghiệm thì được giao nhiệm vụ làm thẩm phán. Vì hai năm nay tôi làm việc khá tốt, nên cũng được viện trưởng để mắt, trở thành một trong các thẩm phán.\r\n\r\nBuổi tối, tôi thắp đèn, tỉ mỉ nghiên cứu tập hồ sơ một lượt, sau khi xem xong hông kìm được phải xuýt xoa cảm thán.\r\n\r\nKhông có ai mới sinh ra đã là tội phạm, người đàn ông tên gọi Kim Minh Viễn này cũng tương tự. Sau khi xem hết tập hồ sơ, tôi không những không cảm thấy căm ghét anh ta chút nào, ngược lại còn có phần thông cảm.\r\n\r\nAnh ta có một cuộc đời trớ trêu, lận đận hơn người bình thường gấp trăm lần, baha mẹ cùng mất, sau đó được cậu mợ nhận nuôi. Mợ anh ta tình tình cay nghiệt, nên ngay từ nhỏ, anh ta đã phải chịu rất nhiều sự ngược đãi, đến năm bảy tuổi lại bị mợ bán cho một gia đình không có con trai trên thị trấn. Nhưng hai năm sau, gia đình đó bất ngờ sinh được một đứa con trai, thế là năm chín tuổi, anh ta bị đưa vào cô nhi viện.\r\n\r\nKim Minh Viễn sống trong cô nhi viện đến năm mười tám tuổi, hồ sơ không miêu tả về cuộc sống của anh ta trong chín năm này, nhưng chắc hẳn là chẳng dễ chịu gì cho lắm, nếu không trong suốt mười năm sau đó tại sao anh ta chẳng về thăm lại lấy một lần.\r\n\r\nNăm 2000, Kim Minh Viễn tốt nghiệp đại học, rồi đến Quảng Châu lập nghiệp. Đến năm 2002 thì bắt đầu có thành tựu, thành lập được một công ty mậu dịch quốc tế, đồng thời tìm được bạn gái, cũng chính là người bị hại đầu tiên trong loạt án giết người sau này - Tăng Tiểu Quyên.\r\n\r\nTrong hồ sơ có hình của Tăng Tiểu Quyên, khuôn mặt tuy không đến mức quá dẹp, nhưng lại đầy vẻ quyến rũ, tác phong cũng khá bạo dạn. nghe nói ban đầu chính cô ta đã chủ động theo đuổi Kim Minh Viễn. Không lâu sau khi hai người xác định mối quan hệ yêu đương, Kim Minh Viễn đã mua nhà ở Quảng Châu để chuẩn bị cho việc kết hôn, nào ngờ đến đầu năm sau Tăng Tiểu Quyên lại đề nghị chia tay, còn cuỗm luôn cả căn nhà cùng với toàn bộ thông tin về khách hàng của công ty.\r\n\r\nCó lẽ do sự đổ vỡ của mối tình đầu, về sau Kim Minh Viễn không tìm thêm người bạn gái nào nữa, mà một lòng một dạ tập trung vào sự nghiệp. Mấy năm sau đó, công ty của anh ta phát triển nhanh chóng, đến năm 2007 đã có được vị thế khá vững chắc trong giới làm ăn.\r\n\r\nTháng Tám năm 2007, Tăng Tiểu Quyên lại một lần nữa xuất hiện, cô ta tìm đến Kim Minh Viễn và yêu cầu nốì lại quan hệ. Không ngờ Kim Minh Viễn đã đồng ý. Tháng Mười cùng năm, hai người quay lại quê cũ ở miền Bắc để chuẩn bị kết hôn. Sau đó vì công ty có việc, Tăng Tiểu Quyên đi lên miền Bắc trước, kết quả là một đi không trở lại, từ đó bặt vô âm tín, đến ngày Hai mươi ba tháng Năm thi thể của cô ta mới được phát hiện tại thành phố S, tỉnh H. Đây chính là ngòi nổ của vụ án giết người 23/5 đặc biệt nghiêm trọng.\r\n\r\nTháng Tám năm nay, khi xem lại các vụ án cũ, Đội phó đội trinh sát thành phố Chu Khai Minh đã phát hiện ra hai vụ án giết người khác, lần lượt là vụ án ở cửa tiệm Trần Gia ngày 17 tháng 10 năm 2008 và vụ còn lại ở đập Vạn Niên vào ngày 17 tháng 10 năm 2009. Chu Khai Minh bất ngờ phát hiện thủ đoạn giết người trong hai vụ này giống hệt với vụ 23/5. Sau quá trình điều tra kỹ lưỡng, cuối cùng ba vụ án này đã được gộp vào làm một để cùng xử lý.\r\n\r\nBị hại trong vụ án ở cửa tiệm Trần gia là một phụ nữ họ Liêu, ba mươi hai tuổi, mất tích vào buổi tối ngày 16 tháng 10 năm 2008, đến buổi chiều ngày Mười bảy thì thi thể của nạn nhân được phát hiện, nguyên nhân tử vong được xác định là do ngộ độc xyanua. Theo phản ánh của hàng xóm, lúc còn sống người phụ nữ này thường xuyên ngược đãi mẹ chồng và hai cô con gái, còn có tin đồn rằng, trước đó cô con gái nhỏ bị bệnh chết của bà ta cũng là bị ngược đãi đến chết. Khi đó cảnh sát đã dồn trọng tâm điều tra vào người chồng họ Thẩm của bà ta, nhưng sau khi điều tra kỹ lưỡng, ông ta đã được chứng minh là trong sạch. Rồi tiếp đó, cảnh sát lại lần lượt thẩm vấn mấy chục người bị tình nghi, nhưng rốt cuộc vẫn không có kết quả.\r\n\r\nCòn người bị hại trong vụ án ở đập Vạn Niên là một người đàn ông trung niên họ Lý, bốn mươi bảy tuổi, còn sống từng làm việc trong một cô nhi viện. Trong quá trình điều tra vụ án, cảnh sát phát hiện, khi còn làm việc, kẻ này đã từng ép mười một bé gái ở cô nhi viện phải bán dâm, dẫn đến cái chết của ba bé trong số đó. Cảnh sát đã đưa các bé gái đó và người nhà của bọn họ vào danh sách đối tượng bị tình nghi, nhưng sau gần nửa năm điều tra vẫn chẳng có chút manh mối nào.\r\n\r\nBởi vì hai vụ án này xảy ra ở hai khu vực khác nhau, cho nên cảnh sát chưa từng nghĩ rằng chúng có liên quan, cho đến sau vụ 23/5, Chu Khai Minh mới phát hiện ra một chút manh mối và ba vụ án được gộp lại để điều tra.\r\n\r\nNếu tôi không phải là thẩm phán mà là một người đàn bà bình thường, nói không chừng sẽ còn lớn tiếng khen hay cho sự \"hành hiệp trượng nghĩa\" của Kim Minh Viễn. Nhưng với tư cách là một người chấp pháp, tôi chỉ có thể lắc đầu thở dài. Anh ta rõ ràng là có cách tốt hơn để giải quyết những chuyện này, nhưng lại lựa chọn biện pháp không thỏa đáng nhất.\r\n\r\nKhông chỉ riêng tôi, lần này ngay đến ông anh họ Lưu Hạo Duy xưa nay luôn ghét ác như thù của tôi cũng phải thay đổi thái độ thường ngày, không khỏi thở than nuối tiếc. Cuối cùng tôi cũng chỉ đành đau đớn đưa ra lời tổng kết: \"Em cảm thấy có lẽ tuổi thơ bất hạnh đã khiến tâm lý của anh ta sản sinh một nỗi ám ảnh. Nếu anh ta là con trai em, nhất định em đã có thể dạy dỗ anh ta trở thành một người vừa lương thiện vừa chính trực...\"\r\n\r\nTôi không ngờ báo ứng lại đến với mình nhanh như thế, ngay tối hôm đó, lão Chương đã tìm đến chỗ tôi.\r\n\r\nTrước khi kể về lão Chương, tôi cảm thấy cũng cần phải giới thiệu sơ qua về bản thân một chút.\r\n\r\nTên tôi là Chung Tuệ Tuệ, năm nay... Chuyện tuổi tác chúng ta tạm không nhắc đến vậy. Trước mắt tôi vẫn độc thân, sống một mình, mỗi dịp tuần đều về nhà thăm cha mẹ. Nhà chúng tôi có nghề Đông y gia truyền, cho nên khi vào đại học tôi đã chẳng do dự gì mà chọn ngay ngành Đông y. Nhưng thời buổi này Đông y chính là ngành học rẻ mạt nhất đã thế tôi lại là con gái, chỉ có mỗi bằng đại học... Và thế là sau khi tốt nghiệp, tôi đã ngay lập tức xung vào đội quân thất nghiệp.\r\n\r\nVề sau, tôi đã phải mất thêm cả một năm trời để chuẩn bị cho việc thi tư pháp, rồi thi công chức, cuối cùng khó khăn lắm mới chen chân được vào đội ngũ công chức nhà nước.\r\n\r\nNếu không có gì bất ngờ, cuộc đời này của tôi hẳn sẽ trôi qua trong lặng lẽ, ấy thế mà lại có một số chuyện không ngờ tới đã xảy ra. Và chắc các bạn cũng đoán được rồi, ngọn nguồn của mọi bất ngờ chính là sự xuất hiện đột ngột của lão Chương.\r\n\r\nNhìn bề ngoài, lão Chương căn bản chẳng khác gì mấy ông cụ, bà cụ hay múa Thái cực ở công viên mỗi ngày. Nếu nhất định phải tìm ra sự khác biệt gì đó, vậy thì là lão ta trông có vẻ bỉ ổi hơn người khác một chút.\r\n\r\nMột buổi sáng tháng Tám năm ngoái, tôi đang vừa ngáp ngắn, ngáp dài vừa chạy vội tới cơ quan, thì chợt bị lão ta chặn lại ngay cửa thang máy, nói gì đó mà trên người tôi có tiên khí.\r\n\r\nKhi đó trực giác đầu tiên của tôi mách bảo mình gặp phi một kẻ lừa đảo, nhưng vừa mới định lên tiếng đuổi lão ta đi, đôi mắt tôi chợt hoa lên, sau đó, tôi đã tới một thế giới khác.\r\n\r\nRồi sau đó nữa, tôi trở thành một trong những nhân viên tạp vụ dưới quyền lão Chương, thỉnh thoảng giúp lão xử lý một vài chuyện mà \"bọn lão\" không tiện ra mặt. Đương nhiên, khi làm những việc này tôi cũng được nhận thù lao, nếu không, bạn cho rằng dựa vào mấy đồng lương còm cõi mỗi tháng của tôi mà có thể ở trong một căn hộ chung cư tiện nghi như bây giờ hay sao chứ?\r\n\r\nBuổi tối hôm đó, lão Chương lại tìm đến, mặt mày ủ rũ uể oải, trông còn có phần bỉ ổi hơn thường ngày, vừa gặp đã chụp ngay lấy chiếc bánh mì trên bàn của tôi, rồi chỉ trong vài ba miếng đã ăn hết quá nửa chiếc bánh. Vừa nhìn thấy bộ dạng này của lão, tôi biết ngay là lại có việc để làm rồi, trong lòng còn thầm cảm thấy vui vẻ.\r\n\r\nLão Chương xưa nay luôn rộng rãi, ngoài tiền bạc ra, thỉnh thoảng còn cho bọn tôi một ít phúc lợi, chẳng hạn như quay về triều đại phong kiến nào đó để nghỉ dưỡng vài ngày... Đương nhiên, việc này tôi chưa được trải nghiệm bao giờ, chỉ là trước đây có nghe một người chị em ở thành phố B kể lại có điều hình như cũng chẳng vui vẻ lắm, nếu không thì về sau chị ta hẳn đã xin đi thêm lần nữa rồi.\r\n\r\nSau khi đã ăn uống no nê, lão Chương rốt cuộc cũng nhớ ra mục đích đến đây lần này, liền vừa lau miệng vừa nói: \"Chắc cô đã biết chuyện về Kim Minh Viễn rồi chứ?\"\r\n\r\nTôi không khỏi ngây ra. Trước đây lão Chương chỉ giao cho tôi mấy công việc nhẹ nhàng kiểu như đi dọn dẹp \"chiến trường\", đa phần đều liên quan tới mấy con yêu quái tép riu. Nói chung, lão chưa hề nhắc tới chuyện của nhà tiên với tôi bao giờ, càng đừng nói là chuyện của người trần mắt thịt.\r\n\r\n\"Tay Kim Minh Viễn đó là yêu quái sao?\" Phản ứng đầu tiên của tôi chính là như vậy\r\n\r\nLão Chương thở dài, đôi lông mày nhíu chặt: \"Nếu là yêu quái thì đã tốt nhưng vấn đề là...\" Lão đưa ngón tay chỉ lên trời dáng vẻ úp úp mở mở.\r\n\r\nTôi giật mình hiểu ra: \"Là người trên đó sao?\"\r\n\r\nLão Chương khẽ gật đầu, tỏ vẻ rất hài lòng về sự lanh lợi của tôi, sau khi suy nghĩ một chút lại giậm chân nói: \"Cô nói xem, có cái lý nào lại như vậy không chứ, rõ ràng là cấp trên làm hỏng chuyện, cuối cùng lại bắt đám cấp dưới chúng ta đi giải quyết.\"\r\n\r\nTôi bực mình nói: “Thôi đi cụ ơi, cháu còn chưa oán trách gì đây này, người làm việc rốt cuộc là cụ hay là bọn cháu chứ?\"\r\n\r\nLão già đó nhe răng toét miệng cười, dáng vẻ như muốn lấy lòng tôi: “Cô yên tâm, đãi ngộ lần này chắc chắn là tốt, tôi sẽ xin với cấp trên để cô được nhận những sự an bài ổn thỏa nhất, tuyệt đối sẽ không bạc đãi cô đâu.”\r\n\r\nCó câu nói này của lão ta là tôi yên tâm rồi. Lão già này tuy trước giờ nói năng chẳng đâu vào với đâu, nhưng con người thực ra cùng không tệ, ít ra cũng rất rộng rãi với tôi. Lão ta đã nói là đãi ngộ tốt, vậy thì chắc chắn là không kém, cho nên tôi cũng không định làm khó lão ta nữa.\r\n\r\nSau đó, lão ta liền kể cho tôi nghe chi tiết về nhiệm vụ lần này. Tới lúc này tôi mới được biết thì ra cái tay tội phạm giết người điển trai Kim Minh Viễn kia lại là đứa con út được \"boss trùm\" cưng chiều nhất, tên là\r\n\r\nTrước đây, khi buôn dưa lê với người chị em ở thành phố B kia, tôi đã từng được nghe kể về anh ta một lần, mà dáng vẻ chị ta khi kể còn hết sức thần bí. Nội dung thì đại khái là gã này đẹp trai thế nào, phong lưu ra sao, lúc thì là có vị tiên tử này đã từng mây mưa, ân ái với hắn ta, lúc lại là có vị tiên tử kia ngoài hắn ta ra thì nhất quyết không chịu lấy ai khác. Nói trắng ra, hắn ta chính là một tên \"dân chơi thứ thiệt phiên bản Thiên Đình,\" suốt ngày chơi bời trác táng, mà trước giờ tôi vốn chẳng có chút ấn tượng tốt nào về loại người này cả.\r\n\r\nCó điều, vị công tử này không chịu ở trên trời làm thần tiên cho sướng, chạy xuống nhân gian làm gì, đã thế lại còn giết người... Thế này chắc là đã phạm vào luật Trời rồi đấy nhỉ?\r\n\r\nLão Chương thấy tôi tỏ ra nghi hoặc, liền chậm rãi giải thích: \"Đây là quy củ của Thiên giới, cứ cách vài trăm năm là các vị đại thiếu gia đó lại phải xuống trần gian một lần, coi như để trải nghiệm nỗi khổ đau của nhân gian. Cô biết đấy, về mặt nữ sắc vị tiểu thiếu gia này... hề hề... có chút chút... không được chung thủy cho lắm, kết quả là con gái nhà người ta tức giận, lén chạy đi thay đổi vận mệnh của cậu ta, lại còn nguyền rủa, dẫn cậu ta vào ma đạo. Chuyện này nhỡ mà thành sự thật, hề hề... chỉ e là không dễ giải quyết cho lắm. Cho nên, cấp trên bắt tôi đi tìm một người quay về quá khứ, chỉ dẫn cho cậu ta đi theo chính đạo.\"\r\n\r\nTôi rốt cuộc đã hiểu chuyện gì đang xảy ra, liền dở khóc dở cười chỉ vào mặt mình nói: \"Cụ thấy cháu trông gibảo mẫu lắm sao?\"\r\n\r\nLão Chương nói: \"Không phải cô đã nói rồi đấy sao, nếu cậu ta là con trai cô, cô nhất định sẽ có thể giáo dục cậu ta trở thành một người vừa lương thiện, vừa chính trực...\"\r\n\r\nTôi thật muốn tát cho mình một cái.\r\n\r\nCác bạn nghĩ nhiệm vụ này dễ lắm sao? Tạm chưa nói việc dạy dỗ một đứa bé vất vả chừng nào, trách nhiệm nặng nề ra sao, chỉ riêng việc phải quay về đầu những năm tám mươi của thế kỷ trước, đó đã không phải là một sự gian khổ mà người bình thường có thể chịu được rồi.\r\n\r\nThời đó ở nông thôn hình như còn chưa có điện, nhà vệ sinh thì chắc đều là hầm cầu, ngay đến việc mua cái kẹp tóc cũng cần có tem phiếu, mọi người còn chẳng có cơm mà ăn... Nếu tôi nhớ không nhầm, gã Kim Minh Viễn đó sinh ra ở vùng nông thôn miền Bắc, một cô gái miền Nam như tôi làm sao có thể chịu nổi cuộc sống mỗi ngày phải ăn bánh bao không nhân chứ? Mà nói không chừng còn chẳng có bánh bao mà ăn ấy chứ[1].\r\n\r\nTôi đương nhiên là không chịu rồi... Tuy biết lão Chương đã tìm đến tôi như vậy, muốn từ chối cũng chẳng từ chối được, nhưng tốt xấu gì cũng phải ra vẻ một chút, như vậy sẽ có thể tranh thủ vòi được nhiều lợi ích hơn về cho bản thân.\r\n\r\n\"Bất kể thế nào, tiền nhất định phải nhiều.\" Tôi bắt đầu vạch từng ngón tay ra tính toán cho lão già đó nghe: \"Cụ cũng nhìn rồi đấy, cháu là thân con gái chân yếu tay mềm, mấy công việc như bưng bê khuân vác chắc chắn là không làm nổi. Chắc cụ không định bắt cháu quay về làm ruộng để nuôi sống cái gã kia đấy chứ, như thế nói không chừng cả hai người bọn cháu đều chết đói mất. Còn nữa, cháu nghe nói chỗ các cụ còn có cái loại pháp bảo gì đó có thể cất giấu đồ vật bên trong...\"\r\n\r\nLão Chương nghe tới đây liền lập tức ngắt lời tôi: \"Cái con nhóc này chỉ ăn nói linh tinh thôi, làm gì có cái thứ pháp bảo nào như vậy chứ.\" Nói xong còn ngoảnh mặt qua hướng khác, dáng vẻ như đang chột dạ.\r\n\r\nTôi khẽ hừ một tiếng, giọng coi thường: \"Thôi đi cụ ơi, bây giờ bên ngoài có ai mà không biết bản lĩnh của các cụ chứ? Không phải là còn có loại không gian di động có thể trồng cây bên trong nữa sao, còn cả loại nước thần tiên gì mà uống vào có thể làm đẹp và kéo đài tuổi thọ ấy. Cụ hãy lấy hết ra đây xem nào, cháu cũng không cần cả đâu, nhưng ít nhất cũng phải cho cháu được mở rộng tầm mắt một chút chứ!\"\r\n\r\nLão Chương tức tối nói: \"Cô đùng có mà nằm mơ nữa đi, nếu thật sự có những pháp bảo như thế, đám yêu quái kia còn cần phải tu luyện nữa hay sao? Cái gì mà không gian di động có thể trồng cây, cô không biết như thế là trái với quy luật tự nhiên à?\"\r\n\r\nNgay đến thần tiên cũng xuất hiện rồi,i quy luật tự nhiên ra đây làm gì nữa chứ?\r\n\r\nThấy tôi tỏ ra kiên quyết, lão Chương rốt cuộc đành nhượng bộ, hậm hực làu bàu: \"Bảo bối có thể cất giấu đồ vật thì cả Thiên Đình cũng chỉ có vài cái thôi, vị tiểu thiếu gia đó hình như cũng có một cái. Dù sao thì cô cũng làm việc cho cậu ta, đợi tôi về xin với cấp trên, rồi sẽ mang cái thứ đó đến cho cô tạm thời sử dụng.\"\r\n\r\nSau đó lão ta lại nói ra một loạt những phép tắc quy củ gì đó, mãi đến lúc tôi đã phải ngáp ngắn, ngáp dài, lão mới chịu rời đi.\r\n\r\nMột lúc sau, lão ta lại đi vào với một món đồ vuông vức được gói lại bằng giấy báo, vừa mở tờ báo ra vừa cất tiếng than phiền: “Cả một khu nhà to bự thế này, vậy mà ngay đến viên gạch cũng không tìm được, cái này phải dỡ từ bồn hoa bên ngoài kia mới có được đấy.\"\r\n\r\nThứ được bọc trong tờ giấy báo là một viên gạch vàng rất lớn, kích thước và những đường nét hoa văn trên đó đều có vẻ quen mắt vô cùng. Việc lão già này có thể biến gạch thành vàng thì tôi đã biết từ lâu, vì vậy bèn bật cười trêu chọc: \"Trong cái nhà này thứ nào mà chẳng được, cụ cứ nhất quyết đòi ra ngoài tìm làm gì? Cụ nhìn cái bàn hay cái tủ quần áo này xem, có gì mà không được chứ?\" Đương nhiên, mấu chốt là mấy thứ đó đều to hơn viên gạch này rất nhiều.\r\n\r\nLão Chương không thèm để ý đến tôi, vứt viên gạch vàng lên bàn, nói một câu: \"Sáng mai tôi sẽ đưa đồ đến.\" Sau đó thì biến mất chẳng còn còn chút tăm tích nào.\r\n\r\nSáng sớm ngày hôm sau, tôi nhìn thấy thứ đồ mà lão Chương đưa đến ở ngay trên gối của mình. Đó là một chiếc nhẫn ngọc xanh biếc, vừa nhìn đã biết là đồ đắt tiền. Tôi thứ đeo nó vào ngón tay cái, nhưng quả thực là vướng víu vô cùng, đành mở ngăn kéo ra tìm một sợi dây mảnh màu đỏ, rồi xỏ qua chiếc nhẫn và đeo lên cổ như dây chuyền.\r\n\r\nThứ đồ này thực sự rất dễ dùng, chỉ cần tập trung tinh thần một chút là có thể cảm nhận được một không gian rộng lớn bên trong, cũng không thể nói rõ là nó rộng đến chừng nào, dù sao thì bên trong đó rất trống trải, chẳng có thứ gì hết. Tôi nhìn chằm chằm vào cái chăn trên giường, cái chăn lập tức biến mất, rồi sau đó là giường, bàn, ghế... úi, phải làm sao mới có thể bỏ chúng ra lại được nhỉ?!\r\n\r\nTôi vội gọi điện thoại cho lão Chương, hỏi han tỉ mỉ về cách sử dụng chiếc nhẫn, sau dó dùng một tờ báo cũ gói viên gạch vàng kia lại, rồi lục lọi trong tủ suốt một hồi lâu, tìm một chiếc ba lô du lịch cũ nát nhất, nhét viên gạch vàng vào trong, và chạy thẳng đến cửa hàng vàng bạc đá quý. Loáng cái, viên gạch vàng đã được đổi thành tiền mặt.\r\n\r\nLão Chương nói sẽ đưa tôi về năm 1981, khi Kim Minh Viên mới ba tuổi. Tình hình thời đó thế nào tôi quả thực khó mà biết rõ, nhưng các bậc tiền bối, cha anh đã nói rồi, có đề phòng trước sẽ tránh được tai vạ, chỉ cần tôi chuẩn bị kỹ càng mọi thứ, đến lúc đó gặp phải chuyện gì cũng không sợ.\r\n\r\nVừa khéo gặp dịp Quốc khánh được nghỉ bảy ngày, tôi nói với mọi người trong nhà là mình ra ngoài du lịch, nhưng thực tế là cả ngày đều lượn lờ khắp nơi để đi mua hàng.\r\n\r\nNhững thứ như gạo, bột mì, dầu thực vật tôi đều mua trực tiếp từ xưởng chế bin, mỗi lần một xe.\r\n\r\nNhững thứ như quần, áo, mũ, tất, giày, dép của trẻ con cùng cần phải chuẩn bị đủ, từ ba tuổi đến mười tám tuổi, mỗi mùa vài bộ, lại còn cả đồ dùng của tôi nữa... Lớn thì như chăn, ga, gối, đệm, nhỏ thì như xà phòng, khăn mặt, dầu gội đầu, còn cả kẹp tóc và bấm móng tay, rồi thì giấy tờ giả, giấy giới thiệu giả... Về sau, tôi liền đi tìm một cậu chuyên khắc dấu giả, bảo cậu ta khắc cho mình mười mấy con dấu, nói là dùng cho việc quay phim, cũng không biết cậu ta có tin không nữa.\r\n\r\nNhững thứ hàng hóa này thực ra đều là chuyện nhỏ, chỉ cần có tiền là có thể mua được, phiền phức nhất là tìm mua tiền cũ.\r\n\r\nDù sao thì tôi cũng không thể mang loại tiền được in vào năm 2010 để quay về thời điểm đó mua đồ được, ít nhất cũng phải dùng loại tiền sản xuất năm 1980. Vì chuyện này tôi đã phải nhờ cậy đến anh họ mình, rồi một người bạn ở cục Cảnh sát của anh đã giúp tôi tìm được một người buôn tiền cũ. Tôi mua luôn hơn hai ngàn đồng loại tiền được sản xuất vào năm tám mươi, toàn bộ đều là những đồng Đại đoàn kết[2].\r\n\r\nMất ba ngày tôi mới tạm coi như chuẩn bị xong xuôi mọi thứ, sau đó kiểm tra kỹ càng lại một lượt, rồi thỉnh thoảng đi mua thêm ít đồ bổ sung vào.\r\n\r\nNgày cuối cùng trước khi đợt nghỉ lễ Quốc khánh kết thúc, tôi đã bị lão Chương đưa về năm 1981.', 1, 'tro-ve-nam-1981.jpg', 21, '2020-08-21 11:07:20', '1');
INSERT INTO `story` (`story_id`, `name`, `preview_text`, `detail`, `cat_id`, `picture`, `counter`, `create_at`, `status`) VALUES
(12, 'Phong Lưu', 'Một thanh niên mạnh khỏe xuyên việt tới thời Nhà Đường, tán đệ nhất mỹ nữ Dương Quý Phi!\r\n\r\nĐây là một truyện không hẳn thuộc thể loại lịch sử, cũng không hẳn kiếm hiệp, lại có phần sắc hiệp.', '\r\nTrong mặt nước trong suốt xuất hiện một gương mặt xa lạ, Đường Tiểu Đông thống khổ rên rỉ một tiếng.\r\n\r\n- Ta kháo, cố sự xuyên qua của Tầm Tần Ký dĩ nhiên thực sự ứng nghiệm trên người lão tử rồi? (Tầm Tần Ký là tác phẩm tuyệt vời của Huỳnh Dị)\r\n\r\nChoáng váng nửa ngày, phải tiếp thu chuyện thực xuyên qua tới thời cổ đại, hắn cúi đầu nhìn y phục bằng vải thô rộng thùng thình trên người chính mình, y phục này hình như\r\n\r\nbị giặt đi giặt lại tới bạc màu vải, khắp nơi là mụn vá, dưới chân là đôi giày rơm rách nát, mặt hắn thiếu chút nữa lệnh sang một bên.\r\n\r\nMẹ nó, tại hiện đại, hắn nghèo tới mức không kiếm được lão bà, thực sự là một tên đen đủi mới ra đời, không nghĩ tới xuyên qua tới thời cổ đại càng thêm nghèo khó, vì sao lão tử lại không may mắn như vậy? Ô ô ô…\r\n\r\nAi, việc đã tới nước này, oán trách lão Thiên thì cũng có cái lông dùng? Tốt nhất nên nghĩ biện pháp phát tài mới là vương đạo.\r\n\r\nThấy trong bụi cỏ là một vài thứ rơi lả tả, không khỏi mừng rỡ tới thiếu chút nữa nhảy dựng lên.\r\n\r\nHa ha, một khẩu súng lục!\r\n\r\nĐường Tiểu Đông mừng rỡ như điên, nhặt khẩu súng lục lên, yêu thích không muốn buông tay. Bạn đang đọc truyện được copy tại TruyệnFULL.vn\r\n\r\nHắc, còn có thể dùng, trong băng đạn còn đầy 8 phát chưa bắn phát nào, vui a.\r\n\r\nHắn không biết khẩu súng lục này là tài sản quốc gia gì gì đó, cũng không biết nó gọi là gì, dù sao có thể sử dụng là tốt rồi.\r\n\r\nNghĩ lại một chút, lúc đó lão đại mang theo bọn họ tới bến tàu giao dịch súng ống đạn dược, đột nhiên cảnh sát xuất hiện, song phương sống mái với nhau một hồi, hắn ôm đầu chui vào trong góc kín, đan bay tán loạn, sau đó truyền tới một tiếng nổ kinh thiên động địa, hắn chỉ cảm thấy thân thể chấn động, con mắt tối sầm, cuối cùng bất tỉnh nhân sự.\r\n\r\nTỉ mỉ nhìn kỹ, không còn vũ khí nào khác.\r\n\r\nĐường Tiểu Đông ngẩng đầu nhìn xung quanh, bốn phía đều là quần sơn liên miên nhấp nhô nối liền với nhau, rừng rậm xanh um tốt tươi, địa phương hắn đang đứng là một khu đất bằng phẳng, cỏ dại mọc thành bụi, chừng ở giữa sườn núi.\r\n\r\nLấy địa phương phát hiện khẩu súng lục làm trung tâm, tìm kiếm trong vòng phương viên một trăm mét xung quanh, quả nhiên không hề thiếu thu hoạch.\r\n\r\nMột kiện áo chống đạn đã có chút tổn hại, dù sao mặc lên được là được. Một chiếc cung nỏ có thể gấp lại, ba mũi tên, có thể phóng ra tên và bi sắt, còn có một bộ phận hỗ trợ ngắm chuẩn, quá vui!\r\n\r\nVui vẻ nhất chính là tìm được một khẩu MP5 tự động, bình thường chơi CS thường xuyên được nhìn thấy, vì vậy hắn nhận ra, một hộp đạn, trọn 30 phát, vui tới ngây người.\r\n\r\nThứ đồ này được sản xuất từ Đức, toàn bộ dài 680mmm, không đạn nặng 2,45kg, băng đạn có dung lượng 30 viên, tầm sát thương vào khoảng 200m, tốc độ bắn 800 RPM, độ xạ kích tầm gần nhất cao, nếu như mục tiêu bị bắn trúng, ngoại trừ biến thành cái sàng, tuyệt đối không còn khả năng sống.\r\n\r\nTỉ mỉ tìm kiếm một lần nữa, kháo, còn phát hiện ra ba lựu đạn quân dụng!\r\n\r\nHiện tại chính mình có một khẩu súng lục, một khẩu MP5 tự động, ta lựu đạn quân dụng, một cung nỏ có bộ phận hỗ trợ ngắm bắn, còn có áo giáp chống đạn, hơn nữa tất cả những thứ này đều tới từ xa hội xe tăng, máy bay, booom nguyên tử công nghệ cao, nếu như đi ngang trong cái xã hội lạc hậu cũ kỹ này không được, vậy thì đâm đầu vào tường chết cho xong.\r\n\r\nMẹ nó, lão tử muốn hỗn cho ra trò tại cái xã hội cũ kỹ không biết triều đại nào này! Ta thời hiện tại không kiếm nổi một lão bà, đã tới xã hội này rồi, nhất định phải kiếm nhiều hơn mấy người làm bồi thường.\r\n\r\nHiện tại sao, rời khỏi cái địa phương quỷ này rồi nói.\r\n\r\nHắn mặc áo giáp chống đạn lên người, treo khẩu MP5 tự động, thắt lưng buộc khẩu súng lục, ba lựu đạn quân dụng thì móc tại quần trong, cầm cung nỏ đi về phía trước, thấy trong bụi cỏ có một con đường nhỏ, liền đi men theo con đường nhỏ này.\r\n\r\nKháo, ba lưu đạn quân dụng đọng lại trên quần trong quá mức nặng, khiến chiếc quần rộng thùng thình bị kéo xệ xuống, không thể làm gì khác hơn là chỉnh lý một chút. Trong cái xã hội này, dây lưng bằng với miếng vải dài, một lần nữa thắt lại, tiếp tục lên đường.\r\n\r\nDọc theo đường đi tính toán làm cách nào có thể phát tài và nổi bật, còn có, cái xã hội cũ kỹ này rốt cuộc là triều đại gì? Hoàng đế hiện nay là ai?\r\n\r\nĐi một đoạn đường rất nhỏ trên con đường quanh co khúc khuỷu, phía trước đột nhiên truyền tới tiếng sắt thép va chạm từng đợt hô đánh đánh giết giết, thỉnh thoảng còn vang vọng tiếng kêu thê lương thảm thiết.\r\n\r\nCó người đánh nhau?\r\n\r\nĐường Tiểu Đông buộc lại chiếc cung nỏ vào đai lưng phía sau, mở chốt bảo hiểm của khẩu MP5 tự động, lặng lẽ tiếp cận địa phương tranh cấu.\r\n\r\nTrên đại đạo, một đội quan binh và hơn mười người bịt mặt đánh nhau kịch liệt, trên mặt đất đã nằm rải rác hơn mười thi thể quan binh.\r\n\r\nNhững quan binh này xếp thành hình tròn, liều mạng bảo hộ một chiếc xe ngựa chính giữa, đám người bịt mặt bên ngoài vòng tròn chiếm hết thượng phong, bọn họ huy vũ đao kiếm, điên cuồng chém giết, thỉnh thoảng có quan binh kêu thảm ngã xuống.\r\n\r\nOa, đây là võ công chỉ thấy xuất hiện trong điện ảnh võ hiệp hoặc là tiểu thuyết nha? Không biết cái gì Hàng Long Thập Bát Chưởng, Cửu âm Chân Kinh lợi hại hay là đạn của lão tử lợi hại?\r\n\r\nHắc hắc, thử một lần không phải sẽ biết sao!\r\n\r\nĐường Tiểu Đông quay về phía một tên bịt mặt bên ngoài vòng tròn bắn tỉa.\r\n\r\nOanh…\r\n\r\nTiếng súng nổ đinh tai nhức óc khiến cả hai bên đều bị dọa tới choáng váng, không hẹn đều cùng dừng tranh đấu.\r\n\r\nHa ha, bầu trời một tiếng sét đánh, lão tử lóe sáng gặt hái.\r\n\r\nĐường Tiểu Đông ngông nghênh bước tới, học tiếng hét lớn từng xem trong hí kịch:\r\n\r\n- Ban ngày ban mặt dám giết người cướp của, các ngươi còn có vương pháp hay không?\r\n\r\nMột tên bịt mặt bị trúng đạn ngã gục trong vũng máu, trước ngực còn đang trào máu tươi, một tên bịt mặt khác nghiến răng nghiến lợi lao tới:\r\n\r\n- Ghê tởm, cuồng đồ đâu tới, báo danh ra!\r\n\r\nĐường Tiểu Đông tà tà cười, trả lời hắn là một phát đạn.\r\n\r\nLại ầm ầm một tiếng vang vọng, giống như sét đánh giữa trời quang, tên bịt mặt kia phát sinh một tiếng kêu thảm thê lương, lập tức ngã xuống đất, đã tắt thở tại chỗ.\r\n\r\nNếu như phát sinh đầu tiên quá mức đột nhiên, mọi người còn đang trong mờ mịt, vậy thì phát súng thứ hai tuyệt đối nhấc lên tác dụng uy hiếp cực lớn.\r\n\r\n- Ám khí gì?\r\n\r\nCó người thất thanh kinh hô, trong giọng nói bao hàm sợ hãi không gì sánh được.\r\n\r\n- Kinh Thiên Lôi của Giang Lam Phích Lịch Đường?\r\n\r\nĐường Tiểu Đông lắc lắc khẩu súng tự động MP5 trong tay, ha ha cười lớn:\r\n\r\n- Các ngươi đoán xem, đoán trúng có thưởng…\r\n\r\nKhóe mắt một tên bịt mặt trong đó chớp động hàn quang, tiếng ám khí phi hành cực nhanh, xé nát không khí truyền tới, Đường Tiểu Đông cả kinh, né tránh theo bản năng.', 1, 'phong-luu.jpg', 18, '2020-09-17 03:03:45', '1'),
(13, 'Mụ Phù Thủy', '\"Vì chúng tôi không tìm thấy người bà con nào, trong khi dường như ông là người liên lạc thư từ và là khách thăm duy nhất của bà ấy nên chúng tôi xin báo cho ông biết là bà Miriam Winters đã qua đời. Bà ấy chết thanh thản trong giấc ngủ vào ngày 25 vừa qu', 'Đọc lướt xong bức thư sau cùng, tôi lơ đãng bóc một bức thư mà người thư ký của tôi để nguyên chưa mở. Có lẽ cô ấy nghĩ đây là thư riêng nên để nguyên niêm. Không biết đầu óc để ở đâu, tôi quên không coi địa chỉ người gởi, vì thế nội dung bức thư làm tôi giật mình.\r\n\r\n\"Vì chúng tôi không tìm thấy người bà con nào, trong khi dường như ông là người liên lạc thư từ và là khách thăm duy nhất của bà ấy nên chúng tôi xin báo cho ông biết là bà Miriam Winters đã qua đời. Bà ấy chết thanh thản trong giấc ngủ vào ngày 25 vừa qua\".\r\n\r\nÁnh nắng đang chiếu qua mành cửa sổ văn phòng tôi bỗng trở nên lạnh lẽo. Tôi đang đứng khi mở bức thư, bây giờ, tôi đã ngồi xuống, xoay cái ghế bọc da một vòng, nhìn đăm đăm ra cửa sổ. Thế là, sau cùng, bà ấy đã chết. \"Khách thăm duy nhất\". Lạy Chúa, không hẳn thế ! Lần sau cùng gặp bà ấy là khi nào nhỉ ? Năm năm ? Sáu năm ? Tôi nhớ có nhận được một thiệp mừng Giáng sinh và bà ấy cứ nhắc đi nhắc lại là tôi phải viết thư cho bà ấy. Bà ấy cô đơn, cô đơn ghê gớm vào những năm cuối cùng trong đời, ắt hẳn thế. Lòng tôi bỗng tràn ngập một nỗi ân hận, thứ cảm giác mà bạn thấy khi một người đã chết mà chưa thu xếp xong những việc còn dang dở.\r\n\r\nTôi chỉ là một thằng nhóc mười sáu tuổi khi họ thả ông Winters và vì thế, tôi có trách nhiệm về việc giải cứu ông ta. Tôi đi lăng quăng mùa thu năm đó, vênh váo như một anh hùng đáng nguyền rủa. Tôi không ý thức được sự lố bịch của mình cho đến sau này. Tôi không trở lại Wilton Falls nhiều năm qua và tôi không biết họ có còn kể cho con cháu họ nghe chuyện xảy ra mùa thu năm ấy hay không. Tôi không biết họ vẫn cứ kể lại câu chuyện theo cách đó - Tạo ra một huyền thoại Miriam - Mụ phù thủy ? Vâng, họ lầm. Bà ấy không phải là phù thủy. Tôi đã được nghe bà ấy kể lại hết.\r\n\r\nXoay cái ghế bọc lại bàn giấy, tôi nhìn bức thư một lần nữa. Thật kỳ lạ ! Nhưng có thể tôi là người còn sống duy nhất đã nghe tất cả câu chuyện kể lại bởi chính bà ấy. Chắc chắn báo chí không cho bà ấy có cái quyền đó. Chắc chắn báo chí không bao giờ cho bà ấy có được dịp kể lại tất cả sự việc. Họ quá mê mải viết những bài giật gân về sự khủng khiếp bà ấy đã gây ra.\r\n\r\nQuả thật, đó là một chuyện khủng khiếp.\r\n\r\nTôi không bao giờ chối bỏ điều đó. Tôi cũng không tha thứ việc bà ấy đã làm. Nhưng định mệnh đã đưa vào tay tôi trọn vẹn tấn bi kịch mà không ai có được. Vì thế, tôi luôn luôn nghĩ khác về Miriam Winters.\r\n\r\n', 3, 'mu-phu-thuy.jpg', 19, '2020-08-31 11:17:03', '1'),
(14, 'Dạ Thoại: U Minh Quái Đàm', 'Chuyện kể về hai sinh viên đại học vào một buổi chiều tối, họ đến tìm giáo sư để xin chỉ giáo về lĩnh vực tâm lý sợ hãi của con người.', 'Edit: Shyn\r\n\r\nLời dẫn:\r\n\r\nKhi giáo sư Lan Thành mở cửa, đứng trước ông là hai nam sinh.\r\n\r\nÔng nhìn chăm chú vào hai chàng trai cao to trước mặt, ánh mắt sâu không lường được.\r\n\r\n“Giáo sư, thực xin lỗi thầy, không hẹn trước mà chúng em đã đến đây, thật sự là xin lỗi.” Trong đó, một nam sinh mặc áo sơ-mi kẻ ô vuông, vóc dáng hơi gầy không ngừng xoa xoa tay, có chút co quắp nói.\r\n\r\n“Nhưng thật sự là chúng em nhịn không được, không thể không đến thỉnh giáo thầy, xin thầy hãy tha thứ.” Một nam sinh thân hình cao to bổ sung thêm.\r\n\r\nGiáo sư đánh giá bọn họ vài giây, hơi mỉm cười:“Không có gì, mời vào!” Hai nam sinh ngồi lên sô pha ấm áp, giáo sư pha cho bọn họ hai cốc nước nóng, hai người nhanh chóng nhận lấy, liên thanh nói lời cảm ơn.\r\n\r\nNam sinh kẻ ô vuông nâng tay nhìn đồng hồ, hơi xin lỗi nói:“Giáo sư, bây giờ đã bảy giờ tối rồi, bọn em đến đây chắc hẳn đã quấy rầy giờ cơm chiều của thầy.”\r\n\r\nLan giáo sư ôn hòa lắc lắc đầu: “Thầy đã ăn rồi. Các em tìm thầy có chuyện gì?”\r\n\r\n“Là như thế này, thưa giáo sư. Hai chúng em đều là học sinh khoa Văn, thầy là giáo sư phụ trách bộ môn tâm lí học của chúng em... Đương nhiên, thầy đã gặp qua rất nhiều học sinh, có thể không có ấn tượng gì với bọn em...”\r\n\r\nNam sinh kẻ ô vuông tạm dừng một chút, nói tiếp:“Chúng em tìm đến thầy, là vì gặp một ít vấn đề phức tạp.”\r\n\r\nLan giáo sư gật gật đầu, ý nói loại chuyện này đối với ông đã quen lắm rồi, chẳng có gì lạ.\r\n\r\n“Đa số mọi người đến đây tìm thầy đều gặp những vấn đề.” Giáo sư ôn hoà nói.\r\n\r\nNam sinh kẻ ô vuông tựa hồ có chút khó nói:“Giáo sư, thầy biết đấy, hai chúng em thuộc khoa Văn, luôn nhiệt tình yêu thích sáng tác truyện trinh thám, tiểu thuyết khủng bố. Nhất là gần đây, chúng em tham gia một hiệp hội tiểu thuyết trinh thám. Ở nơi đó, cả một cộng đồng học sinh đam mê tiểu thuyết trinh thám cùng thảo luận, trao đổi. Vào cuối tuần, các thành viên trong hội lại tổ chức tụ tập. Dựa theo lệ thường, mọi người đều phải tự mình kể một câu chuyện khủng bố mới mẻ --”\r\n\r\nLan giáo sư gật gật đầu, ý bảo cậu ta tiếp tục nói.\r\n\r\nNam sinh kẻ ô vuông cắn cắn môi:“Mỗi tuần chúng em đều tổ chức hoạt động như thế, nhưng mỗi lần nghe chuyện xong đều cảm thấy bình thường, thậm chí đôi lúc còn cảm thấy buồn ngủ -- nhưng cuối tuần trước lại khác, bọn em đã sợ hãi, sợ hãi đến mức đêm không thể ngủ!”\r\n\r\nLan giáo sư sờ cằm:“Là vì có một người kể cho các em nghe một câu chuyện khủng khiếp, đúng không?”\r\n\r\nNam sinh kẻ ô vuông ngẩng đầu lên: “Không phải một, mà là ba.”\r\n\r\n“Thầy đoán tối hôm đó chắc chắn các em cảm thấy rất kích thích!” Lan giáo sư nhướn lông mi nói.\r\n\r\n“Đúng vậy, trên thực tế, tối hôm đó tất cả bọn em đều quên cả sự tồn tại của mình, hết sức chăm chú nhập theo tình tiết câu chuyện, tâm tình cũng theo đó mà phập phồng lên xuống. Đồng thời, thật sâutrong thâm tâm chúng em cảm thấy rất khiếp sợ, cảm giác khó có thể tin tưởng.”\r\n\r\n“Vì sao thế?” Lan giáo sư hỏi.\r\n\r\n“Bởi vì mỗi thành viên của hiệp hội chúng em đều hiểu rõ thực lực của nhau. Trước kia năng lực sáng tác của ba người đó rất bình thường, thậm chí còn có chút kém cỏi hơn người khác -- nhưng riêng buổi tối hôm đó, bọn họ lại bất ngờ kể ra ba câu chuyện khiến người ta kinh ngạc!”\r\n\r\n“Là ba chuyện gì?”\r\n\r\n“Để cho tớ nói đi,” Nam sinh cao to tiếp lời,“Tối hôm đó, đầu tiên có một bạn nữ lê thê kể câu chuyện trinh thám dài ngoằn, chán ngắc. Mọi người gần như đã cảm thấy buồn ngủ, đột nhiên một thành viên trong hội nói, cậu ta muốn kể một câu chuyện kích thích thần kinh mọi người, vì thế, cậu ta bắt đầu kể một câu chuyện mang tên ‘Ác mộng’.”\r\n\r\nNói tới đây, nam sinh cao lớn ngưng mắt nhìn thái độ của Lan giáo sư.\r\n\r\n“Nói tiếp đi.” Lan giáo sư khoanh tay trước ngực, thâm trầm nhìn cậu ta.\r\n\r\n“Sau khi cậu ta kể xong, bọn em vẫn chưa hết sợ hãi, một người khác lại bắt đầu kể một câu chuyện khác, gọi là ‘Phim kinh dị’—câu chuyện ấy khiến cho chúng em cảm thấy toàn thân phát lạnh. Ngay sau đó, câu chuyện thứ ba mang tên ‘Thế giới Doir’ được một thành viên khác kể ra càng làm cho mọi người hoảng sợ -- nghe xong câu chuyện đó, tất cả đều ngây người, vài người đã chuẩn bị xong câuchuyện của mình cũng không kể tiếp vì sợ thua kém.”\r\n\r\n“Sau tối hôm đó, hai chúng em suy nghĩ nát óc vẫn không nghĩ ra được, làm sao bọn họ có thể sáng tác ra câu chuyện ấy. Cho nên, ngày nào hai chúng em cũng đi theo họ, muốn bọn họ truyền thụ kinh nghiệm sáng tác. Bọn họ bị ép đến không còn cách nào khác, rốt cuộc cũng thừa nhận – ba chuyện này nghe được từ chỗ Lan giáo sư -- Giáo sư, là như vậy sao?”\r\n\r\nLan giáo sư thản nhiên cười, hơi hơi gật đầu: “Đúng vậy, là thầy kể cho bọn họ nghe. Nhưng thầy không rõ, ‘vấn đề phức tạp’ của các em là gì?”\r\n\r\n“Giáo sư, chúng em đến đây, chỉ hy vọng được thầy dẫn dắt chút ít. Nếu thầy có thể kể cho bọn họ nghe một ít chuyện kinh dị, nhất định thầy cũng có thể nói cho chúng em biết – làm thế nào có thể thăm dò và kích thích sự sợ hãi của người khác? Cái gì gọi là tâm lí sợ hãi của con người?”\r\n\r\nLan giáo sư nhìn cậu ta chăm chú một lát, lập tức bật cười ha ha.\r\n\r\n“Em biết không? Về vấn đề em vừa nêu ra, nếu dùng căn nguyên lý luận, hệ thống giảng giải mà nói, có thể vận dụng tâm lí học mà viết.” Ông nói.\r\n\r\n“Ý thầy là...... Vấn đề này quá mức phức tạp?” Nam sinh cao to có chút lo lắng hỏi.\r\n\r\nLan giáo sư nhẹ nhàng mà khoát tay áo: “Trước khi chúng ta giải quyết bất cứ vấn đề gì, đều phải xem mục đích ban đầu. Trở lại vấn đề các em đưa ra ban đầu – mục đích của các em là vừa viết vừa trì hoãn, lại khiến cho người ta sinh ra sợ hãi, đúng không?”\r\n\r\nHai người không hẹn mà gật đầu.\r\n\r\n“Vậy thì đơn giản thôi.” Giáo sư nói,“Căn bản các em không cần phải hiểu hết về tâm lí của sợ hãi của nhân loại, chỉ cần nghe thầy kể xong ba câu chuyện là được.”\r\n\r\n“Giáo sư!” Hai nam sinh hưng phấn đứng lên,“Thì ra còn có ba câu chuyện khác mà chúng em chưa được nghe sao?”\r\n\r\nTrên mặt Lan giáo sư mang theo nụ cười thần bí, nhẹ nhàng gật đầu.\r\n\r\n“Quá tốt, giáo sư. Xin thầy hãy kể đi!” Hai nam sinh hết sức chăm chú, ngồi nghiêm chỉnh không nhúc nhích nhìn Lan giáo sư.\r\n\r\n“Nhưng thầy xin nói rõ ràng trước, ba câu chuyện này rất đáng sợ, trong quá trình nghe sẽ khiến người ta sinh ra khẩn trương, tâm trạng lo âu, không thoải mái -- các em chắc chắn muốn nghe sao?”\r\n\r\n“Đương nhiên! Giáo sư, điều chúng em cần chính là có thể sinh ra linh cảm được từ những câu chuyện ấy.” Nam sinh kẻ ô vuông bức thiết nói.\r\n\r\n“Như vậy, thầy cũng có một yêu cầu duy nhất – sau khi các em nghe xong ba câu chuyện này, nếu xuất hiện bất cứ tình huống nào, hoặc phát sinh bất cứ chuyện gì, đều không liên quan đến thầy -- bởi vì các em yêu cầu thầy kể. Đúng không?”\r\n\r\nHai người liếc mắt nhìn nhau, nam sinh cao to hơi hơi nhíu nhíu mày, nói:“Giáo sư, chúng em đã nghe qua ba câu chuyện của các bạn kia, hơn nữa cũng đã đọc rất nhiều câu chuyện kinh dị của các quốc gia khác... Lần này thầy kể ba câu chuyện, thật sự có thể đạt đến hiệu quả đấy sao?”\r\n\r\nLan giáo sư từ chối cho ý kiến, nói:“Thầy chỉ có thể nói cho em biết, ba câu chuyện này không hề giống ba câu chuyện trước. Trước khi kể, thầy sẽ không đánh giá bất cứ điều gì, các em lựa chọn nghe hay không nghe đây.”\r\n\r\nHai nam sinh lại nhìn nhau, cuối cùng đưa ra quyết định, nói: “Nghe!”\r\n\r\n“Tốt lắm.” Lan giáo sư gật đầu, “Quay lại yêu cầu duy nhất của thầy, ba câu chuyện này không nhất định thầy phải kể hết nhé.”\r\n\r\n“Vì sao ạ?”\r\n\r\n“Trong quá trình thầy kể, thầy sẽ quan sát các biểu hiện, động tác của các em. Nếu thầy phát hiện câuchuyện thứ nhất làm các em sợ hãi, thầy sẽ không tiếp tục kể câu chuyện.”\r\n\r\n“Vậy...... Nếu chúng em không bị dọa thì sao?” Nam sinh cao to hỏi.\r\n\r\n“Thầy sẽ kể câu chuyện thứ hai.”\r\n\r\n“Có thể câu chuyện thứ hai cũng không thể dọa bọn em.”\r\n\r\n“Nếu như vậy, thầy sẽ kể câu chuyện thứ ba.”\r\n\r\nNam sinh kẻ ô vuông trầm mặc một lát, sau đó nói:“Giáo sư, tổng cộng thầy chỉ có ba câu chuyện. Em muốn biết là, nếu ba câu chuyện ấy đều không dọa được bọn em, thì sẽ thế nào? Thầy biết đấy, hai chúng em đều nổi tiếng rằng rất gan dạ.”\r\n\r\nCậu ta nói xong câu đó, ngẩng đầu nhìn về phía Lan giáo sư, trong ánh mắt ngây ngô còn chứa một tia khiêu khích.\r\n\r\nLan giáo sư dùng tay trái nâng cằm, nhìn cậu ta một lát, chậm rãi nói:“Nếu như vậy, thầy sẽ kể câuchuyện thứ tư, cũng chính là câu chuyện cuối cùng của thầy.”\r\n\r\n“Thì ra, thầy còn có câu chuyện thứ tư!” Nam sinh cao to ngạc nhiên hô lên.\r\n\r\n“Nhưng câu chuyện thứ tư thầy chưa từng kể cho ai, bởi vì người bình thường nhiều nhất chỉ kiên trì nghe được câu chuyện thứ ba, sau đó chịu không nổi, không muốn nghe tiếp nữa. Cho nên, cho đến bây giờ thầy vẫn chưa kể cho ai nghe câu chuyện thứ tư cả.”\r\n\r\nHai nam sinh nhẹ nhàng há miệng thở dốc, không nói gì. Trong lòng bọn họ suy đoán câu chuyện thứ tư sẽ có nội dung gì.\r\n\r\nVài phút sau, ánh mắt nam sinh kẻ ô vuông sáng ngời nhìn Lan Thành giáo sư, nói:“Giáo sư, xem ra buổi tối hôm nay, mục đích của chúng em đến đây là để khiêu chiến với “câu chuyện thứ tư” của thầy.”\r\n\r\nLan giáo sư vẫn duy trì nụ cười ý vị thâm trường, ông nói: “Vậy được rồi, thầy sẽ bắt đầu kể câu chuyện thứ nhất. Nếu trong khi nghe các em cảm thấy sợ hãi, có thể bảo thầy dừng lại, thầy sẽ không kể tiếp nữa. Hiểu chưa?”\r\n', 3, 'da-thoai-u-minh-quai-dam.jpg', 20, '2020-08-01 11:17:03', '1'),
(15, 'Bóng Người Dưới Trăng', 'Vẫn như những câu chuyện trước, vẫn phong cách hành văn bình dị gần gũi nhưng cách bố cục và sắp sếp chi tiết khéo léo khiến câu chuyện mang màu sắc thần bí, hơi hướm kinh dị', 'Chánh Tổng Hoán, ngay khi còn tại chức đã có tiếng là người khắc nghiệt, cái khắc nghiệt đến độ độc ác của ông thể hiện qua những hành vi diễn ra từng ngày, nhưng ông ko thấy đó là một khuyết điểm mà trái lại ông luôn tự hào cho rằng mình oai nghiêm và có uy quyền.\r\n\r\nVới dân chúng, với kẻ ăn người ở trong nhà ông nhẫn tâm đã đành mà ngay cả với vợ con, ông cũng cứng rắn như gỗ đá, lạnh lùng như người dưng nước lã, ko để lộ chút tình cảm riêng tư nào.\r\n\r\nDường như ông có cái thú được người ta sợ mình vì ông coi đó là một cái uy của một người vừa có chức, vừa có tiền, tha hồ tác yêu tác quái ở vùng nông thôn giữa hàng ngàn người dân thấp cổ bé miệng.\r\n\r\nTuổi chưa đến 50, nhưng ông khệnh khạng đóng vai già trước tuổi, và cả Tổng bất cứ ai chẳng may gặp ông, đều phải đoán ý ông mà chắp tay vái chào từ đằng xa cho phải phép:\r\n\r\n- Con xin phép lạy cụ Chánh ạ.........ạ........\r\n\r\nLời chào ấy ông nghe quen tai lắm, cho nên vô phúc cho ai tình cờ gặp ông ngoài đường mà lại tỏ ra thân mật, quên ko gọi ông bằng cụ: cụ Chánh hoặc cụ Tổng. Chẳng hạn anh Hào con ông thợ nung gạch trong làng, một hôm vác cuốc ngoài đồng trở về, gặp ông Chánh giữa đường, anh bỏ nón, hạ cuốc cuối đầu chào lớn:\r\n\r\n- Ông Chánh đi dạo mát ạ, ông Chánh xơi cơm chưa ạ?\r\n\r\nÔng Chánh coi đó là một lời bất kính, cho nên lập tức ông trừng mắt, vẫy anh Hào lại gần, rồi bất chợt tát cho một cái nảy đom đóm mắt và mắng:\r\n\r\n- Bố mày cũng ko được gọi tao bằng ông, huống chi là mày. Quân láo lếu, ko biết trên, biết dưới gì cả. Lần sau còn thế tao bỏ tù nghe chửa?\r\n\r\nAnh Hào ríu rít vâng dạ hai ba lần rồi xin lỗi bước đi, mặc dầu anh chưa biết mình có lỗi gì.\r\n\r\nKể ra người ta sợ ông Chánh cũng phải. Dạo còn tại chức, có lần ông lên hầu Quan Huyện để nộp tiền cho Quan ăn tết. Lúc trở về, trời cuối năm rét căm căm, lại gặp cơn mưa phùn lê thê. Ông Chánh ngồi chểm chệ trên xe, anh phu xe gò người kéo đi ngược chiều gió khiến sức nặng phốp pháp của ông càng nặng thêm gấp bội. Bất chợt anh phu xe vấp chân vào cục đá trên đoạn đường trơn làm anh ngã chúi xuống. Hai càng xe chống mạnh xuống đất, ông Chánh cũng suýt lộn cổ té theo anh. Anh luống cuống đứng dậy nâng hai càng xe lên đồng thời van lạy xin lỗi ông Chánh, nhưng cơn thịnh nộ đã nổi lên theo thói quen. Ông Chánh cầm ba toong quật lia lịa xuống đầu anh. Cái ba toong biểu lộ sự uy quyền của ông bằng gỗ mun, nặng và cứng như thép, lại bọc sắt ở đầu, nên vô phúc cho ai bị ông xuống tay.\r\n\r\nĐánh người như thế là chuyện bình thường, ông chỉ định ra oai thôi chứ ko có chủ ý giết người, nhưng ko ngờ anh phu xe ngã vật xuống, giẫy giụa mấy cái, máu chảy chan hoà lẫn với nước mưa, chết luôn trên vũng bùn. Án mạng nặng như thế mà ko sao cả, ông Chánh chỉ tốn mất tiền lớn cho Quan Huyện để Quan ra bản án gán cho anh phu xe là người của hội kín, chống nhà nước bảo hộ, âm mưu ám sát ông Chánh Tổng, thế là xong.\r\n\r\nThời ấy biết bao nhiêu người đi tù oan hoặc biệt xứ vì cái tội \" giả tưởng \" là nấu rượu lậu hoặc tệ hơn là làm loạn chống Pháp. Uy quyền ông Chánh càng lớn hơn sau khi giết anh phu xe mà ko bị giam ngày nào, cũng ko mất chức Chánh Tổng. Tiếng đồn về thân thế của ông càng lan rộng hơn khiến người ta càng sợ. Và ai bất hạnh lắm mới phải khúm núm đến gõ cửa nhà ông nhờ vả chuyện giấy tờ hoặc kiện cáo.\r\n\r\nÔng Chánh cứ sống ung dung, nhà cao cửa rộng, kẻ hầu người hạ mà hưởng lộc của thiên hạ giữa bao nhiêu dân làng cùng khổ xung quanh.', 3, 'bong-nguoi-duoi-trang.jpg', 10, '2020-09-01 11:20:43', '1'),
(16, 'Cửu Biện Liên', 'Sau khi cho tôi một thanh chủy thủ bằng gỗ đào, cho Huyền Kỳ một chuỗi Phật châu, ông nội đột ngột qua đời. Sau đó, một người tên Vu Dương đã xuất hiện. Người đó có đôi mắt màu vàng kim, còn tự xưng mình là một yêu quái. Anh ta nói cho chúng tôi biết, ông', 'Kì nghỉ hè năm ấy, ông nội qua đời.\r\n\r\nỞ lễ tang, chúng tôi cố sức núp trong một góc tường, nhưng vẫn không có cách nào tránh được sự tò mò của những người xung quanh, có người mang ánh mắt dò xét, có người thậm chí còn đặc biệt tới “tham quan”. Trong mắt họ, ông nội là một người vừa kì lạ vừa thần bí, mà mấy đứa trẻ được ông nuôi dưỡng như chúng tôi, cũng được xem như một đôi chị em kì lạ thần bí không kém.\r\n\r\nCha tôi có một anh trai, một em trai. Cha mẹ qua đời, trong lúc họ đang thảo luận nên sắp xếp cho chúng tôi thế nào, vẫn chỉ đang lo cho thân mình, ông nội vốn ít lui tới với chúng tôi bỗng nhiên xuất hiện, lại còn thu dưỡng chúng tôi.\r\n\r\nNơi ông nội ở là nhà tổ của gia tộc, ngoài nhà chính, phòng bếp và phòng vệ sinh, còn có bốn năm gian phòng khách, gần đó là một khoảng sân nho nhỏ. Còn nhớ khi còn bé, mỗi tối không khí đều vô cùng náo nhiệt, rất nhiều bạn nhỏ sẽ đến cùng nhau chơi đùa, nhưng ông nội lại rất căng thẳng, lần nào cũng thế, ông không sợ người khác phiền cứ dặn dò: “Nếu nghe có người gọi tên con, không được đáp lại, càng không thể quay đầu lại.”\r\n\r\nLúc đầu tôi cũng không cho là đúng, cho đến một lần kia, không biết là ai đã hà hơi vào ót tôi, tôi vừa quay đầu lại, lập tức, đầu óc liền choáng váng, ngay sau đó cảm thấy toàn thân từ trên xuống dưới đều đau đớn đến tận tim gan, tôi như lơ lửng giữa không trung, nhìn bản thân mình té “bịch” xuống đất, rất sợ hãi, muốn khóc, nhưng lại không thể phát ra bất cứ âm thanh nào, cũng không hề có giọt nước mắt nào. Ông nội đứng bên dưới, tay lóe ánh lửa, sau đó, tôi không còn biết gì nữa. Khi tỉnh lại, tôi đang nằm trên đất, em trai đang gào khóc bên cạnh, vừa khóc vừa nói: “Em không quay đầu lại! Em không quay đầu lại!”\r\n\r\nNếu không để ý, “bọn họ” sẽ không thể phát hiện ra cháu có thể nhìn thấy. Đây là những điều mà ông nội thường nói. Chúng tôi cũng dần thích ứng với cuộc sống như thế.\r\n\r\nHơi lớn hơn một chút, hẳn là năm thứ nhất trung học, ông nội dẫn chúng tôi đến nơi mà cha mẹ đã qua đời — một vùng núi vô cùng xinh đẹp, con đường cái dẫn lên núi chỉ vừa đủ cho hai chiếc xe buýt giao nhau suýt sao. Người tài xế đưa chúng tôi đến đây nói, kể từ khi xảy ra sự việc kia, mười năm qua, sự cố xảy ra càng lúc càng thường xuyên, có lúc, dù chỉ là chạy xe đạp ngang qua, cũng có thể vô duyên vô cớ bị lật xe té, khiến lời đồn đãi ma quái nổi lên bốn phía, con đường này bất đắc dĩ bị phong tỏa.\r\n\r\nÔng nội dẫn chúng tôi đi, dọc theo đường đi, có thể tùy ý thấy được mấy oán linh chết yểu, bồi hồi đứng cách đó mấy bước, dù trông rất hung hăng nhưng lại không dám đến gần. Đi đến giữa sườn núi, ông nội bỗng dừng lại, nhìn một đôi nam nữ đang đứng khóc bên ven đường.\r\n\r\n“Còn tâm nguyện chưa dứt sao?” Ông hỏi.\r\n\r\n“Không nhớ được rồi….không nhớ được rồi….” người con gái ngẩng đầu, mờ mịt lặp đi lặp lại mấy lời này.\r\n\r\n“Nhìn đi, đây là Thanh Loan.” ông nội đẩy tôi về trước một chút.\r\n\r\nTrong lòng tôi mơ hồ nửa hiểu nửa không.\r\n\r\n“Đây là Huyền Kỳ.” Ông lại đẩy em trai về phía trước.\r\n\r\nGương mặt máu thịt mơ hồ, ánh nhìn như chết lặng, nhưng hai người họ lại nhìn chúng tôi một lúc lâu. Lòng tôi chấn động. Tôi hoàn toàn không sợ, chỉ là khó chịu đến muốn khóc.\r\n\r\nSau khi trở về tôi mới biết. Xe buýt chở cha mẹ chính là bị lật tại chính sườn núi này. Ba mươi người trên xe, toàn bộ không ai sống sót.\r\n\r\nTừ đó về sau, cuộc sống cứ trải qua yên bình như thế. Giờ đây, khi nhớ đến buổi chiều hôm ấy cũng không hề cảm thấy có điều gì kì lạ.\r\n\r\nHôm đó rất không thoải mái. Từ sáng sớm tôi đã bắt đầu cảm thấy vừa ẩm ướt vừa nóng bức. Thế mà đợi mãi trời cũng chẳng đổ mưa. Ông nội gọi chúng tôi đến ngồi ở phòng khách, sau đó lặng yên nhìn chúng tôi thật lâu\r\n\r\n“Ông nội, lại muốn ra ngoài nên cho bọn con nhìn ông cho đã, đỡ đến lúc đó lại nhớ ông sao?” Huyền Kỳ còn nói với ông như thế.\r\n\r\nÔng bỗng nhiên thở dài thườn thượt, trịnh trọng đưa cho tôi một thanh chủy thủ bằng gỗ đào, cho Huyền Kỳ một chuỗi Phật châu.\r\n\r\n“Giữ làm bùa hộ mệnh đi, sau này phải cẩn thận.” Nói rồi, ông trở về phòng sách.\r\n\r\nTôi và Huyền Kỳ hai mặt nhìn nhau, muốn đi hỏi ông, nhưng lại không hỏi được điều gì.\r\n\r\nMột tuần sau, ông qua đời.', 3, 'cuu-bien-lien.jpg', 20, '2020-08-04 11:20:43', '1'),
(17, 'Cô Dâu Thứ Bảy', 'Một câu chuyện tình yêu giữa người và ma. Khi cô thay chị mình gả cho hắn, chỉ vì hắn đã là người chết.\r\n\r\nNgày 15/7 âm lịch......', 'Ngày 15/7 âm lịch......\r\n\r\nCô bước chân lên xe hoa về nhà chồng, nhà chồng cô chưa một lần biết mặt, chưa một lần nhìn thấy\r\n\r\nCô chỉ nghe nói nhà họ rất giàu, rất hưng thịnh, đáng lẽ người được gã đi là chị gái của cô, nhưng chị cô đã quỳ xuống van xin cô, mong cô sẽ thay mình gã đi\r\n\r\nGia đình chồng giàu có, có tiếng tăm, có thể nói là danh tiếng lẫy lừng, chị cô cũng thuộc dạng ham hư vinh, ham lợi lộc, được gã vào nhà đó có thể gọi là \"chuột sa hũ nếp\" nhưng vì sao chị cô không muốn gã?\r\n\r\nLí do duy nhất khiến chị cô chùng bước chỉ có một\r\n\r\n\"Chú rễ là một người đã Chết \"\r\n\r\nCô và chị cô là chị em sinh đôi, từ nhỏ cô đã mắc bệnh tim bẫm sinh, còn chị cô sức khỏe lại bình thường, ba mẹ ruột chỉ yêu thương mỗi chị cô, còn cô chính là gánh nặng nên từ nhỏ đã bị ghét bỏ.\r\n\r\nLần này cô vì thương ba mẹ, thương chị nên đành thay chị gã vào nhà đó, do hai chị em sinh đôi nên gương mặt giống nhau, cô thay chị cô gã vào nhà đó họ cũng chẳng thể phát hiện ra.\r\n\r\nSức khỏe cô suy giảm, bệnh tình càng lúc càng nặng, những đợt điều trị đắt đỏ tại bệnh viện khiến cô cảm thấy mình thật sự là gánh nặng của gia đình, chị cô có thể kiếm tiền thay cô chăm sóc ba mẹ, còn cô sẽ thay chị gã đi, dù sao không bao lâu cô cũng phát bệnh mà chết\r\n\r\nCô nghĩ thế cũng cắn răng mà leo lên xe hoa, mặc dù cô biết chú rể của cô bây giờ chỉ là một di ảnh\r\n\r\nGia đình hắn giàu có, hắn ăn chơi trác tán, hắn chết trong một lần đua xe tại trường đua, hưởng dương 21 tuổi, ba mẹ hắn thương con còn trẻ mà đoản mệnh, dựa vào thân thế đồ sộ và danh tiếng tìm cho hắn một người vợ.\r\n\r\nTìm thầy bói tìm xem ai hợp tuổi rồi đến nhà cưới hỏi đàng hoàng, linh đình\r\n\r\nNhưng nhiều lần cưới vợ cho hắn tất cả các cô dâu qua đêm động phòng đều phát điên, đã 6 lần như thế, và bây giờ cô là người thứ 7', 3, 'co-dau-thu-bay.jpg', 10, '2020-08-03 11:23:12', '1'),
(18, 'Khách Sạn Hoàng Tuyền', 'Thế giới tâm linh chứa đựng muôn vàn điều huyền bí mà khoa học hiện đại chưa thể nào giải thích hết được… cũng như không ai có thể thấu hiểu hết tất cả những bí mật, tâm tư và bản chất của một con người.', 'Quyển 1: HUNG KẾ\r\n\r\nChân thật và hư ảo ở ngay bên rìa ánh sáng và bóng tối.\r\n\r\n****\r\n\r\nBầu trời đen kịt, ngay cả trăng sao cũng có vẻ mờ ảo khác thường, vậy mà khuôn viên mấy trăm dặm ở phía dưới lại chẳng hề có một ngọn đèn.\r\n\r\nKhông gian tối tăm ảm đạm.\r\n\r\nNếu như đứng đủ cao sẽ phát hiện ra áng mây tía lấp ló nơi cuối chân trời, khí nóng hừng hực tạt vào mặt, nhưng nơi này giống như một thế giới khác, ngăn cách với thế giới thường bởi một lớp kết giới trong suốt vô hình, xa xôi vô tận mãi chẳng thấy đích. Nếu bị nhấn chìm ở nơi sâu nhất của bóng tối, bạn sẽ biến mất một cách cô độc lặng lẽ, cũng không ai có thể nhìn thấy được bạn.\r\n\r\nNỗi sợ hãi lớn nhất trên thế gian chính là như vậy.\r\n\r\nKhông phải là đẫm máu cỡ nào, tàn khốc cỡ nào và đau đớn cỡ nào, mà là khi bạn đang sợ, đang điên cuồng gào thét, đang cầu xin sự giúp đỡ, bạn muốn liều mình thoát khỏi nhưng lại tốn công vô ích. Giống như rơi vào một cơn ác mộng kinh khủng, một ảo giác kỳ dị, hay giống như bị thây ma vây kín trong một bãi tha ma, chỉ có một mình bạn, một mình bạn nếm trải nỗi cô độc và tuyệt vọng.\r\n\r\nPhần mộ cũng giống như ngôi nhà, có điều là nó dành cho người chết và không có hơi thở của người sống. Nói như vậy, một khu cư xá to lớn không có ai ở thì khác gì một bãi tha ma? Nhưng mà nó lại đáng sợ hơn thế nữa, bởi vì càng tối tăm, càng trống trải thì bên trong càng có thể giấu giếm nhiều thứ.\r\n\r\nHàn Băng không biết tại sao lại nảy sinh ý nghĩ này, trong lòng cô kinh hãi. Cô nhắc nhở mình đừng suy nghĩ bậy bạ nữa, bởi vì nghe nói khi sóng não hoạt động kịch liệt sẽ dễ dàng hấp dẫn một số hiện tượng không phải tự nhiên.\r\n\r\nNhưng mà cô không khống chế được.\r\n\r\nCô đâu muốn chạy qua thành phố Ma nổi tiếng này vào lúc mười hai giờ đêm như thế này đâu, bởi vì đoạn đường cao tốc gần đó xảy ra tai nạn nghiêm trọng, bị phong tỏa. Cho nên, hoặc là cô bị kẹt cứng trên đường quốc lộ, hoặc là đi vòng qua bên này để về nhà.\r\n\r\nCô lại không muốn bị kẹt trên đường quốc lộ, vì trước đây, cô từng nghe nói lực lượng cảnh sát ở đây ít quá nên thỉnh thoảng xảy ra vài vụ cướp. Tuy không có tiền, nhưng dù gì cô cũng có chút nhan sắc, cô thà mạo hiểm đi qua thành phố Ma còn hơn.\r\n\r\nCó điều, bây giờ lòng tin của cô hơi dao động. Hay là đi đường quốc lộ sẽ an toàn hơn nhỉ?\r\n\r\nNhưng cô đã không có đường quay lại nữa, đành phải bật đèn pha xe ô tô lên để chiếu sáng khoảng không phía trước dù cho ánh sáng không cách nào xuyên thấu màn đêm mịt mùng, thì ít nhất nó cũng cho cô cảm giác bình an ngắn ngủi.\r\n\r\nThật ra thành phố Ma thật sự không phải là thành phố có ma thoắt ẩn thoắt hiện, mà nó chỉ là một khu dân cư được xây dựng tại khu vực tiếp giáp giữa vùng nông thôn và thành thị. Tất cả nhà cửa đều được xây theo kiểu biệt thự đẹp đẽ. Đáng tiếc, giao thông không phát triển, sinh hoạt không thuận tiện, giá nhà lại cao ngất, nên ít người mua. Hơn nữa, cũng bởi diện tích khu vực này quá lớn, dân cư thưa, thật sự không an toàn nên số gia đình ít ỏi nơi đây cuối cùng cũng dọn đi, khiến khu đất rộng mênh mông vài dặm này không có lấy một bóng người.\r\n\r\nMấy cánh cửa sắt ra vào khu dân cư đều đã xập xệ, cũng không có ai trông coi. Nếu băng qua khu vực này để từ thành phố T đến thành phố B lân cận, thì quãng đường đi được rút ngắn rất nhiều. Mặc dù vậy, vào ban ngày cũng chỉ thi thoảng mớí có vài người qua lại nơi này mà thôi, còn đến tối thì ngay cả một bóng ma cũng không có. Có nơi cỏ hoang đã phủ dầy kín mít, nếu có đoàn phim đến đây quay Liêu Trai thì không cần phải mất công dựng cảnh nữa.\r\n\r\nCho nên, nơi này trở thành thành phố Ma nổi tiếng gần xa.\r\n\r\nKhông sao, không sao, không có ai ở thôi mà, lái xe nhanh một chút, dù là kẻ bắt cóc cũng không đuổi kịp.\r\n\r\nHàn Băng tự khích lệ mình, nhưng lòng bàn tay lại đó mồ hôi lạnh đầm đìa. Vừa trượt tay xe đã suýt đụng phải vành đai xanh, cô vội vàng dấn mạnh thắng xe khiến xe đột ngột dứng lại, giống như một cái chết bất ngờ.\r\n\r\nĐây là xe của sếp cô, bởi vì cô phải đưa ông ta về nhà ở thành phố T nên mới bị buộc phải lái xe trở về thành phố B lúc đêm hôm khuya khoắt thế này. Hơn nữa tay lái của chiếc xe này quá nhạy, cô vẫn chưa quen. Còn chưa hết bàng hoàng, cô cố gắng nổ máy lần nữa phát hiện xe bị chết máy.\r\n\r\nCô đưa tay về phía chìa khoá theo bản năng…\r\n\r\nPhù!\r\n\r\nCó một tiếng thở nhẹ nhàng vang lên phía sau tai trái cô, gần trong gang tấc như đang kề sát bên tai cô. Nó tựa như một tiếng thở dài yếu ớt, lại tựa như có thứ gì đó đang phả hơi vào người cô.\r\n\r\nTrong nháy mắt, cô lạnh toát cả người, hoàn toàn cứng đờ không thể nhúc nhích.\r\n\r\nMấy người già thường nói trên cơ thể người lớn có ba đốm lửa, chúng ở đỉnh đầu và hai vai, gặp chuyện quái dị đừng quay đầu lại, lửa có thể bảo vệ ta bình an. Nhưng lúc này dường như trên gáy có một sợi dây quấn lấy cô, Hàn Băng quay đầu lại theo bản năng…\r\n\r\nChỗ ngồi phía sau trống không.\r\n\r\nXuyên qua cửa kính phía sau xe là màn đêm đen như mực, tựa như tấm mành đen đang ép về phía cô.\r\n\r\nCô chợt quay người lại, nơi khoé mắt dường như thoáng thấy một bóng đen vút qua truớc đầu xe với tốc độ cực nhanh. Giống như bóng mờ do áng mây trôi ngang qua mặt trăng tạo nên, lại như hình ánh lay động do gió thổi. Lại càng giống như… một góc áo choàng đen khổng lồ của ai đó.\r\n\r\nThành phố Ma rộng lớn nhu vậy, trời đêm đen như mực và một mình cô ở đó.\r\n\r\nTừ xa đến gần, khu nhà cao cấp trông hoặc như một cửa động tối đen. Có thứ gì đó, không thể đặt tên đang núp trong bóng tối theo dõi, có thể tha cô đi bất cứ lúc nào. Xung quanh đột nhiên yên tĩnh, ngay cả tiếng côn trùng chim chóc thường rả rít giữa đêm hè cũng im bặt.\r\n\r\nCả khuôn viên như vùng đất chết.\r\n\r\nNgoại trừ tiếng thở vừa rồi vẫn không ngừng văng vẳng bên tai.\r\n\r\nCô hoảng sợ, chỉ cảm thấy tim đập như trống vỗ, máu toàn thân như bị rút hết, trong lòng trào dâng nỗi sợ hãi tột cùng, bức bách khiến cô khó thở. Ngay cả trái tim cũng nổ tung như pháo hoa, muốn lao ra khỏi lồng ngực.\r\n\r\nChạy đi! Chạy đi! Chay đi!\r\n\r\nTrong đầu cô chỉ lóe lên một ý niệm này, cô ra sức nổ máy xe.Chiếc xe hơi sang trọng trị giá cả triệu chẳng thể nào vô duyên vô cớ chết máy, dấn đề mãi cũng không lên. Trong không khí ẩm ướt lạnh lẽo bao phủ, chiếc xe vẫn bất động.\r\n\r\n“Các vị đại tiên, van xin các vị thả tôi đi đi.” - Cô khẽ khẩn cầu.\r\n\r\nCạch!\r\n\r\nĐáp lại cô là tiếng động khác thường vang lên phía trước xe. Âm thanh vang lên lanh lảnh, chói tai và rõ ràng trong tiếng hô hấp dồn dập và tiếng tim đập như đánh trống của Hàn Băng. Hàn Băng sợ đến mức suýt nữa hét toáng lên, lúc cô ngẩng đầu nhìn lên lại phát hiện tấm kính chắn gió xuất hiện một vết nứt nho nhỏ.\r\n\r\nKhông một bóng người, không một dụng cụ nào, hay cát đá bị gió thổi cuốn lên, thế thì tại sao một tấm kính chắn gió lại xuất hiện một vết nứt nho nhỏ.\r\n\r\nTinh thần cô đang bấn loạn thì tiếng cành cạch kéo dài vang lên càng chói tai hơn. Kèm theo đó là từng vết nứt lần lượt xuất hiện trên tấm kính, nhanh chóng tạo thành một chiếc mạng nhện trên tấm kính chắn gió. Giống như có thứ ẩn hình gì đó cầm vật cứng sắc nhọn không ngừng nện vào, muốn đập bể tấm kính xông vào trong xe.\r\n\r\nHàn Băng sợ chết cứng, mồ hôi lạnh tuôn ra như tắm.\r\n\r\nNỗi sợ hãi giống như móng vuốt của ma quỷ bóp chặt cổ họng cô. Cô không thể hít thở, chỉ có thể chạy trốn theo bản năng. Xung quanh hoang vu không một bóng người, có lẽ trong bụi cây um tùm kia sẽ xuất hiện việc khủng khiếp nào đó đang chờ Hàn Băng. Nhưng cô không có lựa chọn nào khác.\r\n\r\nNhưng mà, cô không thể hành động theo ý muốn của mình, cô quên mất xe tự động khoá kín, ngay cả khi cô tông mạnh vài cái, cửa xe cũng không mở. Mà tiếng nện kia càng lớn thì số vết nứt xuống hiện trên tấm kính chắn gió càng nhiều thêm. Cửa kính nhanh chóng trắng xoá một mảng, ngăn cách người ngồi trong với bóng đen bên ngoài.\r\n\r\nHàn Băng nhìn chăm chăm phía trước, sợ đến mức không thể cử động.\r\n\r\nTrong mơ hồ, dần dần cô thấy được khuôn mặt một cô gái hiện ra mập mờ, mặt mũi nhăn nhó dữ tợn, nửa khuôn mặt bị máu bao phủ, ánh mắt đầy nỗi tuyệt vọng. Cô ta đang cầm giày cao gót nện mạnh lên tấm kính xe.\r\n\r\nCứu mạng… Cứu mạng…\r\n\r\nGiọng cô gái the thé vang lên trước mặt mà lại xa xăm.\r\n\r\nNhưng ngay sau đó một tiếng “xoảng” điếc tai vang lên, cuối cùng thì tấm kính chắn gió đã vỡ vụn.\r\n\r\nGió đêm hầm hập vừa nhớp nháp xen lẫn không khí ngột ngạt pha lẫn tanh tưởi, khiến người ta bất giác cảm thấy buồn nôn.\r\n\r\nCô gái kia đưa tay chụp lấy Hàn Băng.\r\n\r\nHàn Băng khiếp sợ, cô hoàn toàn không thể trốn tránh. Nhưng cô gái kia đột nhiên biến mất trước mặt Hàn Băng hệt như bị thứ vô hình nào đó bắt đi, chỉ còn lại muôn dạng ánh mắt hoảng sợ của cô gái ấy không ngừng phóng đại trong đầu Hàn Băng. Ngay sau đó, một quả cầu lửa cháy bùng bất chợt phát sáng thành phố Ma âm u này, tựa như có thứ gì đó bị nổ tung. Không có tiếng nổ nhưng lại có tiếng thét gào đáng sợ khôn tả vang lên trong đêm tối.\r\n\r\nHàn Băng chỉ cảm thấy có thứ gì đó xông về phía mình, đâm vào thân thể cô, thâm nhập vào đầu cô.\r\n\r\nCơn đau đớn kịch liệt không biết từ đâu đến khiến cô gắng gượng mình cựa quậy. Một tay cô ôm lấy đầu, tay kia quơ quào lung tung. Trong khoảnh khắc đó, không biết vô tình chạm phải vật gì mà chiếc xe đột nhiên khởi động, lao đi như một mũi tên, dường như ngay cả nó cũng đang sợ hãi khôn cùng.\r\n\r\nChiếc xe rung lắc khiến Hàn Băng thoáng tỉnh táo lại, cô cố gắng duy trì tỉnh táo, tay chân luống cuống điều khiển tay lái. Cô không dám quay đầu lại nhìn xung quanh cũng không dám nhìn hai bên trái phải. Cô sợ thứ gì đó đang trèo vào xe phá vỡ chút can đảm cuối cùng của cô.\r\n\r\nCô nghe thấy một tiếng nói khe khẽ gấp rút mà vội vã, nhưng không biết là âm thanh đó xuất phát từ đâu. Tựa như... từ trong cơ thể cô, nó chui ra từ nơi sâu thẳm trong đầu và lấp đầy không khí. Cô cảm nhận được nỗỉ sợ hãi cùng cái chết, và cả nỗi tuyệt vọng vô tận. Tiếng nện đùng đùng như từng hồi trống đòi mạng khiến cho cô không cách nào lẩn trốn.\r\n\r\nĐau quá, đau quá đi!\r\n\r\nĐầu cô như bị bổ ra, thậm chí cô có thể cảm giác được giữa đầu cô nứt ra một cái khe lớn, có gió mát lùa vào, cảnh vật trước mặt cũng dần dần mơ hồ.\r\n\r\nBản năng sinh tồn khiến cô cố gắng mở to mắt nhìn về phía trước, thấy chiếc xe đang cuồng nộ lao vun vút đến cửa. Cánh cửa sắt vừa tối tăm vừa nhọn hoắt giống như cột mốc của hai giới âm dương.\r\n\r\nCô đạp mạnh chân ga, dùng hết sức khiến chân như sắp bị gãy lìa. Tuy nhiên, đúng lúc này chiếc xe giống như bị một sức mạnh mãnh liệt kéo lại. Kính chắn gió đã sớm vỡ tan tành, lực quán tính khổng lồ hất cô ra khỏi xe, rới thẳng xuống bụi cỏ um tùm bên cạnh cửa sắt.\r\n\r\nChỉ còn vài bước nữa thôi! Vài bước nữa là cô có thể rời khỏi nơi quái quỷ này rồi!\r\n\r\nCô nghĩ trong tuyệt vọng, muốn gắng gượng bò dậy.\r\n\r\nCô không còn thấy được hết những thứ xung quanh mình nữa, cũng không nghe được gì hết. Chỉ có tiếng nói dồn dập và hoảng loạn quanh quẩn bên tai, ngoài ra… Còn có một tiếng động kỳ lạ vang lên, tiếng hét đó rất chói tai.\r\n\r\nHàn Băng khẽ nhấc người, chỉ thấy trong đêm tối như mực có hai luồng sáng chiếu đến. Ánh sáng đó quá chói mắt, cô không thể thấy được gì trong màn sáng chói mắt đó cả.\r\n\r\nCô sắp chết rồi phải không? Xuống địa ngục hay lên thiên đường vậy?\r\n\r\nTrong khoảnh khắc đó, cô đột nhiên cảm thấy hơi hoang mang, hơi trống vắng cũng quên mất sợ hãi. Khi nhìn thấy một cánh tay vươn ra trong luồng ánh sáng chói loà, rốt cuộc cô cũng hét lên một tiếng chói tai đã bị đè nén thật lâu rồi.\r\n\r\n“Hàn Băng, Hàn Băng, là tôi.” - Chủ nhân cánh tay cố giữ chặt lấy Hàn Băng đang ra sức tránh né.\r\n\r\nỞ nơi giao nhau giữa sáng và tối, gương mặt kia dần dần trở nên rõ nét. Trên gương mặt điển trai treo nụ cười nhàn nhạt, cho dù là lúc nghiêm túc cũng không thể giấu được ý cười như có như không. Cô nhận ra chủ nhân gương mặt này - Xuân Thất thiếu.', 3, 'khach-san-hoang-tuyen.jpg', 27, '2020-09-17 03:22:55', '1');
INSERT INTO `story` (`story_id`, `name`, `preview_text`, `detail`, `cat_id`, `picture`, `counter`, `create_at`, `status`) VALUES
(19, 'Này, Chớ Làm Loạn', 'Còn gì éo le hơn việc \"thẳng\" nữ lại bị hiểu nhầm thành người đồng tính, bị công ty sa thải, lại còn đụng phải một tên \"vô lại\". Vừa thất nghiệp, đường tình trắc trở vì vội vàng tìm bạn trai để chứng minh mình trong sạch, cuộc sống của cô dường như trở nê', 'Trong văn phòng làm việc của tập đoàn Hằng Viễn, Cao Ngữ Lam vừa đi công tác về.\r\n\r\nCao Ngữ Lam là một chuyên viên hoạch định hoạt động tiêu thụ của phòng thị trường. Cô làm việc ở công ty này hơn một năm. Công việc rất bận rộn, thường phải làm thêm giờ, đi công tác như cơm bữa. Nhưng công ty có quy mô lớn, có thể học hỏi thêm nhiều nghiệp vụ, lương lậu cũng không tồi, vì vậy Cao Ngữ Lam rất hài lòng với công việc của mình.\r\n\r\nBây giờ là năm giờ chiều, thời điểm sắp tan tầm. Cao Ngữ Lam vừa xuống máy bay, cô có thể về nhà nghỉ ngơi. Vì muốn tổng hợp lại nội dung đã thương lượng trong chuyến công tác nên Cao Ngữ Lam đến thẳng công ty.\r\n\r\nVừa vào văn phòng còn chưa kịp ngồi xuống, Cao Ngữ Lam nghe có tiếng gọi cô.\r\n\r\n“Ngữ Lam”.\r\n\r\nCô quay đầu nhìn, là Ôn Sa, nhân vật nổi tiếng trong công ty.\r\n\r\nCô Ôn Sa này đứng đầu bảng về nhan sắc ở công ty, thành tích nghiệp vụ mỗi tháng luôn dẫn đầu. Về mặt quan hệ hay đối nội đối ngoại, Ôn Sa cũng không nhất thì nhì. Vì vậy, cô ta quả thực là nhân vật nổi tiếng trong công ty.\r\n\r\nLúc này, Ôn Sa đi nhanh về phía Cao Ngữ Lam. Cô không biết cô ta có việc gì tìm mình, cô định mở miệng hỏi thì bất chợt bắt gặp ánh mắt kỳ lạ của đồng nghiệp ở bàn bên cạnh. Cao Ngữ Lam chưa kịp phản ứng đã bị Ôn Sa ôm chặt vào lòng.\r\n\r\n“Em yêu. Tôi xin lỗi, tôi đã nói hết rồi”.\r\n\r\nNói gì cơ? Cao Ngữ Lam còn đang nghi hoặc, trước mắt đột nhiên tối lại, môi cô bị một thứ mềm mềm đặt lên, mùi nước hoa nhè nhẹ phảng phất xung quanh. Cao Ngữ Lam giật bắn mình.\r\n\r\nÔn Sa dịu dàng hôn cô, cất giọng nhẹ nhàng: “Em yêu, đừng giận tôi nhé”.\r\n\r\nCao Ngữ Lam sững sờ như bị điểm trúng huyệt.\r\n\r\nRốt cuộc xảy ra chuyện gì?\r\n\r\nVừa rồi... vừa rồi cô bị cưỡng hôn?\r\n\r\nHơn nữa bị một người phụ nữ hôn?\r\n\r\nCao Ngữ Lam ngây người một lúc, đầu óc trống rỗng, cô không thể tin vào chuyện vừa xảy ra.\r\n\r\nCao Ngữ Lam vốn là một người phụ nữ nhà lành bảo thủ. À không... là cô gái nhà lành mới đúng. Ngoài chuyện cô yêu sớm từ thời cấp ba, yêu hết mình trong bảy năm trời nhưng cuối cùng lại bị bạn trai bỏ rơi, Cao Ngữ Lam hoàn toàn trong sạch. Đến ngón tay của người đàn ông thứ hai cô còn chưa từng chạm vào, sao bây giờ có thể bị một người phụ nữ hôn?\r\n\r\nThiên hạ quả là đại loạn mất rồi.\r\n\r\nÔn Sa dường như không bận tâm đến phản ứng của Cao Ngữ Lam. Cô ta vuốt ve mặt cô, nói một câu an ủi rồi quay người đi mất.\r\n\r\nCao Ngữ Lam từ từ quay đầu, sững sờ nhìn theo bóng Ôn Sa. Lúc này, bàn tay run rẩy của cô chỉ theo hướng cửa ra vào, miệng cô ấp úng không thể thốt nên lời.\r\n\r\nKẻ phạm tội ung dung bỏ trốn, làm thế nào bây giờ?\r\n\r\nCao Ngữ Lam dần định tâm trở lại. Cô không hiểu chuyện gì xảy ra, cũng không dám đưa mắt nhìn các đồng nghiệp ở xung quanh. Trong lòng cô hiểu rõ, vào lúc này, cô đã trở thành tâm điểm chú ý của cả văn phòng.\r\n\r\nÔng trời ơi, cho cô một cái lỗ để chui xuống được không?\r\n\r\nÔng trời không thấy trả lời, chỉ có một tiếng nói khẽ vang lên bên tai cô: “Ngữ Lam, giám đốc bảo chị đi gặp ông ấy”.\r\n\r\nCao Ngữ Lam quay đầu về phía người đồng nghiệp vừa lên tiếng thông báo, cô nhìn thấy vẻ thương hại trong ánh mắt người đồng nghiệp. Một cảm giác chẳng lành len lỏi vào tim Cao Ngữ Lam.\r\n\r\nGiám đốc là một người đàn ông hói đầu gần 50 tuổi.\r\n\r\nÔng ta chăm chú quan sát Cao Ngữ Lam bằng ánh mắt vô cùng nghiêm nghị trong suốt mười phút đồng hồ. Cuối cùng, ông ta cũng mở miệng: “Tiểu Cao này... ”.\r\n\r\nCao Ngữ Lam ngồi im lặng, trong lòng rất bất an. Giám đốc trầm mặc một thời gi­an dài khiến cô bắt đầu phân tâm.\r\n\r\nLát nữa cô phải đi tìm Ôn Sa tính sổ mới được. Cô ta tự nhiên hôn cô là có mục đích gì. Đều là phụ nữ, hành động đó thật ghê tởm. Nếu trò đùa này không được giải thích rõ, làm sao cô có thể tiếp tục làm việc ở công ty?\r\n\r\n“Cô làm việc ở công ty hơn một năm rồi, cô luôn có biểu hiện rất tốt”.\r\n\r\n“Là trách nhiệm của tôi mà”\r\n\r\n“Cô cũng biết công ty chúng ta không tán thành tình yêu văn phòng?”\r\n\r\n“Giám đốc yên tâm đi ạ. Tôi sẽ nghiêm chỉnh chấp hành”.\r\n\r\nVẻ mặt kỳ lạ của đồng nghiệp lúc nãy là gì nhỉ?\r\n\r\n“Cô yêu đương cũng chẳng sao. Nhưng cô lại gây ra... chuyện không bình thường, khiến công ty có ảnh hưởng không tốt”.\r\n\r\n“...... ”\r\n\r\nCao Ngữ Lam sững sờ, ngẩng đầu nhìn thẳng vào mắt giám đốc. Ánh mắt đó... xin thứ lỗi cho sự ngu dốt của cô, nhìn mãi không hiểu ông ta ám chỉ điều gì.\r\n\r\n“Giám đốc! Giám đốc bảo ai yêu đương cơ?” Cao Ngữ Lam hỏi.\r\n\r\n“Cô chứ còn ai”. Giám đốc sờ lên đầu nhẵn thín. Lần này, Cao Ngữ Lam đã đọc được vẻ mặt của ông ta. Trên mặt giám đốc viết hàng chữ: cô có giả bộ thế giả bộ nữa cũng chẳng có ý nghĩa gì cả.\r\n\r\n“Nhưng... ai yêu tôi?”. Lẽ nào trong công ty có một vị nam giới tinh anh nào đó phải lòng cô mà cô không biết?\r\n\r\nGiám đốc đại nhân nhíu mày nhìn cô.\r\n\r\nCao Ngữ Lam càng không hiểu đầu cua tai nheo. Lẽ nào ai đó hiểu nhầm cô yêu người ta, khiến người ta bối rối khó xử?\r\n\r\n“Giám đốc, tôi có yêu ai đâu”. Cao Ngữ Lam giải thích một cách nghiêm túc. Dù cô cũng muốn yêu đương hẹn hò để thoát ra khỏi đáy vực tình cảm, bắt đầu cuộc đời mới, nhưng nguyện vọng của cô vẫn chưa trở thành hiện thực.\r\n\r\nGiám đốc hắng giọng hai tiếng, mắt không rời khỏi Cao Ngữ Lam, tay gõ gõ lên bàn như không biết phải bắt đầu thế nào.\r\n\r\nCảm giác chẳng lành trong lòng Cao Ngữ Lam ngày càng rõ rệt. Sự việc kỳ quái vừa xảy ra hiện lên trong trí óc cô. Sau đó, cô nghe thấy tiếng giám đốc: “Toàn công ty đã biết chuyện của cô và Ôn Sa”.\r\n\r\n“Ôn Sa?” Cao Ngữ Lam cất cao giọng. Cô đột ngột tỉnh ra.\r\n\r\nĐúng là loạn hết cả rồi.\r\n\r\n“Giám đốc! Tôi tuyệt đối không yêu Ôn Sa. Tôi thích đàn ông cơ”.\r\n\r\nGiám đốc lại liếc cô, không nói một lời nào.\r\n\r\n“Giám đốc, tôi và Ôn Sa không phải quan hệ đó. Lẽ nào cô ấy nói, cô ấy yêu tôi? Dù là như vậy cũng chẳng liên quan gì đến tôi cả. Tôi và cô ấy không thân thiết lắm, nhất định có sự hiểm nhầm ở đây. Thật đấy, giám đốc hãy gọi cô ấy đến đây, chúng ta ba mặt một nhời... ”\r\n\r\nGiám đốc cắt ngang lời Cao Ngữ Lam: “Tiểu Cao, tôi hiểu tâm trạng của cô bây giờ”.\r\n\r\nHiểu gì? Không phải là tin lời tôi?\r\n\r\nThế thì có tác dụng gì!\r\n\r\n“Giám đốc! Giám đốc nhất định phải tin tôi”.\r\n\r\nGiám đốc lặng thinh, tay nghịch chiếc máy tính.\r\n\r\n“Giám đốc, tôi xin thề. Tôi yêu đàn ông! Yêu đàn ông! Là đàn... ông”. Cao Ngữ Lam suýt nghẹn không thể thốt ra từ cuối cùng, bởi vì giám đốc đã quay chiếc lap­top của ông ta về phía cô. Cao Ngữ Lam nhìn chằm chằm vào màn hình như thể nhìn thấy bóng ma.\r\n\r\nTrên màn hình có tấm ảnh Ôn Sa đang ôm hôn một cô gái ở một nơi giống như quán bar. Bọn họ mặc đồ bốc lửa, hôn nhau rất mãnh liệt. Ôn Sa để lộ nửa gương mặt nên thấy rõ. Còn cô gái kia đứng quay người nên chỉ có thể thấy bóng dáng từ phía sau. Nhìn qua quả thật rất giống Cao Ngữ Lam.\r\n\r\nCao Ngữ Lam há miệng kinh ngạc, ngón tay chỉ lên màn hình bắt đầu run rẩy: “Người đó... người đó không phải là tôi. Thật đấy, không phải là tôi”.\r\n\r\nNhưng giám đốc đại nhân không muốn tiếp tục thảo luận vấn đề “có phải là cô hay không”. Ông ta nghiêm nghị nhìn Cao Ngữ Lam: “Tiểu Cao à, yêu đương là tự do cá nhân. Yêu đàn ông hay đàn bà cũng là tự do cá nhân. Có điều, công ty có quy định của công ty. Tôi cũng không muốn làm vậy nhưng rất tiếc phải thông báo với cô, công ty đã phê chuẩn đơn xin thôi việc của cô”.\r\n\r\n“Tôi không xin thôi việc”. Cao Ngữ Lam nhảy ra khỏi chỗ ngồi.\r\n\r\n“Nếu cô không xin thôi việc, công ty sẽ gửi thông báo đuổi việc đến cô. Làm vậy không được hay cho lắm, do đó cô nên tự đề xuất thì hơn”.\r\n\r\nCao Ngữ Lam ngơ ngác. Công ty ức hiếp người quá đáng!\r\n\r\nCô dùng hết sức nắm chặt tay mình, thầm nhủ bản thân phải cố giữ bình tĩnh. Những nỗi ấm ức và khó chịu bao trùm toàn thân Cao Ngữ Lam. Tại sao cô luôn gặp phải những chuyện đen đủi như thế này, tại sao cô luôn là người xui xẻo.\r\n\r\n“Giám đốc, tôi không thể chấp nhận. Tôi không làm sai điều gì”.\r\n\r\n“Tiểu Cao, sáng hôm nay mọi đồng nghiệp trong công ty đã nhận được tấm ảnh này. Cô mới đi công tác về nên có thể chưa kịp mở hộp thư. Vụ này có ảnh hưởng rất xấu, đồng thời vi phạm quy định của công ty. Ôn Sa đã khai hết mọi chuyện rồi. Sự thật sờ sờ trước mắt, cô không nhận cũng vô dụng thôi. Để chỉnh đốn lại nề nếp của công ty, ổn định tinh thần làm việc của nhân viên, công ty quyết định cho cô thôi việc. Nói thật, cô không thích hợp tiếp tục làm việc ở đây khi công ty có không khí như thế này. Vì vậy, cô nên phối hợp thì tốt hơn”.\r\n\r\nCao Ngữ Lam đột nhiên hiểu ra phản ứng của các đồng nghiệp lúc vừa rồi. Nhưng cô bị oan mà!\r\n\r\n“Tôi bị hãm hại. Người đó không phải tôi”.\r\n\r\nGiám đốc thở dài: “Tiểu Cao, Ôn Sa đã thừa nhận rồi. Bây giờ cô nói gì cũng vô ích thôi. Công ty đã quyết định rồi. Hôm nay cô hãy bàn gi­ao lại công việc của cô. Lát nữa, phòng nhân sự sẽ tìm cô làm thủ tục”.\r\n\r\nCao Ngữ Lam nghiến răng, gương mặt không dấu vẻ tức giận: “Tôi sẽ đi tìm Ôn Sa. Tại sao cô ta lại hãm hại tôi?”\r\n\r\nCao Ngữ Lam nện gót giày bình bịch xuống nền nhà, lao nhanh đến văn phòng Ôn Sa. Không thấy bóng Ôn Sa, trợ lý của Ôn Sa nói nhỏ: “Sa Sa bị phê bình. Công ty cho chị ấy nghỉ phép một tuần để tự kiểm điểm”.\r\n\r\nNghỉ phép một tuần? Dựa vào cái gì mà kẻ gây tội được nghỉ phép còn người bị hại lại bị đuổi việc?\r\n\r\nCô chỉ là một nhân vật bé nhỏ, không quan trọng ở công ty nên cô bị hãm hại bị bắt nạt hay sao?\r\n\r\n“Cho tôi biết số điện thoại của cô ta”. Cao Ngữ Lam tỏ ra hung dữ, khiến cô trợ lý giật bắn mình. Cô ta đưa tấm danh thiếp của Ôn Sa cho Cao Ngữ Lam. Cao Ngữ Lam giật lấy, bấm máy gọi vào số di động trên danh thiếp. Kết quả, Ôn Sa tắt máy.\r\n\r\nMột đồng nghiệp nam ở bên cạnh nhìn Cao Ngữ Lam gọi điện thoại bằng ánh mắt mờ ám, như chế nhạo cô đóng kịch giỏi. Cao Ngữ Lam phẫn nộ hét lớn với anh ta: “Tôi không quen thân cô ta”.\r\n\r\nCao Ngữ Lam quay đầu hỏi người trợ lý: “Cô ta sống ở đâu?”\r\n\r\nCô trợ lý lắc đầu nói không biết. Mấy đồng nghiệp ở xung quanh đều quay sang nhìn. Ánh mắt của họ khiến Cao Ngữ Lam càng tức giận. Cô đập bàn, nói lớn tiếng: “Tôi thật sự không quen thân gì cô ta”.\r\n\r\nTất cả mọi người đều cúi đầu, giả vờ chăm chú làm công việc của mình.\r\n\r\nCao Ngữ Lam đứng nguyên tại chỗ đảo mắt bốn xung quanh. Trong mỗi văn phòng chứa đầy người của tòa cao ốc, máy tính của mỗi người đều có hình ảnh đồng tính thân mật của cô. Một đồng nghiệp đi qua liếc nhìn cô. Một người khác đang cúi đầu làm việc, khi cô đưa ánh mắt về phía anh ta, anh càng càng cúi thấp đầu.\r\n\r\nTrong lòng Cao Ngữ Lam đột nhiên thấy lạnh lẽo. Cuối cùng cô cũng nhận ra, không khí nơi này quả thực không còn thích hợp với cô.\r\n\r\nCao Ngữ Lam cố gắng quay về bàn làm việc của mình thu dọn đồ. Mấy người đồng nghiệp bình thường có quan hệ tốt với cô tiến đến giúp cô thu dọn, an ủi cô vài câu. Cao Ngữ Lam cảm thấy rất buồn, cô nhanh chóng làm xong thủ tục rồi bê đồ về nhà.\r\n\r\nBuổi tối, Cao Ngữ Lam say khướt ở trong quán bar. Bình thường, cô rất ngoan ngoãn, không dính đến rượu thuốc, ngủ sớm dậy sớm, chăm chỉ làm việc, ngay thẳng thật thà. Chỉ khi nào cô bị đả kích ghê gớm mới uống rượu đến say khướt. Hôm nay Cao Ngữ Lam cảm thấy quá bi thương nên cô quyết định phóng túng một lần.\r\n\r\nNhưng càng uống rượu, Cao Ngữ Lam càng thấy rầu rĩ.\r\n\r\nCao Ngữ Lam không biết nên làm gì khi gặp phải chuyện như thế này.\r\n\r\nTrên thực tế, đây không phải lần đầu tiên vận đen đeo bám cô. Mối tình đầu kéo dài bảy năm của cô kết thúc bi thảm khi có người đổ tội cô bắt cá hai tay. Công việc trước đó của cô, cấp trên đổ tội cho cô để giữ lấy bản thân, bắt cô phải chịu hết trách nhiệm ký sai hợp đồng, do đó cô bị đuổi việc. Lần này càng hoang đường hơn, cô bị khai trừ vì tình yêu với người cùng giới trong công ty.\r\n\r\nCao Ngữ Lam vừa uống rượu vừa gạt nước mắt. Uống rượu chẳng giải quyết được vấn đề gì. Cô lại thất nghiệp rồi, cô không biết phải làm gì bây giờ. Phụ nữ yêu phụ nữ không có tội, nhưng cô yêu đàn ông cơ mà. Cô bị oan ức. Tại sao mọi người toàn đổ tội oan cho cô? Tại sao cô luôn bị bắt nạt.\r\n\r\nNữ ca sỹ trong quán bar cất giọng hát mượt mà: “Đừng sợ hãi, hãy tiến về phía trước. Tất cả những chuyện không vui rồi sẽ qua đi. Sẽ có một thiên thần yêu bạn... ”.\r\n\r\nỞ đâu có thiên thần? Thiên thần đang ở đâu? Cao Ngữ Lam tiếp tục lau nước mắt. Bây giờ cô chẳng cần thiên thần gì đó, cô cảm thấy một người đàn ông càng thực tế hơn. Nếu cô có bạn trai, cô có thể chứng minh cô không phải yêu đàn mà là yêu đàn ông.\r\n\r\nCao Ngữ Lam vừa nghĩ ngợi linh tinh vừa tiếp tục nốc rượu, đầu óc cô bắt đầu quay cuồng. Đột nhiên có tiếng chuông điện thoại, Cao Ngữ Lam bắt máy rồi cất giọng thô lỗ: “Ai đó?”.\r\n\r\n“Tôi là Ôn Sa”.\r\n\r\nNghe đến cái tên này, mọi tế bào trên người Cao Ngữ Lam đều bốc hỏa. Cô đập mạnh bàn: “Cô là đồ khốn, tại sao cô hãm hại tôi?”\r\n\r\n“Rất xin lỗi đã đổ tội oan cho cô, nhưng tôi cũng lâm vào tình thế bắt buộc. Có người hãm hại tôi, may mà dáng hình trên tấm ảnh đó rất giống cô, cô lại không có bạn trai nên càng phù hợp điều kiện. Vì vậy tôi chỉ còn cách kéo cô xuống bùn. Tôi xin lỗi, tôi sẽ bù đắp cho cô. Nếu có công việc thích hợp, tôi nhất định sẽ giới thiệu cho cô”.\r\n\r\n“Giới thiệu cái đầu cô ấy. Cô mau đưa bạn gái của cô đến công ty giải thích rõ với bọn họ, trả lại sự trong sạch cho tôi”. Cao Ngữ Lam càng nói càng lớn tiếng: “Cô hãy cho bọn họ biết, tôi yêu đàn ông, đàn ông, cô rõ chưa?”. Nhắc đến từ “đàn ông”, Cao Ngữ Lam hung hãn đập bôp bốp xuống bàn, khiến hai người đàn ông bên cạnh định đến làm quen với cô vội chuồn mất.\r\n\r\n“Cô muốn chứng minh không có quan hệ gì với tôi? Đơn giản thôi, cô tìm một người bạn trai là được rồi”. Ôn Sa hoàn toàn không có ý định nghe theo lời cô. Cô ta không tỏ ra bị ảnh hưởng bởi thái độ tức giận của Cao Ngữ Lam. Ôn Sa nhẹ nhàng đề xuất ý kiến: “Cô đưa bạn trai cô đến công ty, là có thể vạch trần lời nói dối của tôi”.\r\n\r\nÔn Sa khinh thường cô không có bạn trai phải không? Cao Ngữ Lam cô dễ bị bắt nạt lắm sao?\r\n\r\nCao Ngữ Lam đứng dậy, nói lớn tiếng: “Tôi sẽ tìm đàn ông cho cô thấy. Cô sẽ nắm tay anh ta cùng đến công ty, cô hãy đợi đấy!”.\r\n\r\nCao Ngữ Lam lảo đảo đi thanh toán tiền, sau đó chân nam đá chân xiêu mãi mới ra đến cửa quán bar. Trong lòng cô đang nghĩ nên tìm người đàn ông như thế nào, một tiếng binh rất lớn vang lên, cô đâm đầu vào bên cạnh cửa.\r\n\r\nMọi người đều nhìn về phía cô, nhưng Cao Ngữ Lam không cảm thấy đau đớn.\r\n\r\nNhân viên phục vụ đứng ở ngoài cửa chạy đến đỡ Cao Ngữ Lam: “Cô ơi, cô có sao không? Có cần tôi gọi taxi cho cô?”\r\n\r\n“Không cần”. Cao Ngữ Lam phất tay: “Không cần taxi, nhà tôi ở gần đây, tôi không về nhà. Tôi phải đi tìm đàn ông. Tôi đang cần đàn ông”.\r\n\r\nMột người phụ nữ uống rượu say giở trò điên rồ.\r\n\r\nĐám đàn ông ở xung quanh quay đầu nhìn Cao Ngữ Lam rồi nhanh chóng tản ra, như sợ bị người phụ nữ này nhìn trúng.\r\n\r\n“Đừng sợ hãi, hãy tiến về phía trước. Tất cả những chuyện không vui rồi sẽ qua đi. Sẽ có một thiên thần yêu bạn... ”. Vẫn là tiếng hát của cô ca sỹ trong quán bar. Cao Ngữ Lam lảo đảo đi ra khỏi quán bar.\r\n\r\nLúc này cô đã say khướt, hai mắt mờ dần. Cao Ngữ Lam bước từng bước liêu xiêu, phát hiện trên đường có không ít đàn ông. Cô nên chọn người nào?\r\n\r\nCao Ngữ Lam dừng lại dụi dụi mắt. Cô cố gắng cũng không nhìn rõ, không thấy rõ thì chọn kiểu gì đây?\r\n\r\nCao Ngữ Lam nheo mắt nhìn về phía trước. Có câu “chọn ngày không bằng gặp ngày” (có nghĩa thuận theo tự nhiên), vậy cô cũng thay vì lựa chọn đàn ông, chi bằng vớ đại một người (nguyên gốc: chọn đàn ông không bằng đâm vào một người đàn ông).\r\n\r\nChính là anh ta, người ở trước mặt!\r\n\r\nCao Ngữ Lam rảo bước nhanh về phía trước. Cô không biết rốt cuộc bản thân đã làm gì. Trong cơn mơ hồ, Cao Ngữ Lam chỉ nghe thấy tiếng người đàn ông hét lớn: “Cô làm gì vậy? Này, này, cô chớ làm loạn”.', 4, 'nay-cho-lam-loan.jpg', 20, '2020-09-17 02:09:12', '1'),
(20, 'Bang Chủ Biết Yêu', 'Nó và hắn, hai con người, hai thế giới khác nhau. Bất ngờ đụng chạm, gặp mặt rồi đem lòng yêu thương nhau, Nhưng hạnh phúc không chỉ đơn giản như thế. Hai người bắt đầu xảy ra mâu thuẫn. Liệu tình yêu của hai người sẽ đến đâu??? Câu chuyện tình cảm đặc sắ', '*Trung học phổ thông K\r\n\r\n8:00 am\r\n- Tiểu Vy! Cát Chi! Hiệu trưởng gọi hai cậu kìa! - một cậu bạn hớt ha hớt hải chạy vào thông báo.\r\n- Mình biết rồi! Cảm ơn cậu! - nó bước đến bàn Cát Chi - Đi thôi!\r\nCát Chi đưa bộ mặt yểu xìu ra nhìn nó:\r\n- Mình sợ quá! Nếu không được thì tính sao đây?\r\n- Thì đi bụi chớ sao? Đi! Nhanh lên! - nó kéo tay Cát Chi lôi đi xềnh xệch.\r\n*Phòng hiệu trưởng:\r\n\"cộc...cộc...cộc\"\r\n- Mời vào!\r\nMột giọng nói ôn tồn vang lên, nó đẩy cửa bước vào, cả hai lễ phép:\r\n- Chúng em chào thầy ạ!\r\n- Chào hai em! Hai em ngồi đi thầy có việc muốn thông báo!\r\n- Dạ!!!\r\nThầy lấy trong ngăn bàn ra một tờ giấy rồi tiếp:\r\n- Kết quả kì thi giành học bổng Paradise School đã có rồi!\r\nNghe đến đây, chán Cát Chi đầy mồ hôi còn trống ngực nó thì thúc liên hồi, cả hai chỉ biết siết chặt tay nhau mà cầu nguyện.\r\n- Hai em chính thức là nữ sinh của Paradise School kể từ hôm nay!\r\nHai đứa tụi nó không tin vào tai mình nữa. Nó lắp bắp:\r\n- Thầy...cho...em mượn bảng kết quả ạ!\r\n- Đây hai em cứ xem! Thầy tự hào về hai em lắm! - giọng thầy phấn khởi.\r\nNó với Cát Chi xin phép ra ngoài.\r\n- Nguyệt Tiểu Vy 600 điểm, Lâm Cát Chi 597 điểm! - nó đọc to.\r\n- YEAHHHHHHHHHHHH! - Cát Chi hét um lên.\r\nCả hai ôm nhau nhảy cẫng lên sung sướng. Cũng may là đang ở phía sau trường, nếu không chắc tụi nó phải lên văn phòng gặp hiệu trưởng một lần nữa. Sau 5 phút ôm nhau như thế thì buông nhau ra và đọc tiếp:\r\n- Sau 1 tuần nhận được thông báo, các học sinh phải đến trường để làm thủ tục nhập học.\r\n- Một tuần hả? Sao nhanh dzậy? - Cát Chi nhăn nhó.\r\n- Về lớp thông báo ọi người thôi nào!\r\nNói rồi cả hai chạy về lớp. Lòng ai cũng vui sướng và rất háo hức.\r\n......................................................\r\n*Đêm trước khi đi:\r\n\"Tiểu Vy dễ thương nghe điện thoại...Tiểu Vy dễ thương nghe điện thoại\"\r\nChuông điện thoại của nó vang lên.\r\n- alo! Vy nghe!\r\n- Tớ Chi nè! Bố mẹ tớ đã đặt vé máy bay rồi! Sáng mai 7:30 máy bay cất cánh đấy!\r\n- Tụi mình sẽ ở đâu?\r\n- Ở nhà anh họ tớ! Ba mẹ tớ bảo thế!\r\n- Cho tớ gửi lời cám ơn ba mẹ cậu nhé!\r\n- Không có gì! Thôi ngủ sớm đi! Ngủ ngon nha!\r\n- Ừ tạm biệt cậu!\r\nĐầu dây bên kia cúp máy, nó đặt điện thoại xuống rồi cũng mơ màng chìm vào giấc ngủ.\r\nSáng hôm sau, nó dậy sớm chuẩn bị. Nó nhìn một lượt ngôi nhà thân yêu ba mẹ để lại, nơi nào cũng có kỉ niệm cả. Những chiếc cúp nó thi đấu mang về, đám thú nhồi bông thân yêu ba mẹ mua cho làm lòng nó rạo rực. Nó rơi nước mắt:\r\n- Bố mẹ hãy tin ở con! Con nhất định sẽ thành công!\r\nNó kéo vali đi ra ngoài...\r\n..........................................\r\n*9:00 thành phố X:\r\nCả hai vừa xuống máy bay xong, mệt rã rời còn gì. Đang ngồi ở hàng ghế nghỉ thì Cát Chi bảo:\r\n- Mình đi hoàn tất một số thủ tục, ngồi đây đợi mình nha!\r\n- Ok.\r\nNó lấy cái vòng tay bằng bạc mẹ mua cho ra ngắm, nó toan đứng lên mang vali ra ngoài cổng thì có tiếng người kêu la thất thanh:\r\n- CƯỚP! LÀM ƠN BẮT LẠI GIÙM TÔI!\r\nNó quay sang thì nó đâm vào một người, cả hai té nhào đầu và chiếc vòng nó đang cầm văng xuống đất. Đồ đạc trong vali người kia cũng bị đổ đầy ra. Trong hắn có vẻ rất hối hả, nhặt đồ nhanh đến chóng mặt rồi chạy vụt đi. Nó ngẩng người ra nhìn cho đến khi nhớ ra là tên kia đã nhặt luôn chiếc vòng. Nó lập tức đuổi theo, thấy nó đuổi tên kia còn chạy nhanh hơn. Kết quả là hắn lên một chiếc ôtô đen và rồ ga. Nó bay lên và tung một cước vào kính xe trông rất đẹp mắt.\r\n\"Xoảng\"\r\nNgười và xe không ai còn nguyên vẹn. Tên đó lộn tròng mắt bị nó lôi ra ngoài giao cho bảo vệ. Nó lấy lại chiếc vòng và trả lại cái túi cho khổ chủ. Nó phủi tay trước sự tán thưởng nhiệt liệt của mọi người.\r\n- Cái gì đây? - Giọng nói nghiêm nghị pha chút uy quyền vang lên.\r\nNó xoay người lại thì thấy một tên con trai style tóc Hàn Quốc, mặc vest đen, gương mặt thì ôi thôi đẹp trai hết chỗ nói đang trừng mắt nhìn nó. Đẹp thì đẹp thật nhưng cái bản mặt thì lạnh tanh cứ như vừa ở Nam Cực chơi với chim cánh cụt về vậy.\r\n- Hả??? - nó không hiểu gì hết ráo.\r\n- Là cô làm sao? - hắn nghiêm giọng hỏi.\r\n- Ờ thì....- mắt nó đảo qua đảo lại suy nghĩ.\r\n- Đúng thật là xui xẻo! - hắn bắt đầu thấy bực.\r\n- Thưa cậu chúng ta phải đi ngay bây giờ ạ! - một trong số những tên áo đen đứng sau lưng hắn lên tiếng.\r\n- Tôi biết rồi! - hắn đáp - Sáng sớm ra đường gặp kẻ phá hoại! Tránh ra! - hắn nhìn nó không chút thiện cảm.\r\n- Anh làm gì dữ vậy? Ai bảo anh không chịu khóa cửa xe? Bây giờ bày đặt quát phụ nữ à? Đúng là cái đồ phách lối! - nó tuôn một tràng.\r\n- Cô...cô...- hắp lắp bắp.\r\n- Cô sao con? Cô đẹp quá chớ gì! - nó vênh mặt lên thách thức.\r\n- Giữ cô ta lại! - hắn ra lệnh.\r\n5 tên áo đen bặm trợn tiến về phía chỗ nó. Mặt ai cũng hầm hầm.\r\n\"Định hành hung phụ nữ à? Các người chết chắc\" Nó cười đắc ý.\r\n\"Bốp...phịch....binh...bốp\"\r\n5 tên thuộc hạ của hắn đo đất, hắn khẽ nhíu đôi chân mày anh tú lại.\r\n- Muốn bắt tôi à? Còn lâu! - nó trêu hắn.\r\n- Cô giỏi lắm! Đừng bao giờ để tôi gặp lại cô lần nữa! - hắn nhấn mạnh từng chữ rồi bỏ đi.\r\nĐám thuộc hạ bước theo sau, lần đầu tiên có kẻ dám ăn nói với hắn như vậy mà lại là con gái nữa chứ. Hắn ra lệnh với đám thuộc hạ:\r\n- Điều tra con nhóc đó cho tôi!\r\n- Vâng thư Bang Chủ!\r\n', 4, 'bang-chu-biet-yeu.jpg', 25, '2020-09-18 10:15:22', '1'),
(21, 'Tiểu Bảo Bối Băng Giá Của Tôi', 'Những cô công chúa của tập đoàn đác quý cùng những chàng hoàng tử của các tập đoàn lớn, Nếu các bạn này gặp nhau trong trường học, Rồi những tích cách tưởng chừng trái ngược nhau nhưng lại bổ sung cho nhau dưới một mái trường.', 'GIỚI THIỆU NHÂN VẬT\r\n\r\nNỮ\r\n\r\nNó : Lê Anh Ngọc Băng (Ema) 17t, là con nuôi của chủ tịch tập đoàn đá quý lớn nhất thế giới, tính tình lạnh lùng, ít nói , trầm tính vì một số lí do, xinh đẹp như từ tranh bước ra, giỏi võ, IQ 300/300.\r\n\r\nNguyễn Kì Thu( Jen) 17t , bạn thân nó, đại tiểu thư tập đoàn thời trang thứ tư trên thế giới, xinh đẹp, dễ thương, gần gũi, nhưng giận lên thì cần tránh xa năm mét. Giỏi võ thua nó, IQ 300/300\r\n\r\nNguyễn Kì Như(Jun) 17t, bạn thân nó và em của Jen. Gia thế giống Jen, ương ngạnh, ngang bướng, không sợ kì ai, chỉ sợ mình nó, giỏi võ thua nó, IQ 290/300\r\n\r\nNAM\r\n\r\nHắn: Trần Thiên Anh (Ran) 17t con trai tập đoàn kinh doanh xe hơi lớn nhất thế giới, ngang ngửa với nhà nó, lạnh lùng ít nói ( từ nhỏ tính tình đã vậy rồi), cực đẹp trai, giỏi võ, và biết sử dụng một số loại vũ khí, là bang chủ bang Hellmoon. Iq 300/300\r\n\r\nLê Anh Ngọc Bảo (Ren) 17t , anh trai nó, bạn trai Jen va bạn hắn, gia thế giống nó cũng là con nuôi, đẹp trai tính tình ấm áp, hòa đồng, rất thương em gái mình, ai đụng vào nó thì chuẩn bị quan tài trước. giỏi võ, biết sử dụng một số vũ khí, bang phó Hellmoon IQ 300/300\r\n\r\nHoàng Khánh Nam (Rin) 17t ,bạn hắn và Ren, thiếu gia tập đoàn dầu khí lớn thứ ba trên thế giới, đẹp trai, lăng nhăng, sát gái, thay bồ như thay áo, giỏi võ chỉ biết dùng súng, bang phó hellmoon.iq 300/300\r\n\r\nCHAP 1: QUÁ KHỨ\r\n\r\nTại biệt thự nhà nó\r\n\r\nChoang,…choang..tiếng đỗ vỡ của đồ đạc\r\n\r\n-Tại sao, tai sao ông lại đối xử với mẹ con tôi như vậy chứ,hả?-mẹ nó vừa khóc vừa hét\r\n\r\n-Tại vì tôi không còn yêu cô nữa, à không, trước giờ chưa từng yêu, tôi lấy cô cũng chỉ vì gia sản kết xù mà ba cô đã để lại cho cô, giờ nó đã thuộc về tay tôi, cô không còn giá trị lợi dụng nữa- ông ta vừa nói vừa cười một cách khinh bỉ nhìn mẹ nó\r\n\r\nLúc này hai anh em nó vừa đi chơi về thấy nhà cửa tan hoang, đồ đạc bể nát, và bây giờ trước mặt hai anh e nó là mẹ nó đang ngồi bệt dưới sàn nhà khóc, thấy mẹ khóc nó cũng khóc theo, nó vừa khóc vừa đánh ba nó nói ” ba là người xấu, sao ba lại làm mẹ khóc chứ, con ghét ba” nhưng ông ta vô tình hất nó ra khiến nó ngã nhào, vô tình trúng phải mảnh vỡ thủy tinh, tay nó rớm máu, mẹ và anh nó thấy vậy chạy lại đỡ nó dậy. và:\r\n\r\n“ ông biết mình đang làm gì không hả, ông làm em tôi và cũng chính đứa con gái của ông bị thương đó” anh nó dùng ánh mắt lạnh lùng nhìn ba nó và quát\r\n\r\n“Chát” Ông ta tức quá liền vung tay tát anh nó và chửi “ mày là đồ mất dạy, dám quát cả ba mày à” anh nó trả treo lại “ đúng, tôi là đồ mất dạy đấy, bởi vì ba của tôi suốt ngày chỉ có công việc, nếu có thời gian rảnh thì lại ở bên người tình và đứa con riêng của ông ta” khi ông ta nghe vậy hơi bất ngờ \"vì sao anh nó chỉ mới 6t mà lại biết rõ việc ông làm như vậy\" (tg ông ơi con ông chỉ số IQ cao lắm đó) ông nghĩ, nhưng ông ta nhanh chóng lấy lại bình tĩnh nói “ không nói nhiều, bây giờ tôi cho cô hai sự lựa chọn, một là kí vào giấy không nhận bất cứ gia tài nào khi li dị thì tôi sẽ để cô nuôi con, còn không tôi sẽ nuôi nó” ông nói và chỉ vào nó, ông biết bà rất thương con không thể rời ra chúng nên ông đã lấy cớ này để ép bà. mẹ nó nghe thấy ông nói vậy liền tái mặt, sợ rằng nếu bà nếu không chấp nhận thì nó sẽ xa bà, phải sống cùng người cha độc ác .Nên bà quyết định chấp nhận điều kiện của ông. Sau khi bà kí giấy xong, ông ta liền sai người hầu đuổi ba mẹ con nó ra đường. trước khi đi, anh nó không quên ném lại cho ông một câu “ ông hãy nhớ ngày hôm nay đã làm gì đối với mẹ con tôi, tôi sẽ trả lại ông gấp ngàn lần”', 4, 'tieu-bao-boi-bang-gia-cua-toi.jpg', 17, '2020-09-18 10:15:15', '1'),
(22, 'Nhật Ký Mang Tên Anh', 'Truyện xoay quanh tình yêu và cuộc sống của Hoàng Ngọc Lâm và Nguyễn Phương My là hai người bạn thân thiết, có thể nói tình cảm của họ giống như chị em ruột vậy.Vũ Hoàng Minh là chàng trai lạnh lùng, mạnh mẽ, tuy gia cảnh nghèo khó nhưng lại cực kỳ giỏi.', 'Nó: Hoàng Ngọc Lâm 16 tuổi (người ta thường gọi là Lâm Lâm) Là cô bé khá xinh.tính tình dễ gần.hay giúp đỡ người khác\r\n\r\nCô: Nguyễn Phương My 16 tuổi.bạn tốt của nó.Là cô gái rất xinh đẹp.tính tình dịu dàng.thông minh.Cả hai đều là con của gia đình giàu có.ít được quan tâm từ người thân nên hai cô nàng rất thân từ nhỏ.tình cảm như hai chị em ruột vậy.\r\n\r\nAnh: Vũ Hoàng Minh 20 tuổi.Là chàng trai đẹp trai.tính tình lạnh lùng.mạnh mẽ.Anh làm việc bán thời gian.gia đình anh hơi nghèo khó nhưng anh vô cùng học giỏi.Hắn: Kim Hải Nam 20 tuổi. Là bạn tốt từ nhỏ của Hoàng Minh. Cũng khá đẹp trai Tính tình dễ gần.vui vẻ.\r\n\r\nVài bạn học: Nguyễn Mỹ Sang. Mai Ngọc Kim. Võ Như Ái.16 tuổi đều yêu thích Minh Hoàng.nó lại ngồi chung bàn với anh nên thành ra ghét nó luôn.hay giơ trò chọc pha nó.\r\n\r\nĐinh Ngọc Khánh.bạn ngồi chung bàn với Phương.tính tình hơi ngốc chút thích làm đẹp.Nguyễn Thái Sơn. học tệ nhất lớp.\r\n\r\ncòn vài nhân vật khác thì Sứ sẽ giới thiệu sau nhé.câu chuyện của chúng ta thế loại học đường.tình cảm\r\n\"chết! trễ giờ học mất rồi\"\r\n\r\nNó thét lên và chạy như bay vào phòng tắm, thay đồng phục đi học. Phương My nhỏ bạn tốt của nó đã chạy xe đến trước nhà nó và hỏi lớn\r\n\r\n\"Lâm Lâm, mày xong chưa vậy\"\r\n\r\nNó thay đồ xog thì vội lấy ba lô và chạy xuống\r\n\r\n\"tao xog rồi nè\"\r\n\r\nPhương My đánh nhẹ vào trán nó\r\n\r\n\"đồ con heo ngủ gì mà dữ vậy hả\"\r\n\r\nNó lấy xe ra và nói vội\r\n\r\n\"đi học đi.trễ giờ rồi\"\r\n\r\nPhương My khẽ cười\r\n\r\n\"mày mà cũng sợ trễ giờ học nữa ư\"\r\n\r\nNó và Phương My cùng chạy xe đạp đi học...... Gần đến trường thì ở bên kìa đường tự nhiên có 1 chiếc xe đạp khác lao ra dụng vào xe nó.cả hai cùng ngã xuống.\r\n\r\n\"ui đau quá đi\"\r\n\r\nNó cố ngồi dậy. Phương My dừng xe lại hỏi\r\n\r\n\"Lâm Lâm, mày có sao không?\"\r\n\r\nVà chạy đến đỡ nó đứng dậy.nó nhìn lại xe đập của mình bị trầy thì tức giận\r\n\r\n\"là ai không có mắt vậy hả?\"\r\n\r\n\"câu này để tôi hỏi mới đúng chứ?\"\r\n\r\ngiọng nói nam vang lên. Nó quay qua thì thấy một chàng trai mặc áo sơ mi trắng với quần tây đen, trông anh thật đẹp trai. Nó bước tới nói\r\n\r\n\"là anh dụng tôi trước mà còn dám lên tiếng nữa hả?\"\r\n\r\n\"là cô chạy sai đường mà, sao trách tôi\"\r\n\r\nCả hai không ai chịu thua ai, cứ cãi nhau. Phương My nói\r\n\r\n\"đã trễ giờ học rồi đừng cãi nữa\"\r\n\r\nNó và Hoàng Minh nhìn lại đồng hồ rồi cùng thét lớn lên\r\n\r\n\"trễ học mất rồi\"\r\n\r\nrồi nó và Hoàng Minh đều hoảng hốt chạy như bay.\r\n\r\nnó và Phương My chạy đến trường thì thấy người ta đã đóng cổng rồi. Phương My bực minh nói\r\n\r\n\"tại mày hết đó Lâm Lâm\"\r\n\r\n\"tao có cách. đi theo tao\"\r\n\r\nNó cười khẽ và kéo tay Phương My đi... Phương My tưởng nó kéo mình đi đâu thì ra là ra sau trường, một bức tường rất cao. Phương My hỏi\r\n\r\n\"mày bị điên hay sao vậy. Làm sao tao leo qua được\"\r\n\r\nHoàng Minh bước tới nói\r\n\r\n\"vậy để anh giúp em nha\"\r\n\r\nNó ngạc nhiên quay qua nhìn thấy Hoàng Minh đứng đó thì liền hỏi\r\n\r\n\"lại là anh nữa hả đồ xui xèo\"\r\n\r\nHoàng Minh nhìn nó bằng ánh mắt lạnh lùng\r\n\r\n\"cô mới là người xui xèo đó\"\r\n\r\nPhương My nhìn và thở ra\r\n\r\n\"ôi trời, cãi nhau nữa rồi...mình bị xui xèo nhất thì có\"\r\n\r\nHoàng Minh xô nó ra bước đến gần Phương My và nói\r\n\r\n\"em đưa tay cho anh đi\"\r\n\r\nrồi anh đưa tay về phía cô. Phương My hơi ngại ngùng từ từ đặt tay mình vào bàn tay anh. Hoàng Minh bỗng nhiên bế Phương My lên. Khiến cả Phương My và nó ngạc nhiên. Nó chạy đến hỏi\r\n\r\n\"Ê anh đang làm gì thế\"\r\n\r\nHoàng Minh không trả lời nó mà cứ bước gần bức tường cao lớn ở trước mặt và đỡ Phương My lên ngồi trên bức tường ấy rồi tự mình nhảy qua. Cú nhảy của Hoàng Minh khiến cho nó phải kinh ngạc. Hoàng Minh bên trong đưa tay đỡ Phương My xuống đất an toàn. Phương My lớn tiếng hỏi\r\n\r\n\"Lâm Lâm, Mày qua được không?\"\r\n\r\nnó cười mỉm và lui lại về phía sau mấy bước rồi làm cú nhảy cao tuyệt đẹp. Sau vài giây nó đã chạm xuống đất an toàn với những tìa nắng yếu ớt và mái tóc đài của nó nhẹ nhàng bay bay trong gió khiến cho Hoàng Minh nhìn thấy rõ khuôn mặt xinh đẹp của nó. Phương My quay sang Hoàng Minh và nói giọng ngọt ngào\r\n\r\n\"cảm ơn anh vì đã giúp em\"\r\n\r\n\"không có chi\"\r\n\r\nHoàng Minh lạnh lùng nói rồi quay lưng bỏ đi. Nó nhìn và bực mình\r\n\r\n\"anh ta có thái độ gì vậy\"\r\n\r\nPhương My đứng yên nhìn và cười cười\r\n\r\n\"đẹp trai quá đi\"\r\n\r\n\"tao mặt kệ mày\"\r\n\r\nnó nói và quay lưng chạy vào trường. Phương My khẽ giật mình chạy theo\r\n\r\n\"Này đợi tao với\"\r\n\r\nnó và Phương My đến phòng hiệu trưởng. Nó nhẹ gõ cửa\r\n\r\n-Cốc Cốc-\r\n\r\nhiệu trưởng khoảng 40 tuổi họ Lý đang chấm bài nghe tiếng gõ cửa thì lên tiếng nói\r\n\r\n\"ai đó! Mời vào\"\r\n\r\nnó và Phương My nhẹ đẩy cửa bước vào\r\n\r\n\"dạ chào hiệu trưởng ạ\"\r\n\r\nHiệu trưởng Lý quay mặt qua nhìn và hỏi\r\n\r\n\"hai em là Hoàng Ngọc Lâm và Nguyễn Phương My ở Anh mới về.phải không\"\r\n\r\nPhương My và nó nhẹ gật đầu. Phương My lễ phép hỏi\r\n\r\n\"dạ cho em hỏi.tụi em sẽ học lớp nào ạ\"\r\n\r\nHiệu trưởng Lý đứng dậy và nói\r\n\r\n\"lớp 11 a6.để tôi dẫn hai em đi\"\r\n\r\nnó và Phương My nhẹ gật đầu\r\n\r\n\"dạ\"\r\n\r\nHiệu trưởng Lý dẫn nó với Phương My vào lớp 11 a6 và lớn tiếng nói\r\n\r\n\"lớp chúng ta hôm nay có hai bạn mới. đó là bạn Ngọc Lâm và bạn Phương My\"\r\n\r\ncả lớp đều vỗ tay vui mừng. Hiệu trưởng Lý nói khẽ\r\n\r\n\"hai em tự tìm chỗ ngồi nhé\"\r\n\r\n\"dạ vâng\"\r\n\r\nPhương My và nó nhẹ gật đầu rồi bước đến hai chỗ còn trống. Đang đi thì Ngọc Khánh kéo tay Phương My lại và nói\r\n\r\n\"bạn ngồi với mình nhé\"\r\n\r\nPhương My thấy cô bạn trước mặt mình cũng dễ thương nên nhẹ gật đầu và ngồi xuống\r\n\r\n\"mình là Phương My.bạn tên gì?\"\r\n\r\nNgọc Khánh vui vẻ nói\r\n\r\n\"ôi người đã đẹp rồi mà tên cũng đẹp nữa.hihi.mình tên Ngọc Khánh\"\r\n\r\nnhững lời khen của Ngọc Khánh khiến cho Phương My bật cười\r\n\r\n\"bạn quá khen rồi\"\r\n\r\ncòn nó thì ngồi xa Phương My một chút. Người chung bàn với nó là một chàng trai đang áp mặt xuống ngủ.chẳng biết là ai nữa nó chẳng tò mò và không muốn biết. Lo học vẫn tốt hơn. Nhưng đang có ba ánh mắt tâm ghét nhìn về phía nó mà nó chẳng hay biết. Mỹ Sang đánh nhẹ Như Ái và hỏi nhỏ\r\n\r\n\"Ê mày có biết con nhỏ ngồi chung lớn trưởng là ai không?\"\r\n\r\nNhư Ái quay đầu lại nhìn nó và nói\r\n\r\n\"hình như là học sinh mới\"\r\n\r\nMỹ Sang tức giận đánh vào đầu Như Ái\r\n\r\n\"tào lao.chuyện đó ai mà chẳng biết. tao muốn biết nó là con cái nhà ai mà được vào lớp này học\"\r\n\r\nNgọc Kim ngồi phía sau lúc này lên tiếng nói\r\n\r\n\"con nhỏ đó tên Hoàng Ngọc Lâm.là con của ông chủ tịch.nhà nó cũng giàu có lắm đấy\"\r\n\r\nNhư Ái hoảng hốt nói\r\n\r\n\"vậy thôi chết.có khi nào con nhỏ đó sẽ cướp mất lớp trưởng đẹp trai của chúng ta không?\"\r\n\r\nMỹ Sang nói giọng tức giận\r\n\r\n\"hừ.nếu nó đám\"\r\n\r\n\"tụi em đang to nhỏ gì đó!\"\r\n\r\nnãy giờ tụi người của Mỹ Sang chỉ lo nói chuyện nên hiệu trưởng Lý xuống bàn mình lúc nào cũng không hay. Mỹ Sang lắp bắp\r\n\r\n\"dạ...tụi...em đang...hỏi bài thôi...\"\r\n\r\nhiệu trưởng Lý nhìn và nói\r\n\r\n\"hỏi bài thiệt thì tốt.đừng quậy phá nữa đấy\"\r\n\r\nMỹ Sang giả vờ ngây thơ\r\n\r\n\"em ngoan như vậy sao quậy phá được chứ?\"\r\n\r\nhiệu trưởng Lý đã nhận ra được sự giả tạo của Mỹ Sang từ lâu nhưng vì nể cha cô ta hay tài trợ cho trưởng nên đành mắt nhắm mắt mở thôi...\r\n\r\nnó đang viết bài thì vô tình nhìn ra cửa sổ. Woa bầu trời hôm nay đẹp quá nhỉ vậy mà từ sáng nó chẳng để ý. Nó muốn nhìn rõ hơn nhưng chẳng thể. Vì người chung với nó đang ngủ một dóng ở đó rồi.\r\n\r\n\"cản đường cản lối.đáng ghét\"\r\n\r\nnó lẩm bẩm trong miệng với vẻ mặt bực mình. Chẳng biết tại sao ngày đầu tiên đi học mà toàn gặp người gì đâu không? bực mình quá đi.\r\n\r\n--Tan Học--\r\n\r\nNgọc Khánh cùng Phương My bước qua bàn nó. Phương My nhìn và hỏi khẽ\r\n\r\n\"mày sao vậy Lâm Lâm.sao tao thấy mày có vẻ bực mình quá vậy\"\r\n\r\nnó vừa lấy tay chỉ vào chàng trai bên mình vừa nói\r\n\r\n\"tai tao sắp bị điếc bởi con heo này rồi đây\"\r\n\r\nNgọc Khánh khờu nhẹ tay nó và vội nói\r\n\r\n\"bạn đừng nói lớn mà.để các bạn trong lớp nghe được thì bạn sẽ gặp rắc rối đó!\"\r\n\r\nnó nhìn Ngọc Khánh với ánh mắt không hiểu.\r\n\r\n\"ý bạn là sao.mình không hiểu lắm...người này là ai\"\r\n\r\nNgọc Khánh nhìn chàng trai bên nó và khẽ nói\r\n\r\n\"anh ấy tên Hoàng Minh là lớp trưởng đẹp trai nhất lớp này mọi người đều yêu thích anh ấy cả? Họ không cho ai nói xấu anh ấy đâu\"\r\n\r\nnó mở to mắt nhìn và hỏi\r\n\r\n\"hắn ta là lớp trưởng hả? lớp trưởng kiểu gì mà ngủ suốt trong giờ học à\"\r\n\r\nlúc này Thái Sơn bỗng chạy đến lấy cuốn tập trong tay Ngọc Khánh rồi bỏ chạy. Ngọc Khánh nói giọng bức mình\r\n\r\n\"Thái Sơn.ông trả tập cho tôi.sao suốt ngày sao chép bài của tôi chứ? trả tập cho tôi\"\r\n\r\nrồi Ngọc Khánh đuổi theo Thái Sơn chạy vòng vòng trong lớp.Thái Sơn vừa chạy vừa nói\r\n\r\n\"tại chữ của bà đẹp.cho tôi mượn chép bài chút đi mà\"\r\n\r\nVì vậy đã gây tiếng ồn khiến chàng trai bên nó giật mình dậy. Ngay lúc ấy nó vô tình quay lại nhìn và ngạc nhiên thốt lên\r\n\r\n\"lại là anh\"\r\n\r\nHoàng Minh cũng ngạc nhiên khi nhìn thấy nó đang bên cạnh mình. Anh hói giọng buồn ngủ\r\n\r\n\"sao lại cô nữa.lại ngồi chung bàn với tôi nữa\"\r\n\r\nPhương My lúc này nhẹ quay đầu qua nhìn thì rất ngạc nhiên khi thấy người đang trước mặt mình là anh chàng trai mà mình đã gặp hồi sáng', 4, 'nhat-ky-mang-ten-anh.jpg', 24, '2020-09-05 20:53:18', '1'),
(23, 'Bảy Bước Tới Mùa Hè', 'Bảy bước tới mùa hè là tác phẩm mới nhất của Nguyễn Nhật Ánh, được nhà văn đề tặng \"Những năm ấu thơ\", như một món quà dành tặng các bạn đọc thân thiết của mình nhân dịp đầu năm mới.', 'Câu chuyện về một mùa hè ngọt ngào, những trò chơi nghịch ngợm và bâng khuâng tình cảm tuổi mới lớn. Chỉ vậy thôi nhưng chứng tỏ tác giả đúng là nhà kể chuyện hóm hỉnh, khiến người đọc cuốn hút từ tựa đến trang cuối cùng, có lẽ chính vì giọng văn giản dị và trong trẻo của Nguyễn Nhật Ánh, và kết thúc thì có hậu đầy thuyết phục. Câu chuyện cho tuổi học trò, đọc xong là thấy ngập lên khao khát quay về một thời thơ bé, với tình thầy trò, bè bạn, tình xóm giềng, họ hàng, qua cách nhìn đời nhẹ nhõm, rộng lượng. Bảy bước tới mùa hè sẽ là món quà mà nhà văn Nguyễn Nhật Ánh dành tặng bạn đọc thân thiết, một lần nữa ông đánh thức “Những năm tháng ấu thơ”, trở lại tuổi học trò đầy nghịch ngợm, trong veo với những bâng khuâng tình cảm “treo ngược cành cây” … Ông vẫn dùng cây đũa thần là giọng kể chuyện hóm hỉnh của mình, khiến ai đọc qua cũng thấy xao xuyến với một thưở hồn nhiên cắp sách đến trường.\r\nKhoa kẹp chân vào cán chổi, hai đầu gối cụng vào nhau để giữ thăng bằng, cứ thế Khoa đong đưa trong gió như một con diều.\r\n\r\nTheo cung cách mà Khoa thể hiện thì Khoa đúng là chàng phù thủy trẻ tuổi đang cưỡi chổi bay trong một ngày đầu hè tuyệt đẹp. Có thể thấy ráng chiều đỏ ối phía chân trời phản chiếu óng ánh trên mặt chàng.\r\n\r\nỞ trên cao, lưng chừng trời, chàng phù thủy trẻ mắt vẫn không ngừng bám cứng hàng rào mồng tơi nhà hàng xóm, miệng ngân nga một giai điệu chả biết có đúng phép tắc âm nhạc hay không nhưng chắc chắn là rất rực rỡ trữ tình:\r\n\r\n- Trên chiếc chổi bay này tôi nhớ em\r\n\r\nGiữa cơn gió lạnh tê người này tôi nhớ em...\r\n\r\nEm ở đây, trong trí não của chàng phù thủy trẻ tuổi tên Khoa, là nhỏ Trang con gái bà Chín Ghe.\r\n\r\nNhỏ Trang vào giờ này thường ra sau hè ngồi lặt rau, vo gạo.\r\n\r\nKhoa biết vậy nên nó cứ đu đưa trên cây chổi mà gào thật to, giống như kiểu các thợ săn vẫn gõ thanh la để đánh động thú rừng.\r\n\r\nCây chổi được treo toòng teng trên nhánh ổi bằng hai sợi dây cột ở hai đầu, trông từa tựa cái xích đu nhưng thế ngồi của Khoa thì đúng là thế ngồi hùng dũng của một kỵ sĩ đang xông ra chiến trường (ở đây là... tình trường).\r\n\r\nMột tay nắm chặt cán chổi, tay kia níu nhánh cây trên đầu cho khỏi ngã, chàng phù thủy bạo dạn và lãng mạn của chúng ta cứ kẹp cứng lấy cây chổi mà buông ra những lời nhăng nhít. Trời lưng lửng chiều, nóng hầm hập, nhưng Khoa vẫn ca cẩm \"giữa cơn gió lạnh tê người\" cho ra vẻ thống thiết.\r\n\r\nNhưng nhỏ Trang chẳng biết biến đi đằng nào. Khoa ra sức gào rát cả họng nhưng chẳng thu hoạch được gì.\r\n\r\nĐã thế, cái trò tán tỉnh rẻ tiền của nó chỉ đem lại tai họa.\r\n\r\n- Mày làm cái trò om sòm gì thế, thằng khỉ kia?\r\n\r\nDì Liên thò đầu ra khỏi cửa ngách, quát.\r\n\r\nRồi nhác thấy thằng cháu bảo bối đang lơ lửng trên cao, mặt dì từ đỏ chuyển qua xanh:\r\n\r\n- Ối trời ơi! Mày leo lên tám tầng trời làm gì thế, có xuống nhanh đi không!\r\n\r\nGiọng dì chợt dịu xuống, lo lắng khi thấy Khoa đang luống cuống:\r\n\r\n- Từ từ thôi cháu! Coi chừng té gãy cổ bây giờ! Từ từ mà trèo xuống, dì không phạt cháu đâu...\r\n\r\nĐang chuyển giọng ngọt ngào, dì chợt nhận ra cái mà Khoa đang kẹp giữa hai chân là cây chổi dì tìm mỏi mắt từ trưa đến giờ, thế là dì chuyển giọng một lần nữa - lần này là từ thấp lên cao:\r\n\r\n- Trời ơi là trời! Thế ra là mày đã lấy cây chổi của tao mà nghịch đấy!\r\n\r\n- Dạ... dạ...\r\n\r\nKhoa ấp úng đáp, chả biết mình phun ra từ gì vì vậy ý nghĩa của chuỗi \"dạ... dạ\" kia thật vu vơ, vừa đảo mắt tìm chỗ đặt chân.\r\n\r\nChân nó khều khều huơ huơ trong khoảng không, một lát cũng mò trúng được chỗ chạc ba. Nó dồn trọng tâm lên chân phải, nhưng đến khi nó rút chân trái ra khỏi chiếc \"xích đu\" gót chân nó lại vướng vào cán chổi.\r\n\r\n\"Phựt\" một tiếng, sợi dây treo đứt ngang, cây chổi chúc một đầu xuống đất nhưng vẫn lơ lửng trên không bởi sợi dây bên kia vẫn còn bám chắc trên cành. Trong khi đó, Khoa mất đà, bổ nhào xuống cỏ. Khác với cây chổi, Khoa đã lao là lao thẳng một lèo.\r\n\r\nKhoa không nhớ mình tiếp đất bằng bộ phận nào, nó chỉ nghe một tiếng \"binh\", rất giống tiếng mít rụng, mông đau nhói.\r\n\r\nNhánh ổi mà Khoa treo cây chổi thực ra cao chưa tới hai mét nên cú ngã của nó không lấy gì làm đau khổ tang thương cho lắm.\r\n\r\nNhưng mà ngã xuống rồi thì Khoa rú lên một tiếng như còi xe lửa, lăn qua lăn lại kha khá vòng - một động tác chỉ có ở người sắp về chầu ông bà, và tất nhiên cuối cùng là nó nằm bất động như một thằng người bằng gỗ - lần này thì là tư thế của kẻ đã gặp ông bà rồi.\r\n\r\nChàng phù thủy Khoa không chỉ gan dạ, lãng mạn, tình tứ mà còn một ưu điểm nữa là rất ranh mãnh. Chàng nghĩ mình chỉ có chết đi thì dì của chàng mới không tóm lấy tai chàng lôi xềnh xệch vào nhà và sau đó trừng phạt những tội trạng mà chàng đã gây ra bằng một thứ gì đó có hình thù na ná cây chổi, nhỏ hơn nhưng mềm hơn, vì vậy đét vô mông thì đau hơn. Vũ khí đáng gờm đó, xưa nay dân gian vẫn gọi là \"roi\".\r\n\r\nKhoa nghĩ quả không sai. Dì Liên đã rắp tâm sẽ tóm lấy vành tai thằng cháu hoang đàng khi nó đặt chân xuống đất và tiếp theo sẽ nghĩ ra những hình phạt đích đáng cho nó, nhưng khi thấy Khoa không đặt chân mà đặt mông xuống đất (và sau khi lao xuống đất bằng tư thế khó coi đó rồi thì Khoa có vẻ không chết cũng đã trọng thương) thì dì không còn lòng dạ nào nghĩ đến chuyện trừng phạt thằng bé nữa.\r\n\r\nHồn vía lên mây, dì quýnh quíu chạy lại phía Khoa, miệng mồm mếu xệch:\r\n\r\n- Khoa, Khoa! Cháu có sao không?\r\n\r\nKhoa vẫn nằm im lìm, trông bộ dạng thì có vẻ sẽ không bao giờ dậy nữa.\r\n\r\nNước mắt ròng ròng trên má, dì Liên ôm vai Khoa lay lay như người ta đang lắc một chiếc xe cạn xăng, hy vọng sẽ đánh thức một giọt sinh khí còn sót lại đâu đó trong cơ thể bất động kia.\r\n\r\n- Ôi, cháu làm sao thế Khoa! Cháu vừa nói chuyện với dì đây mà!\r\n\r\nDì Liên nức nở ghê quá khiến Khoa cảm thấy mềm lòng. Nhưng hồn nó đã trót lìa khỏi xác, Khoa cố mím chặt cả môi lẫn mắt, biết rằng bất cứ một sự bất cẩn nào cũng khiến cặp mông nó trả giá. Thậm chí nó ép mình nín thở càng lâu càng tốt.\r\n\r\nDì Liên sờ tay lên mũi nó, thấy cháu mình hoàn toàn tắt thở thì khóc rống lên:\r\n\r\n- Ôi, tội nghiệp thằng bé! Nó chỉ nghịch cây chổi thôi mà mình đã khiến nó ra nông nỗi này. Cây chổi thì có đáng gì cơ chứ. Ngoài chợ người ta bán hàng mớ, muốn mua mấy cây chả được. Thế mà thằng bé phải bỏ mạng chỉ vì một cây chổi vứt đi!\r\n\r\nDì khóc, nói một tràng, ngưng khóc để xì mũi rồi tiếp tục ca cẩm trong đau thương, rồi lại tự dằn vặt bản thân, trông cái cách dì kịch liệt lên án mình có cảm tưởng nếu có thể tống được mình vào tù dì không ngần ngại gì mà không làm. Cuối cùng dì quyết định bày tỏ sự hối hận to lớn đó bằng hành động.\r\n\r\nDì lục lọi trong túi áo, moi ra một cây compa, đặt vào bàn tay \"giá lạnh\" của Khoa:\r\n\r\n- Dì trả cho cháu nè. Sáng nay dì tịch thu nó chẳng qua dì sợ cháu dùng nó đánh nhau với bạn...\r\n\r\nKhoa mừng quá, suýt chút nữa nó đã co các ngón tay lại để nắm lấy món đồ tưởng đã ra đi không bao giờ trở lại đó.\r\n\r\nDì lục túi áo bên kia, lần này dì tìm thấy một hòn đá đen, nhẵn thín, lớn bằng quả ổi sẻ. Lại đặt vào bàn tay kia của Khoa, không để ý bàn tay đó vừa rồi nắm hờ nay đã xòe ra từ lúc nào:\r\n\r\n- Dì trả cho cháu luôn hòn đá khi nãy này. Bây giờ thì cháu không thể dùng nó để ném vỡ đồ đạc nữa rồi.\r\n\r\nDì nhìn cây compa bên tay này của Khoa, rồi nhìn hòn đá bên tay kia của Khoa, bất giác động lòng ôm mặt khóc rấm rứt.\r\n\r\nDì mải khóc làm Khoa sốt ruột quá. Nó nhớ dì Liên đã tịch thu của nó bao nhiêu là thứ và thật không có cơ hội nào thuận lợi hơn lúc này để thu hồi tài sản.\r\n\r\nThế nhưng sau khi trả lại cho Khoa hai món đồ, dì dường như quên mất chuyện tiếp tục bày tỏ sự hối hận với thằng cháu tội nghiệp bằng cách trả các món thứ ba, thứ tư...\r\n\r\nKhoa he hé mắt liếc trộm dì, thấy dì vẫn vùi mặt vào giữa hai bàn tay, thút tha thút thít, và cứ cái kiểu này có vẻ dì không còn nhớ gì đến những món đồ đã thu giữ của Khoa.\r\n\r\nSau một thoáng lưỡng lự, Khoa liều cất tiếng:\r\n\r\n- Còn bộ bài cát-tê nữa...\r\n\r\n- Ờ, bộ bài dì giấu trong ngăn tủ quần áo. - Dì Liên thôi bưng mặt, dì dùng cườm tay quẹt nước mắt lẫn nước mũi và lảo đảo đứng lên - Để dì vào nhà lấy...\r\n\r\n- Còn con dao nhíp... - Khoa nhắc, lòng tham ùn ùn trỗi dậy.\r\n\r\n- Cháu yên tâm! - Dì gật đầu dễ dãi, giọng tràn ngập bao dung - Dì nhớ con dao đó rồi...\r\n\r\nNhưng vừa dợm bước, như sực nhớ ra điều gì, dì Liên quay ngoắt lại, miệng há hốc:\r\n\r\n- Ủa, Khoa!\r\n\r\nTrong nháy mắt, dì Liên biến thành con người khác, tương phản hoàn toàn với con người dịu dàng vừa rồi. Giọng dì rít lên, the thé:\r\n\r\n- Tao tưởng mày chết rồi mà, Khoa! Thế ra...\r\n\r\nKhông đợi dì Liên nói hết câu, Khoa trượt đi bằng hai gót chân, thoát khỏi cú tóm của dì. Thêm một cái chỏi tay nữa, Khoa lật người lại, lồm cồm bò dậy và nhanh như cheo, nó lạng tuốt ra xa, phóng về phía hàng rào cuối vườn, tay vẫn nắm khư khư hai món đồ béo bở.\r\n\r\nKhoa nghe rất rõ tiếng giậm chân bình bịch và tiếng gọi í ới của dì nó ở phía sau nhưng nó mặc kệ. Như một vận động viên nhảy cao, nó búng người bay qua hàng giậu mồng tơi, vướng chân vào bụi tơ hồng, ngã đánh uỵch.\r\n\r\nKhoa nén đau, lóp ngóp đứng lên, quay mặt đá chân vào hàng giậu để trả thù cú té rồi tập tễnh đi tiếp.', 4, 'bay-buoc-toi-mua-he.jpg', 37, '2020-09-18 21:33:58', '1');

